import{_ as r}from"./chunks/pix32_hero.BGwOX7hT.js";import{_ as i,c as o,j as t,a as l,G as s,a8 as n,B as h,o as p}from"./chunks/framework.BDnHobkS.js";const y=JSON.parse('{"title":"Holybro pix32 Flight Controller (Discontinued)","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/holybro_pix32.md","filePath":"en/flight_controller/holybro_pix32.md"}'),c={name:"en/flight_controller/holybro_pix32.md"};function d(u,e,b,m,g,f){const a=h("Badge");return p(),o("div",null,[e[0]||(e[0]=t("h1",{id:"holybro-pix32-flight-controller-discontinued",tabindex:"-1"},[l("Holybro pix32 Flight Controller (Discontinued) "),t("a",{class:"header-anchor",href:"#holybro-pix32-flight-controller-discontinued","aria-label":'Permalink to "Holybro pix32 Flight Controller (Discontinued)"'},"​")],-1)),s(a,{type:"info",text:"Discontinued"}),e[1]||(e[1]=n('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The Holybro<sup>®</sup> <a href="https://holybro.com/collections/autopilot-flight-controllers/products/pix32pixhawk-flight-controller" target="_blank" rel="noreferrer">pix32 autopilot</a> (also known as &quot;Pixhawk 2&quot;, and formerly as HKPilot32) is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk<sup>®</sup>-project</a> <strong>FMUv2</strong> open hardware design. This board is based on hardware version Pixhawk 2.4.6. It runs the PX4 flight stack on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><p><img src="'+r+'" alt="pix32"></p><p>As a CC-BY-SA 3.0 licensed Open Hardware design, schematics and design files should be <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">available here</a>.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The Holybro pix32 is software compatible with the <a href="./../flight_controller/pixhawk.html">3DR Pixhawk 1</a>. It is not connector compatible, but is otherwise physically very similar to the 3DR Pixhawk or mRo Pixhawk.</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li>Main System-on-Chip: <a href="http://www.st.com/web/en/catalog/mmc/FM141/SC1169/SS1577/LN1789" target="_blank" rel="noreferrer">STM32F427</a><ul><li>CPU: 32-bit STM32F427 Cortex<sup>®</sup> M4 core with FPU</li><li>RAM: 168 MHz/256 KB</li><li>Flash: 2 MB</li></ul></li><li>Failsafe System-on-Chip: STM32F103</li><li>Sensors: <ul><li>ST Micro L3GD20 3-axis 16-bit gyroscope</li><li>ST Micro LSM303D 3-axis 14-bit accelerometer / magnetometer</li><li>Invensense<sup>®</sup> MPU 6000 3-axis accelerometer/gyroscope</li><li>MEAS MS5611 barometer</li></ul></li><li>Dimensions/Weight <ul><li>Size: 81x44x15mm</li><li>Weight: 33.1g</li></ul></li><li>GPS: u-blox<sup>®</sup> super precision Neo-7M with compass</li><li>Input Voltage: 2~10s (7.4~37V)</li></ul><h3 id="connectivity" tabindex="-1">Connectivity <a class="header-anchor" href="#connectivity" aria-label="Permalink to &quot;Connectivity&quot;">​</a></h3><ul><li>1x I2C</li><li>2x CAN</li><li>3.3 and 6.6V ADC inputs</li><li>5x UART (serial ports), one high-power capable, 2x with HW flow control</li><li>Spektrum DSM / DSM2 / DSM-X® Satellite compatible input up to DX8 (DX9 and above not supported)</li><li>Futaba<sup>®</sup> S.BUS compatible input and output</li><li>PPM sum signal</li><li>RSSI (PWM or voltage) input</li><li>SPI</li><li>External microUSB port</li><li>Molex PicoBlade connectors</li></ul><h2 id="where-to-buy" tabindex="-1">Where to Buy <a class="header-anchor" href="#where-to-buy" aria-label="Permalink to &quot;Where to Buy&quot;">​</a></h2><p><a href="https://holybro.com/collections/autopilot-flight-controllers/products/pix32pixhawk-flight-controller" target="_blank" rel="noreferrer">shop.holybro.com</a></p><h3 id="accessories" tabindex="-1">Accessories <a class="header-anchor" href="#accessories" aria-label="Permalink to &quot;Accessories&quot;">​</a></h3><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Digital airspeed sensor</a></li><li><a href="https://hobbyking.com/en_us/apm-pixhawk-wireless-wifi-radio-module.html" target="_blank" rel="noreferrer">Hobbyking<sup>®</sup> Wifi Telemetry</a></li><li><a href="./../telemetry/holybro_sik_radio.html">HolyBro SiK Telemetry Radio (EU 433 MHz, US 915 MHz)</a></li></ul><h2 id="building-firmware" tabindex="-1">Building Firmware <a class="header-anchor" href="#building-firmware" aria-label="Permalink to &quot;Building Firmware&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v2_default</span></span></code></pre></div><h2 id="debug-port" tabindex="-1">Debug Port <a class="header-anchor" href="#debug-port" aria-label="Permalink to &quot;Debug Port&quot;">​</a></h2><p>See <a href="./../flight_controller/pixhawk.html#debug-ports">3DR Pixhawk 1 &gt; Debug Ports</a>.</p><h2 id="pinouts-and-schematics" tabindex="-1">Pinouts and Schematics <a class="header-anchor" href="#pinouts-and-schematics" aria-label="Permalink to &quot;Pinouts and Schematics&quot;">​</a></h2><p>The board is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk project</a> <strong>FMUv2</strong> open hardware design.</p><ul><li><a href="https://raw.githubusercontent.com/PX4/Hardware/master/FMUv2/PX4FMUv2.4.5.pdf" target="_blank" rel="noreferrer">FMUv2 + IOv2 schematic</a> -- Schematic and layout</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>As a CC-BY-SA 3.0 licensed Open Hardware design, all schematics and design files are <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">available</a>.</p></div><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (flow control)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (flow control)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',26))])}const w=i(c,[["render",d]]);export{y as __pageData,w as default};
