import{_ as e}from"./chunks/mro_pixhawk.TeT2w4oe.js";import{_ as a,c as i,a8 as r,o as l}from"./chunks/framework.BDnHobkS.js";const b=JSON.parse('{"title":"mRo Pixhawk Flight Controller (Pixhawk 1)","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/mro_pixhawk.md","filePath":"en/flight_controller/mro_pixhawk.md"}'),o={name:"en/flight_controller/mro_pixhawk.md"};function s(h,t,n,p,d,c){return l(),i("div",null,t[0]||(t[0]=[r('<h1 id="mro-pixhawk-flight-controller-pixhawk-1" tabindex="-1">mRo Pixhawk Flight Controller (Pixhawk 1) <a class="header-anchor" href="#mro-pixhawk-flight-controller-pixhawk-1" aria-label="Permalink to &quot;mRo Pixhawk Flight Controller (Pixhawk 1)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <em>mRo Pixhawk<sup>®</sup></em> is a hardware compatible version of the original <a href="./../flight_controller/pixhawk.html">Pixhawk 1</a>. It runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The controller can be used as a drop-in replacement for the 3DR<sup>®</sup> <a href="./../flight_controller/pixhawk.html">Pixhawk 1</a>. The main difference is that it is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design, which corrects a bug that limited the original Pixhawk 1 to 1MB of flash.</p></div><p><img src="'+e+'" alt="mRo Pixhawk Image"></p><p>Assembly/setup instructions for use with PX4 are provided here: <a href="./../assembly/quick_start_pixhawk.html">Pixhawk Wiring Quickstart</a></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li><p>Microprocessor:</p><ul><li>32-bit STM32F427 Cortex<sup>®</sup> M4 core with FPU</li><li>168 MHz/256 KB RAM/2 MB Flash</li><li>32 bit STM32F100 failsafe co-processor</li><li>24 MHz/8 KB RAM/64 KB Flash</li></ul></li><li><p>Sensors:</p><ul><li>ST Micro L3GD20 3-axis 16-bit gyroscope</li><li>ST Micro LSM303D 3-axis 14-bit accelerometer / magnetometer</li><li>Invensense<sup>®</sup> MPU 6000 3-axis accelerometer/gyroscope</li><li>MEAS MS5611 barometer</li></ul></li><li><p>Interfaces:</p><ul><li>5x UART (serial ports), one high-power capable, 2x with HW flow control</li><li>2x CAN</li><li>Spektrum DSM / DSM2 / DSM-X® Satellite compatible input up to DX8 (DX9 and above not supported)</li><li>Futaba<sup>®</sup> S.BUS compatible input and output</li><li>PPM sum signal</li><li>RSSI (PWM or voltage) input</li><li>I2C</li><li>SPI</li><li>3.3 and 6.6V ADC inputs</li><li>External microUSB port</li></ul></li><li><p>Power System:</p><ul><li>Ideal diode controller with automatic failover</li><li>Servo rail high-power (7 V) and high-current ready</li><li>All peripheral outputs over-current protected, all inputs ESD protected</li></ul></li><li><p>Weight and Dimensions:</p><ul><li>Weight: 38g (1.31oz)</li><li>Width: 50mm (1.96&quot;)</li><li>Thickness: 15.5mm (.613&quot;)</li><li>Length: 81.5mm (3.21&quot;)</li></ul></li></ul><h2 id="availability" tabindex="-1">Availability <a class="header-anchor" href="#availability" aria-label="Permalink to &quot;Availability&quot;">​</a></h2><ul><li><a href="https://store.mrobotics.io/Genuine-PixHawk-1-Barebones-p/mro-pixhawk1-bb-mr.htm" target="_blank" rel="noreferrer">Bare Bones</a> - Just the board (useful as a 3DR Pixhawk replacement)</li><li><a href="https://store.mrobotics.io/Genuine-PixHawk-Flight-Controller-p/mro-pixhawk1-minkit-mr.htm" target="_blank" rel="noreferrer">mRo Pixhawk 2.4.6 Essential Kit!</a> - Everything except for telemetry radios</li><li><a href="https://store.mrobotics.io/product-p/mro-pixhawk1-fullkit-mr.htm" target="_blank" rel="noreferrer">mRo Pixhawk 2.4.6 Cool Kit! (Limited edition)</a> - Everything you need including telemetry radios</li></ul><h2 id="building-firmware" tabindex="-1">Building Firmware <a class="header-anchor" href="#building-firmware" aria-label="Permalink to &quot;Building Firmware&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v3_default</span></span></code></pre></div><h2 id="debug-ports" tabindex="-1">Debug Ports <a class="header-anchor" href="#debug-ports" aria-label="Permalink to &quot;Debug Ports&quot;">​</a></h2><p>See <a href="./../flight_controller/pixhawk.html#debug-ports">3DR Pixhawk 1 &gt; Debug Ports</a></p><h2 id="pinouts" tabindex="-1">Pinouts <a class="header-anchor" href="#pinouts" aria-label="Permalink to &quot;Pinouts&quot;">​</a></h2><p>See <a href="./../flight_controller/pixhawk.html#pinouts">3DR Pixhawk 1 &gt; Pinouts</a></p><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (flow control)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (flow control)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table><h2 id="schematics" tabindex="-1">Schematics <a class="header-anchor" href="#schematics" aria-label="Permalink to &quot;Schematics&quot;">​</a></h2><p>The board is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design.</p><ul><li><a href="https://github.com/PX4/Hardware/raw/master/FMUv3_REV_D/Schematic%20Print/Schematic%20Prints.PDF" target="_blank" rel="noreferrer">FMUv3 schematic</a> -- Schematic and layout</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>As a CC-BY-SA 3.0 licensed Open Hardware design, all schematics and design files are <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">available</a>.</p></div>',25)]))}const g=a(o,[["render",s]]);export{b as __pageData,g as default};
