import{_ as a}from"./chunks/pixhack_v3_157_large_default.BolSQN88.js";import{_ as i,c as e,a8 as r,o as l}from"./chunks/framework.BDnHobkS.js";const f=JSON.parse('{"title":"Pixhack V3","description":"","frontmatter":{},"headers":[],"relativePath":"en/flight_controller/pixhack_v3.md","filePath":"en/flight_controller/pixhack_v3.md"}'),o={name:"en/flight_controller/pixhack_v3.md"};function s(n,t,d,c,h,p){return l(),e("div",null,t[0]||(t[0]=[r('<h1 id="pixhack-v3" tabindex="-1">Pixhack V3 <a class="header-anchor" href="#pixhack-v3" aria-label="Permalink to &quot;Pixhack V3&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store.cuav.net/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The CUAV <em>Pixhack V3</em> flight controller board is a flexible autopilot intended primarily for manufacturers of commercial systems.</p><p>The board is a variant of the SOLO Pixhawk<sup>®</sup> 2 (PH2) flight controller, which is in turn based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design. It runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS, and is fully compatible with both PX4 or ArduPilot<sup>®</sup> (APM) firmware.</p><p><em>Pixhack V3</em> has significant improvements with respect to the original design, including better interface layout and the addition of vibration damping and a thermostat system.</p><p><img src="'+a+'" alt="Pixhack v3"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="quick-summary" tabindex="-1">Quick Summary <a class="header-anchor" href="#quick-summary" aria-label="Permalink to &quot;Quick Summary&quot;">​</a></h2><ul><li>Microprocessor: <ul><li>STM32F427</li><li>STM32F100 (Failsafe co-processor)</li></ul></li><li>Sensors: <ul><li>Accelerometers (3): LS303D, MPU6000, MPU9250/hmc5983</li><li>Gyroscopes (3): L3GD20, MPU6000, MPU9250</li><li>Compass (2): LS303D, MPU9250</li><li>Barometer (2): MS5611 X2</li></ul></li><li>Interfaces: <ul><li>MAVLink UART (2)</li><li>GPS UART (2)</li><li>DEBUG UART (1)</li><li>RC IN (for PPM, SBUS, DSM/DSM2)</li><li>RSSI IN: PWM OR 3.3ADC</li><li>I2C (2)</li><li>CAN BUS (1)</li><li>ADC IN: 3.3V X1 , 6.6V X1</li><li>PWM OUT: 8 PWM IO + 4 IO</li></ul></li><li>Power System: <ul><li>PM POWER IN: 4.5 ~ 5.5 V</li><li>USB POWER IN: 5.0 V +- 0.25v</li></ul></li><li>Weight and Dimensions: <ul><li>Weight: 63g</li><li>Width: 68mm</li><li>Thickness: 17mm</li><li>Length: 44mm</li></ul></li><li>Other Characteristics: <ul><li>Operating temperature: -20 ~ 60°C</li></ul></li></ul><h2 id="availability" tabindex="-1">Availability <a class="header-anchor" href="#availability" aria-label="Permalink to &quot;Availability&quot;">​</a></h2><p>The board can be purchased from:</p><ul><li><a href="http://store.cuav.net/index.php?id_product=8&amp;id_product_attribute=0&amp;rewrite=pixhack-v3-autopilot&amp;controller=product&amp;id_lang=3" target="_blank" rel="noreferrer">store.cuav.net</a></li><li><a href="https://leixun.aliexpress.com/store" target="_blank" rel="noreferrer">leixun.aliexpress.com/store</a></li></ul><h2 id="building-firmware" tabindex="-1">Building Firmware <a class="header-anchor" href="#building-firmware" aria-label="Permalink to &quot;Building Firmware&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v3_default</span></span></code></pre></div><h2 id="pinouts-and-schematics" tabindex="-1">Pinouts and Schematics <a class="header-anchor" href="#pinouts-and-schematics" aria-label="Permalink to &quot;Pinouts and Schematics&quot;">​</a></h2><ul><li><a href="http://doc.cuav.net/flight-controller/pixhack/en/pixhack-v3.html" target="_blank" rel="noreferrer">Documentation/wiring guides</a></li></ul><h2 id="serial-port-mapping" tabindex="-1">Serial Port Mapping <a class="header-anchor" href="#serial-port-mapping" aria-label="Permalink to &quot;Serial Port Mapping&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Device</th><th>Port</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (flow control)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (flow control)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',20)]))}const b=i(o,[["render",s]]);export{f as __pageData,b as default};
