import{_ as r,a as l,b as d,c as o}from"./chunks/voxl-flight-bottom.2H30pHYz.js";import{_ as i,c as n,j as e,a as s,G as h,a8 as f,B as c,o as p}from"./chunks/framework.BDnHobkS.js";const k=JSON.parse('{"title":"ModalAI VOXL Flight","description":"","frontmatter":{},"headers":[],"relativePath":"ko/flight_controller/modalai_voxl_flight.md","filePath":"ko/flight_controller/modalai_voxl_flight.md"}'),g={name:"ko/flight_controller/modalai_voxl_flight.md"};function m(x,t,b,u,y,P){const a=c("Badge");return p(),n("div",null,[t[0]||(t[0]=e("h1",{id:"modalai-voxl-flight",tabindex:"-1"},[s("ModalAI VOXL Flight "),e("a",{class:"header-anchor",href:"#modalai-voxl-flight","aria-label":'Permalink to "ModalAI VOXL Flight"'},"​")],-1)),h(a,{type:"tip",text:"PX4 v1.11"}),t[1]||(t[1]=f('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://forum.modalai.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The ModalAI <a href="https://modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight</a> (<a href="https://docs.modalai.com/voxl-flight-datasheet" target="_blank" rel="noreferrer">Datasheet</a>) is one of the first computing platforms to combine the power and sophistication of Snapdragon with the flexibility and ease of use of PX4 on an STM32F7. Made in the USA, VOXL Flight supports obstacle avoidance and GPS-denied (indoor) navigation fused with a PX4 flight controller on a single PCB.</p><p><img src="'+r+'" alt="VOXL-Flight"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="사양" tabindex="-1">사양 <a class="header-anchor" href="#사양" aria-label="Permalink to &quot;사양&quot;">​</a></h2><h3 id="시스템" tabindex="-1">시스템 <a class="header-anchor" href="#시스템" aria-label="Permalink to &quot;시스템&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">기능</th><th style="text-align:left;">세부 정보</th></tr></thead><tbody><tr><td style="text-align:left;">중량</td><td style="text-align:left;">26 g</td></tr></tbody></table><h3 id="보조-컴퓨터" tabindex="-1">보조 컴퓨터 <a class="header-anchor" href="#보조-컴퓨터" aria-label="Permalink to &quot;보조 컴퓨터&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">기능</th><th style="text-align:left;">세부 정보</th></tr></thead><tbody><tr><td style="text-align:left;">기본 운영 시스템</td><td style="text-align:left;">3.18 커널을 사용하는 Linux Yocto Jethro. Additional Linux Operating Systems can be used by running Docker on VOXL, details <a href="https://docs.modalai.com/docker-on-voxl/" target="_blank" rel="noreferrer">here</a></td></tr><tr><td style="text-align:left;">계산</td><td style="text-align:left;">Qualcomm Snapdragon 821 w/ 4GB LPDDR4 1866MHz, Snapdragon 821 <a href="https://developer.qualcomm.com/download/sd820e/qualcomm-snapdragon-820e-processor-apq8096sge-device-specification.pdf" target="_blank" rel="noreferrer">Datasheet</a>, <a href="https://developer.qualcomm.com/hardware/apq-8096sg/tools" target="_blank" rel="noreferrer">Docs</a></td></tr><tr><td style="text-align:left;">CPU</td><td style="text-align:left;">최대 2.15GHz의 쿼드 코어 CPU</td></tr><tr><td style="text-align:left;">GPU</td><td style="text-align:left;">624MHz의 Adreno 530 GPU</td></tr><tr><td style="text-align:left;">DSP 컴퓨팅</td><td style="text-align:left;">Hexagon compute DSP (cDSP) 825MHz</td></tr><tr><td style="text-align:left;">센서 DSP</td><td style="text-align:left;">Hexagon 센서 DSP (sDSP) 700MHz</td></tr><tr><td style="text-align:left;">비디오</td><td style="text-align:left;">4k30 비디오 캡처 h.264/5 w/ 720p FPV</td></tr><tr><td style="text-align:left;">Camera Interfaces</td><td style="text-align:left;">MIPI-CSI2, USB UVC, HDMI 지원</td></tr><tr><td style="text-align:left;">Wi-Fi</td><td style="text-align:left;">Pre-certified Wi-Fi module <a href="https://fccid.io/PPD-QCNFA324" target="_blank" rel="noreferrer">QCNFA324 FCC ID:PPD-QCNFA324</a>, QCA6174A modem, 802.11ac 2x2 Dual-band, Bluetooth 4.2 (dual-mode)</td></tr><tr><td style="text-align:left;">4G LTE</td><td style="text-align:left;"><a href="https://www.modalai.com/collections/voxl-add-ons/products/voxl-lte" target="_blank" rel="noreferrer">Optional add-on module</a></td></tr><tr><td style="text-align:left;">Microhard pDDL</td><td style="text-align:left;"><a href="https://www.modalai.com/collections/voxl-add-ons/products/voxl-microhard-modem-usb-hub" target="_blank" rel="noreferrer">Optional add-on module</a></td></tr><tr><td style="text-align:left;">GNSS</td><td style="text-align:left;">WGR7640 10Hz</td></tr><tr><td style="text-align:left;">I/O</td><td style="text-align:left;">1x USB3.0 OTG (ADB 포트), 1x USB2.0 (확장 포트), 2x UART, 3x I2C, 추가 GPIO 및 SPI 설정 가능</td></tr><tr><td style="text-align:left;">저장 장치</td><td style="text-align:left;">32GB (UFS 2.0), Micro SD 카드</td></tr><tr><td style="text-align:left;">소프트웨어</td><td style="text-align:left;">Docker, OpenCV 2.4.11, 3.4.6, 4.2, ROS Indigo, Qualcomm Machine Vision SDK, see <a href="https://gitlab.com/voxl-public" target="_blank" rel="noreferrer">GitLab</a> for lots of open source examples!</td></tr><tr><td style="text-align:left;">관성계</td><td style="text-align:left;">ICM-42688 (SPI10), ICM-20948 (SPI1)</td></tr><tr><td style="text-align:left;">기압계</td><td style="text-align:left;">BMP280</td></tr></tbody></table><h3 id="비행-콘트롤러" tabindex="-1">비행 콘트롤러 <a class="header-anchor" href="#비행-콘트롤러" aria-label="Permalink to &quot;비행 콘트롤러&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">기능</th><th style="text-align:left;">세부 정보</th></tr></thead><tbody><tr><td style="text-align:left;">MCU</td><td style="text-align:left;">216MHz, 32-bit ARM M7 <a href="https://www.st.com/en/microcontrollers-microprocessors/stm32f765ii.html" target="_blank" rel="noreferrer">STM32F765II</a></td></tr><tr><td style="text-align:left;">메모리</td><td style="text-align:left;">256Kb FRAM</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">2Mbit Flash</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">512Kbit SRAM</td></tr><tr><td style="text-align:left;">펌웨어</td><td style="text-align:left;"><a href="https://github.com/PX4/PX4-Autopilot/tree/main/boards/modalai/fc-v1" target="_blank" rel="noreferrer">PX4</a></td></tr><tr><td style="text-align:left;">관성계</td><td style="text-align:left;"><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">ICM-20602</a> (SPI1)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">ICM-42688 (SPI2)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">BMI088</a> (SPI6)</td></tr><tr><td style="text-align:left;">기압계</td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp388/" target="_blank" rel="noreferrer">BMP388</a> (I2C4)</td></tr><tr><td style="text-align:left;">보안 요소</td><td style="text-align:left;"><a href="https://www.nxp.com/products/security-and-authentication/authentication/plug-and-trust-the-fast-easy-way-to-deploy-secure-iot-connections:A71CH" target="_blank" rel="noreferrer">A71CH</a> (I2C4)</td></tr><tr><td style="text-align:left;">microSD 카드</td><td style="text-align:left;"><a href="./../dev_log/logging.html#sd-cards">Information on supported cards</a></td></tr><tr><td style="text-align:left;">입력</td><td style="text-align:left;">GPS/자력계</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Spektrum</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">텔레메트리</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">CAN 버스</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PPM</td></tr><tr><td style="text-align:left;">출력</td><td style="text-align:left;">LED 6 개 (2xRGB)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PWM 채널 8개</td></tr><tr><td style="text-align:left;">추가 인터페이스</td><td style="text-align:left;">시리얼포트 3개</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">I2C</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">GPIO</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">INFO</p><p>More detailed hardware documentation can be found <a href="https://docs.modalai.com/voxl-flight-datasheet/" target="_blank" rel="noreferrer">here</a>.</p></div><h2 id="크기" tabindex="-1">크기 <a class="header-anchor" href="#크기" aria-label="Permalink to &quot;크기&quot;">​</a></h2><p><img src="'+l+'" alt="FlightCoreV1Dimensions"></p><p><a href="https://storage.googleapis.com/modalai_public/modal_drawings/M0019_VOXL-Flight.zip" target="_blank" rel="noreferrer">3D STEP File</a></p><h2 id="px4-firmware-compatibility" tabindex="-1">PX4 Firmware Compatibility <a class="header-anchor" href="#px4-firmware-compatibility" aria-label="Permalink to &quot;PX4 Firmware Compatibility&quot;">​</a></h2><p><em>VOXL Flight</em> is fully compatible with the official PX4 Firmware from PX4 v1.11.</p><p>ModalAI maintains a <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.11" target="_blank" rel="noreferrer">branched PX4 version</a> for PX4 v1.11. This includes UART ESC support and improvements in VIO and VOA that are planned to be upstreamed.</p><p>More information about the firmware can be found <a href="https://docs.modalai.com/flight-core-firmware/" target="_blank" rel="noreferrer">here</a>.</p><h2 id="qgroundcontrol-지원" tabindex="-1">QGroundControl 지원 <a class="header-anchor" href="#qgroundcontrol-지원" aria-label="Permalink to &quot;QGroundControl 지원&quot;">​</a></h2><p>아래 다이어그램은 PX4 v1.11(및 <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.10">ModalAI가 유지하는 PX4 v1.10 브랜치</a>)부터 <code>ROTATION_NONE</code> 권장 방향을 나타냅니다.</p><h2 id="구매처" tabindex="-1">구매처 <a class="header-anchor" href="#구매처" aria-label="Permalink to &quot;구매처&quot;">​</a></h2><ul><li><a href="https://modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight Complete Kit</a></li><li><a href="https://www.modalai.com/products/voxl-flight?variant=31707275362355" target="_blank" rel="noreferrer">VOXL Flight Board</a> (only)</li><li><a href="https://modalai.com/flight-deck" target="_blank" rel="noreferrer">VOXL Flight integrated with Obstacle Avoidance Cameras (VOXL Flight Deck)</a> (<a href="https://docs.modalai.com/voxl-flight-deck-platform-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li><li><a href="https://www.modalai.com/collections/development-drones/products/voxl-m500" target="_blank" rel="noreferrer">VOXL Flight in a ready to fly VOXL m500 Development Drone</a> (<a href="https://docs.modalai.com/voxl-m500-reference-drone-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li></ul><h2 id="빠른-시작" tabindex="-1">빠른 시작 <a class="header-anchor" href="#빠른-시작" aria-label="Permalink to &quot;빠른 시작&quot;">​</a></h2><p>A quickstart from the vendor is located <a href="https://docs.modalai.com/voxl-flight-quickstart/" target="_blank" rel="noreferrer">here</a>.</p><h3 id="voxl-vision-px4" tabindex="-1">voxl-vision-px4 <a class="header-anchor" href="#voxl-vision-px4" aria-label="Permalink to &quot;voxl-vision-px4&quot;">​</a></h3><p>The VOXL Flight runs <a href="https://gitlab.com/voxl-public/modal-pipe-architecture/voxl-vision-px4" target="_blank" rel="noreferrer">voxl-vision-px4</a> on the companion computer portion of the hardware serving as a sort of MAVLink proxy. For details, the source code is available <a href="https://gitlab.com/voxl-public/modal-pipe-architecture/voxl-vision-px4" target="_blank" rel="noreferrer">here</a></p><h3 id="커넥터" tabindex="-1">커넥터 <a class="header-anchor" href="#커넥터" aria-label="Permalink to &quot;커넥터&quot;">​</a></h3><p>Detailed information about the pinouts can be found <a href="https://docs.modalai.com/voxl-flight-datasheet-connectors/" target="_blank" rel="noreferrer">here</a>.</p><h4 id="상단" tabindex="-1">상단 <a class="header-anchor" href="#상단" aria-label="Permalink to &quot;상단&quot;">​</a></h4><p><img src="'+d+'" alt="VOXLFlightTop"></p><p><em>Note: 1000 Series connectors accessible from the STM32/PX4</em></p><table tabindex="0"><thead><tr><th>커넥터</th><th>요약</th><th>사용처</th></tr></thead><tbody><tr><td>J2</td><td>4k 이미지 센서 (CSI0) 고용</td><td>Snapdragon - 리눅스</td></tr><tr><td>J3</td><td>스테레오 이미지 센서 (CSI1)</td><td>Snapdragon - 리눅스</td></tr><tr><td>J6</td><td>냉각 팬 커넥터</td><td>Snapdragon - 리눅스</td></tr><tr><td>J7</td><td>BLSP6 (GPIO) and BLSP9 (UART)</td><td>Snapdragon - 리눅스</td></tr><tr><td>J13</td><td>확장 B2B</td><td>Snapdragon - 리눅스</td></tr><tr><td>J14</td><td>통합 GNSS 안테나 연결</td><td>Snapdragon - 리눅스</td></tr><tr><td>J1001</td><td>프로그래밍 및 디버그/UART3</td><td>STM32 - PX4</td></tr><tr><td>J1002</td><td>UART ESC, UART2/TELEM3</td><td>STM32 - PX4</td></tr><tr><td>J1003</td><td>PPM RC 입력</td><td>STM32 - PX4</td></tr><tr><td>J1004</td><td>RC 입력, Spektrum/SBus/UART6</td><td>STM32 - PX4</td></tr><tr><td>J1006</td><td>USB 2.0 커넥터(PX4/QGroundControl)</td><td>STM32 - PX4</td></tr><tr><td>J1007</td><td>8 채널 PWM/DShot 출력</td><td>STM32 - PX4</td></tr><tr><td>J1008</td><td>CAN 버스</td><td>STM32 - PX4</td></tr><tr><td>J1009</td><td>I2C3, UART4</td><td>STM32 - PX4</td></tr><tr><td>J1010</td><td>텔레메트리 (TELEM1)</td><td>STM32 - PX4</td></tr><tr><td>J1011</td><td>I2C2, 안전 버튼 입력</td><td>STM32 - PX4</td></tr><tr><td>J1012</td><td>External GPS &amp; Mag, UART1, I2C1</td><td>STM32 - PX4</td></tr><tr><td>J1013</td><td>전원 입력, I2C3</td><td>STM32 - PX4 (powers whole system)</td></tr></tbody></table><h4 id="하단" tabindex="-1">하단 <a class="header-anchor" href="#하단" aria-label="Permalink to &quot;하단&quot;">​</a></h4><p><img src="'+o+'" alt="VOXLFlightBottom"></p><p><em>Note: 1000 Series connectors accessible from the STM32/PX4</em></p><table tabindex="0"><thead><tr><th>커넥터</th><th>요약</th><th>사용처</th></tr></thead><tbody><tr><td>J4</td><td>추적/광류 이미지 센서 (CSI2)</td><td>Snapdragon - 리눅스</td></tr><tr><td>J8</td><td>USB 3.0 OTG</td><td>Snapdragon - Linux, <strong>adb</strong></td></tr><tr><td>J10</td><td>BLSP7 UART 및 I2C 오프보드</td><td>Snapdragon - 리눅스</td></tr><tr><td>J11</td><td>BLSP12 UART 및 I2C 오프보드</td><td>Snapdragon - 리눅스</td></tr><tr><td>VOXL microSD</td><td></td><td>Snapdragon - 리눅스</td></tr><tr><td>PX4 microSD</td><td>32Gb Max</td><td>STM32 - PX4</td></tr><tr><td>Wi-Fi 안테나</td><td>포함됨.</td><td>Snapdragon - 리눅스</td></tr></tbody></table><h3 id="사용자-가이드" tabindex="-1">사용자 가이드 <a class="header-anchor" href="#사용자-가이드" aria-label="Permalink to &quot;사용자 가이드&quot;">​</a></h3><p>The full user guide is available <a href="https://docs.modalai.com/voxl-flight-quickstart" target="_blank" rel="noreferrer">here</a>.</p><h3 id="빌드-방법" tabindex="-1">빌드 방법 <a class="header-anchor" href="#빌드-방법" aria-label="Permalink to &quot;빌드 방법&quot;">​</a></h3><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make modalai_fc-v1</span></span></code></pre></div><h2 id="시리얼-포트-매핑" tabindex="-1">시리얼 포트 매핑 <a class="header-anchor" href="#시리얼-포트-매핑" aria-label="Permalink to &quot;시리얼 포트 매핑&quot;">​</a></h2><p><em>Note: mappings shown are for the PX4 controlled interfaces only</em></p><table tabindex="0"><thead><tr><th>UART</th><th>장치</th><th>포트</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1 (J1012)</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3 (J1002)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>디버그 콘솔(J1001)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>확장 UART (J6)</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>PX4와 보조 컴퓨터간의 UART</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>RC (J1004)</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1 (J1010)</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>해당없음</td></tr></tbody></table><h2 id="지원" tabindex="-1">지원 <a class="header-anchor" href="#지원" aria-label="Permalink to &quot;지원&quot;">​</a></h2><p>Please visit the <a href="https://forum.modalai.com/category/8/voxl-flight" target="_blank" rel="noreferrer">ModalAI Forum</a> for more information.</p>',47))])}const _=i(g,[["render",m]]);export{k as __pageData,_ as default};
