import{_ as e,a as r,b as o,c as a}from"./chunks/mro_control_pinouts.COJ70kob.js";import{_ as l,c as i,a8 as n,o as s}from"./chunks/framework.BDnHobkS.js";const _=JSON.parse('{"title":"mRo Control Zero F7 비행 콘트롤러","description":"","frontmatter":{},"headers":[],"relativePath":"ko/flight_controller/mro_control_zero_f7.md","filePath":"ko/flight_controller/mro_control_zero_f7.md"}'),d={name:"ko/flight_controller/mro_control_zero_f7.md"};function c(h,t,p,m,u,b){return s(),i("div",null,t[0]||(t[0]=[n('<h1 id="mro-control-zero-f7-비행-콘트롤러" tabindex="-1">mRo Control Zero F7 비행 콘트롤러 <a class="header-anchor" href="#mro-control-zero-f7-비행-콘트롤러" aria-label="Permalink to &quot;mRo Control Zero F7 비행 콘트롤러&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <em>mRo Control Zero F7<sup>®</sup></em> is a new flight controller from mRo.</p><p><img src="'+e+'" alt="mRo Control Zero F7"></p><p>매우 우수한 트리플 IMU 상용 등급 비행 컨트롤러입니다. 8x PWM 출력(DShot 가능), 3x IMU, 1x 자력계, 1x 기압 센서 (고도계), 6x UART 및 SD 카드가 모두 32mm x 20mm PCB에 포장되어 있습니다. PWM은 양방향이고 EMI로 보호되며 레벨이 5V 로직 레벨로 이동합니다. 모두 전면 및 후면 30 핀 Molex Pico Clasp 커넥터를 사용하여 액세스합니다. 내구성이 강한 플라스틱 케이스, 컨 포멀 보드 코팅, 온도 보정 옵션이 포함되어 있습니다.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="주요-특징" tabindex="-1">주요 특징 <a class="header-anchor" href="#주요-특징" aria-label="Permalink to &quot;주요 특징&quot;">​</a></h2><ul><li><p>마이크로 프로세서:</p><ul><li>FPU가있는 32 비트 STM32F777 코어 텍스<sup>®</sup> M4 코어. 3</li><li>216 MHz/512 KB RAM/2 MB 플래시</li><li>F-RAM Cypress MF25V02-G 256Kbit 비휘발성 메모리(RAM만큼 빠른 플래시 메모리)</li></ul></li><li><p>센서:</p><ul><li><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">Bosch BMI088</a> 3-axis accelerometer/gyroscope (internally vibration dampened)</li><li><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">Invensense ICM-20602</a> 3-axis accelerometer/gyroscope</li><li><a href="https://www.invensense.com/products/motion-tracking/9-axis/icm-20948/" target="_blank" rel="noreferrer">Invensense ICM-20948</a> 3-axis accelerometer/gyroscope/magnetometer</li><li><a href="https://www.infineon.com/cms/en/product/sensor/pressure-sensors/pressure-sensors-for-iot/dps310/" target="_blank" rel="noreferrer">Infineon DPS310 barometer</a> (So smooth and NO more light sensitivity)</li></ul></li><li><p>인터페이스:</p><ul><li>6x UART(총 직렬 포트), 3x(HW 흐름 제어 포함), 1x FRSky Telemetry(D 또는 X 유형), 1x 콘솔 및 1x GPS + I2C</li><li>PWM 출력(모든 DShot 가능) 8개</li><li>CAN 1개</li><li>I2C 1개</li><li>SPI 1개</li><li>Spektrum DSM/DSM2/DSM-X® Satellite 호환 입력 및 바인딩</li><li>Futaba S.BUS® &amp; S.BUS2® compatible input</li><li>FRSky 텔레메트리 포트 출력</li><li>Graupner SUMD</li><li>Yuneec ST24</li><li>PPM 합계 입력 신호</li><li>JTAG(TC2030 커넥터) 1개</li><li>RSSI(PWM 또는 전압) 입력 1개</li><li>삼색 LED</li></ul></li><li><p>중량 및 크기(케이스 없음):</p><ul><li>중량: 5.3g (0.19oz)</li><li>넓이: 20mm (0.79&quot;)</li><li>길이: 32mm (1.26&quot;)</li></ul></li><li><p>전원시스템</p><ul><li>초저잡음 LDO 전압 조정기 3개</li></ul></li></ul><h2 id="구매처" tabindex="-1">구매처 <a class="header-anchor" href="#구매처" aria-label="Permalink to &quot;구매처&quot;">​</a></h2><ul><li><a href="https://store.mrobotics.io/mRo-Control-Zero-F7-p/mro-ctrl-zero-f7.htm" target="_blank" rel="noreferrer">mRo Control Zero</a></li></ul><h2 id="펌웨어-빌드" tabindex="-1">펌웨어 빌드 <a class="header-anchor" href="#펌웨어-빌드" aria-label="Permalink to &quot;펌웨어 빌드&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make mro_ctrl-zero-f7</span></span></code></pre></div><h2 id="디버그-포트" tabindex="-1">디버그 포트 <a class="header-anchor" href="#디버그-포트" aria-label="Permalink to &quot;디버그 포트&quot;">​</a></h2><h3 id="콘솔-포트" tabindex="-1">콘솔 포트 <a class="header-anchor" href="#콘솔-포트" aria-label="Permalink to &quot;콘솔 포트&quot;">​</a></h3><p>The <a href="./../debug/system_console.html">PX4 System Console</a> runs on <code>USART7</code> using the pins listed below. This is a standard serial pinout, designed to connect to a <a href="https://www.digikey.com/en/products/detail/TTL-232R-3V3/768-1015-ND/1836393" target="_blank" rel="noreferrer">3.3V FTDI</a> cable (5V tolerant).</p><table tabindex="0"><thead><tr><th>mRo control zero f7</th><th></th><th>FTDI</th><th></th></tr></thead><tbody><tr><td>17</td><td>USART7 Tx</td><td>5</td><td>FTDI RX (황)</td></tr><tr><td>19</td><td>USART7 Rx</td><td>4</td><td>FTDI TX (적황)</td></tr><tr><td>6</td><td>USART21 GND</td><td>1</td><td>FTDI GND (흑)</td></tr></tbody></table><h3 id="swd-포트" tabindex="-1">SWD 포트 <a class="header-anchor" href="#swd-포트" aria-label="Permalink to &quot;SWD 포트&quot;">​</a></h3><p>The <a href="./../debug/swd_debug.html">SWD port</a> (JTAG) for FMU debugging is a TC2030 debug connector, as shown below.</p><p><img src="'+r+'" alt="mro swd port"></p><p>You can use the <a href="https://www.tag-connect.com/" target="_blank" rel="noreferrer">Tag Connect</a> cable <a href="https://www.tag-connect.com/product/tc2030-idc-nl" target="_blank" rel="noreferrer">TC2030 IDC NL</a> below (with associated <a href="https://www.tag-connect.com/product/tc2030-clip-retaining-clip-board-for-tc2030-nl-cables" target="_blank" rel="noreferrer">retaining clip</a>) to attach to either a BlackMagic probe or a ST-LINK V2 debugger.</p><p><img src="'+o+'" alt="tc2030 idc nl cable"></p><p>There is also an <a href="https://www.tag-connect.com/product/arm20-ctx-20-pin-to-tc2030-idc-adapter-for-cortex" target="_blank" rel="noreferrer">ARM20-CTX 20-Pin to TC2030-IDC adapter</a> that can be used with other debug probes.</p><h2 id="핀배열" tabindex="-1">핀배열 <a class="header-anchor" href="#핀배열" aria-label="Permalink to &quot;핀배열&quot;">​</a></h2><p><img src="'+a+'" alt="mRo Control Zero F7"></p><h2 id="시리얼-포트-매핑" tabindex="-1">시리얼 포트 매핑 <a class="header-anchor" href="#시리얼-포트-매핑" aria-label="Permalink to &quot;시리얼 포트 매핑&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>장치</th><th>포트</th></tr></thead><tbody><tr><td>USART2</td><td>/dev/ttyS0</td><td>TELEM1 (흐름 제어)</td></tr><tr><td>USART3</td><td>/dev/ttyS1</td><td>TELEM2 (흐름 제어)</td></tr><tr><td>UART4</td><td>/dev/ttyS2</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS3</td><td>플렉스 포트(흐름 제어를 사용하여 SPI 또는 UART로 구성할 수 있음).</td></tr><tr><td>UART7</td><td>/dev/ttyS4</td><td>콘솔</td></tr><tr><td>UART8</td><td>/dev/ttyS5</td><td>직렬 포트 여분(일반적으로 FrSky 원격 측정용)</td></tr></tbody></table><h2 id="추가-정보" tabindex="-1">추가 정보 <a class="header-anchor" href="#추가-정보" aria-label="Permalink to &quot;추가 정보&quot;">​</a></h2><ul><li><a href="https://mrobotics.io/introducing-the-new-mro-control-zero-autopilot/" target="_blank" rel="noreferrer">Introducing the new mRo Control Zero Autopilot</a> (blog)</li><li><a href="https://mrobotics.io/mrocontrolzero/" target="_blank" rel="noreferrer">Quick Start Guide</a></li></ul>',30)]))}const k=l(d,[["render",c]]);export{_ as __pageData,k as default};
