import{_ as a}from"./chunks/mro_pixhawk.TeT2w4oe.js";import{_ as e,c as i,a8 as r,o as l}from"./chunks/framework.BDnHobkS.js";const k=JSON.parse('{"title":"mRo Pixhawk 비행 컨트롤러 (Pixhawk 1)","description":"","frontmatter":{},"headers":[],"relativePath":"ko/flight_controller/mro_pixhawk.md","filePath":"ko/flight_controller/mro_pixhawk.md"}'),o={name:"ko/flight_controller/mro_pixhawk.md"};function s(h,t,n,p,d,c){return l(),i("div",null,t[0]||(t[0]=[r('<h1 id="mro-pixhawk-비행-컨트롤러-pixhawk-1" tabindex="-1">mRo Pixhawk 비행 컨트롤러 (Pixhawk 1) <a class="header-anchor" href="#mro-pixhawk-비행-컨트롤러-pixhawk-1" aria-label="Permalink to &quot;mRo Pixhawk 비행 컨트롤러 (Pixhawk 1)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <em>mRo Pixhawk<sup>®</sup></em> is a hardware compatible version of the original <a href="./../flight_controller/pixhawk.html">Pixhawk 1</a>. It runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The controller can be used as a drop-in replacement for the 3DR<sup>&amp;reg;</sup> <a href="./../flight_controller/pixhawk.html">Pixhawk 1</a>. The main difference is that it is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design, which corrects a bug that limited the original Pixhawk 1 to 1MB of flash.</p></div><p><img src="'+a+'" alt="mRo Pixhawk Image"></p><p>Assembly/setup instructions for use with PX4 are provided here: <a href="./../assembly/quick_start_pixhawk.html">Pixhawk Wiring Quickstart</a></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="주요-특징" tabindex="-1">주요 특징 <a class="header-anchor" href="#주요-특징" aria-label="Permalink to &quot;주요 특징&quot;">​</a></h2><ul><li><p>마이크로 프로세서:</p><ul><li>FPU가있는 32 비트 STM32F427 코어 텍스<sup>®</sup> M4 코어</li><li>168 MHz / 256 KB RAM / 2 MB 플래시</li><li>32 비트 STM32F100 failsafe 코프로세서</li><li>24 MHz/8 KB RAM/64 KB 플래시</li></ul></li><li><p>센서:</p><ul><li>ST Micro L3GD20 3축 16비트 자이로스코프</li><li>ST Micro LSM303D 3축 14비트 가속도계/자력계</li><li>Invensense<sup>®</sup> MPU 6000 3축 가속도계/자이로스코프</li><li>MEAS MS5611 기압계</li></ul></li><li><p>인터페이스:</p><ul><li>UART (직렬 포트) 5개, 1 개의 고전력 지원, 2x (HW 흐름 제어 포함)</li><li>CAN 2 개</li><li>최대 DX8의 Spektrum DSM/DSM2/DSM-X® Satellite 호환 입력(DX9 이상은 지원되지 않음)</li><li>Futaba<sup>®</sup> S.BUS 호환 입력 및 출력</li><li>PPM 합계 신호</li><li>RSSI(PWM 또는 전압) 입력</li><li>I2C</li><li>SPI</li><li>3.3 및 6.6V ADC 입력</li><li>외부 microUSB 포트</li></ul></li><li><p>전원시스템</p><ul><li>자동 복구 기능의 이상적인 다이오드 컨트롤러</li><li>서보 레일 고전력 (7V) 및 고전류 준비</li><li>모든 주변 장치 출력 과전류 보호, 모든 입력 ESD 보호</li></ul></li><li><p>중량과 크기</p><ul><li>무게 : 38g (1.31oz)</li><li>너비 : 50mm (1.96 &quot;)</li><li>두께 : 15.5mm (.613 &quot;)</li><li>길이 : 81.5mm (3.21&quot;)</li></ul></li></ul><h2 id="구매처" tabindex="-1">구매처 <a class="header-anchor" href="#구매처" aria-label="Permalink to &quot;구매처&quot;">​</a></h2><ul><li><a href="https://store.mrobotics.io/Genuine-PixHawk-1-Barebones-p/mro-pixhawk1-bb-mr.htm" target="_blank" rel="noreferrer">Bare Bones</a> - Just the board (useful as a 3DR Pixhawk replacement)</li><li><a href="https://store.mrobotics.io/Genuine-PixHawk-Flight-Controller-p/mro-pixhawk1-minkit-mr.htm" target="_blank" rel="noreferrer">mRo Pixhawk 2.4.6 Essential Kit!</a> - Everything except for telemetry radios</li><li><a href="https://store.mrobotics.io/product-p/mro-pixhawk1-fullkit-mr.htm" target="_blank" rel="noreferrer">mRo Pixhawk 2.4.6 Cool Kit! (Limited edition)</a> - Everything you need including telemetry radios</li></ul><h2 id="펌웨어-빌드" tabindex="-1">펌웨어 빌드 <a class="header-anchor" href="#펌웨어-빌드" aria-label="Permalink to &quot;펌웨어 빌드&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v3_default</span></span></code></pre></div><h2 id="디버그-포트" tabindex="-1">디버그 포트 <a class="header-anchor" href="#디버그-포트" aria-label="Permalink to &quot;디버그 포트&quot;">​</a></h2><p>See <a href="./../flight_controller/pixhawk.html#debug-ports">3DR Pixhawk 1 &gt; Debug Ports</a></p><h2 id="핀배열" tabindex="-1">핀배열 <a class="header-anchor" href="#핀배열" aria-label="Permalink to &quot;핀배열&quot;">​</a></h2><p>See <a href="./../flight_controller/pixhawk.html#pinouts">3DR Pixhawk 1 &gt; Pinouts</a></p><h2 id="시리얼-포트-매핑" tabindex="-1">시리얼 포트 매핑 <a class="header-anchor" href="#시리얼-포트-매핑" aria-label="Permalink to &quot;시리얼 포트 매핑&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>장치</th><th>포트</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO 디버그</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (흐름 제어)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (흐름 제어)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>콘솔</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table><h2 id="회로도" tabindex="-1">회로도 <a class="header-anchor" href="#회로도" aria-label="Permalink to &quot;회로도&quot;">​</a></h2><p>The board is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design.</p><ul><li><a href="https://github.com/PX4/Hardware/raw/master/FMUv3_REV_D/Schematic%20Print/Schematic%20Prints.PDF" target="_blank" rel="noreferrer">FMUv3 schematic</a> -- Schematic and layout</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>As a CC-BY-SA 3.0 licensed Open Hardware design, all schematics and design files are <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">available</a>.</p></div>',25)]))}const b=e(o,[["render",s]]);export{k as __pageData,b as default};
