import{_ as a}from"./chunks/pixhack_v3_157_large_default.BolSQN88.js";import{_ as e,c as i,a8 as l,o as r}from"./chunks/framework.BDnHobkS.js";const f=JSON.parse('{"title":"Pixhack V3","description":"","frontmatter":{},"headers":[],"relativePath":"ko/flight_controller/pixhack_v3.md","filePath":"ko/flight_controller/pixhack_v3.md"}'),o={name:"ko/flight_controller/pixhack_v3.md"};function n(d,t,s,h,c,p){return r(),i("div",null,t[0]||(t[0]=[l('<h1 id="pixhack-v3" tabindex="-1">Pixhack V3 <a class="header-anchor" href="#pixhack-v3" aria-label="Permalink to &quot;Pixhack V3&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 does not manufacture this (or any) autopilot. Contact the <a href="https://store.cuav.net/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The CUAV <em>Pixhack V3</em> flight controller board is a flexible autopilot intended primarily for manufacturers of commercial systems.</p><p>The board is a variant of the SOLO Pixhawk<sup>®</sup> 2 (PH2) flight controller, which is in turn based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design. It runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS, and is fully compatible with both PX4 or ArduPilot<sup>®</sup> (APM) firmware.</p><p><em>Pixhack V3</em> has significant improvements with respect to the original design, including better interface layout and the addition of vibration damping and a thermostat system.</p><p><img src="'+a+'" alt="Pixhack v3"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="요약" tabindex="-1">요약 <a class="header-anchor" href="#요약" aria-label="Permalink to &quot;요약&quot;">​</a></h2><ul><li>마이크로 프로세서: <ul><li>STM32F427</li><li>STM32F100 (오류복구 코프로세서)</li></ul></li><li>센서: <ul><li>가속도계 (3): LS303D, MPU6000, MPU9250/hmc5983</li><li>자이로스코프 (3): L3GD20, MPU6000, MPU9250</li><li>나침반 (2): LS303D, MPU9250</li><li>기압계 (2): MS5611 X2</li></ul></li><li>인터페이스: <ul><li>MAVLink UART (2)</li><li>GPS UART (2)</li><li>디버그 UART (1)</li><li>RC 입력(PPM, SBUS, DSM/DSM2 용)</li><li>RSSI 입력: PWM 또는 3.3ADC</li><li>I2C (2)</li><li>CAN 버스 (1)</li><li>ADC 입력: 3.3V X1 , 6.6V X1</li><li>PWM 출력: 8 PWM IO + 4 IO</li></ul></li><li>전원시스템 <ul><li>PM 전원 입력: 4.5 ~ 5.5V</li><li>USB 전원 입력: 5.0V +- 0.25v</li></ul></li><li>중량과 크기 <ul><li>중량: 63g</li><li>폭: 68mm</li><li>두께: 17mm</li><li>길이: 44mm</li></ul></li><li>기타 특성: <ul><li>작동 온도: -20 ~ 60°c</li></ul></li></ul><h2 id="구매처" tabindex="-1">구매처 <a class="header-anchor" href="#구매처" aria-label="Permalink to &quot;구매처&quot;">​</a></h2><p>보드는 아래에서 구입할 수 있습니다.</p><ul><li><a href="http://store.cuav.net/index.php?id_product=8&amp;id_product_attribute=0&amp;rewrite=pixhack-v3-autopilot&amp;controller=product&amp;id_lang=3" target="_blank" rel="noreferrer">store.cuav.net</a></li><li><a href="https://leixun.aliexpress.com/store" target="_blank" rel="noreferrer">leixun.aliexpress.com/store</a></li></ul><h2 id="펌웨어-빌드" tabindex="-1">펌웨어 빌드 <a class="header-anchor" href="#펌웨어-빌드" aria-label="Permalink to &quot;펌웨어 빌드&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v3_default</span></span></code></pre></div><h2 id="핀배열과-회로도" tabindex="-1">핀배열과 회로도 <a class="header-anchor" href="#핀배열과-회로도" aria-label="Permalink to &quot;핀배열과 회로도&quot;">​</a></h2><ul><li><a href="http://doc.cuav.net/flight-controller/pixhack/en/pixhack-v3.html" target="_blank" rel="noreferrer">Documentation/wiring guides</a></li></ul><h2 id="시리얼-포트-매핑" tabindex="-1">시리얼 포트 매핑 <a class="header-anchor" href="#시리얼-포트-매핑" aria-label="Permalink to &quot;시리얼 포트 매핑&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>장치</th><th>포트</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO 디버그</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (흐름 제어)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (흐름 제어)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>콘솔</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',20)]))}const b=e(o,[["render",n]]);export{f as __pageData,b as default};
