import{_ as a}from"./chunks/difficulty_hard.N1nnc_xq.js";import{_ as e}from"./chunks/remote_control.ss1psCxn.js";import{_ as o,c as r,a8 as l,o as i}from"./chunks/framework.BDnHobkS.js";const M=JSON.parse('{"title":"Manual Mode (Fixed-Wing)","description":"","frontmatter":{},"headers":[],"relativePath":"ko/flight_modes_fw/manual.md","filePath":"ko/flight_modes_fw/manual.md"}'),n={name:"ko/flight_modes_fw/manual.md"};function d(s,t,c,h,m,f){return i(),r("div",null,t[0]||(t[0]=[l('<h1 id="manual-mode-fixed-wing" tabindex="-1">Manual Mode (Fixed-Wing) <a class="header-anchor" href="#manual-mode-fixed-wing" aria-label="Permalink to &quot;Manual Mode (Fixed-Wing)&quot;">​</a></h1><p><img src="'+a+'" title="Hard to fly" width="30px"> <img src="'+e+'" title="Manual/Remote control required" width="30px"> </p><p><em>Manual mode</em> sends manual stick input directly to control allocation for fully manual control.</p><p>This is the hardest mode to fly, because nothing is stabilised. Unlike <a href="./../flight_modes_fw/acro.html">Acro Mode</a>, if the roll-pitch stick is centered the vehicle will not automatically stop rotating around the axis — the pilot actually has to move the stick to apply force in the other direction.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This is the only mode that overrides the FMU (commands are sent via the safety coprocessor). It provides a safety mechanism that allows full control of throttle, elevator, ailerons and rudder via RC in the event of an FMU firmware malfunction.</p></div><h2 id="technical-description" tabindex="-1">Technical Description <a class="header-anchor" href="#technical-description" aria-label="Permalink to &quot;Technical Description&quot;">​</a></h2><p>Manual mode where stick input is sent directly to control allocation (for &quot;fully&quot; manual control).</p><p>This is the only mode that overrides the FMU (commands are sent via the safety coprocessor). It provides a safety mechanism that allows full control of throttle, elevator, ailerons and rudder via RC in the event of an FMU firmware malfunction.</p><h2 id="매개변수" tabindex="-1">매개변수 <a class="header-anchor" href="#매개변수" aria-label="Permalink to &quot;매개변수&quot;">​</a></h2><table tabindex="0"><thead><tr><th>매개변수</th><th>설명</th></tr></thead><tbody><tr><td><a id="FW_MAN_P_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_P_SC">FW_MAN_P_SC</a></td><td>Manual pitch scale. Scale factor applied to the desired pitch actuator command in full manual mode. This parameter allows to adjust the throws of the control surfaces. Default: 1.0 norm.</td></tr><tr><td><a id="FW_MAN_R_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_R_SC">FW_MAN_R_SC</a></td><td>Manual roll scale. Scale factor applied to the desired roll actuator command in full manual mode. This parameter allows to adjust the throws of the control surfaces. Default: 1.0 norm.</td></tr><tr><td><a id="FW_MAN_Y_SC"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_Y_SC">FW_MAN_Y_SC</a></td><td>Manual yaw scale. Scale factor applied to the desired yaw actuator command in full manual mode. This parameter allows to adjust the throws of the control surfaces. Default: 1.0 norm.</td></tr></tbody></table>',10)]))}const w=o(n,[["render",d]]);export{M as __pageData,w as default};
