import{_ as a}from"./chunks/leddar_one.bRc46qSb.js";import{_ as t,c as r,a8 as d,o as n}from"./chunks/framework.BDnHobkS.js";const u=JSON.parse('{"title":"LeddarOne 라이다","description":"","frontmatter":{},"headers":[],"relativePath":"ko/sensor/leddar_one.md","filePath":"ko/sensor/leddar_one.md"}'),o={name:"ko/sensor/leddar_one.md"};function l(i,e,s,h,c,p){return n(),r("div",null,e[0]||(e[0]=[d('<h1 id="leddarone-라이다" tabindex="-1">LeddarOne 라이다 <a class="header-anchor" href="#leddarone-라이다" aria-label="Permalink to &quot;LeddarOne 라이다&quot;">​</a></h1><p><a href="https://leddartech.com/solutions/leddarone/" target="_blank" rel="noreferrer">LeddarOne</a> is small Lidar module with a narrow, yet diffuse beam that offers excellent overall detection range and performance, in a robust, reliable, cost-effective package. 감지 범위는 1cm ~ 40m이며 UART/직렬 버스로 연결합니다.</p><img src="'+a+'" alt="LeddarOne Lidar rangefinder" width="200px"><h2 id="하드웨어-설정" tabindex="-1">하드웨어 설정 <a class="header-anchor" href="#하드웨어-설정" aria-label="Permalink to &quot;하드웨어 설정&quot;">​</a></h2><p>LeddarOne can be connected to any unused <em>serial port</em> (UART), e.g.: TELEM2, TELEM3, GPS2 etc.</p><p>보드 핀배열과 LeddarOne 핀배열(아래 참조)에 따라 적절한 케이블을 사용합니다. 5V, TX, RX 및 GND 핀만 연결하면 됩니다.</p><table tabindex="0"><thead><tr><th>핀</th><th>LeddarOne</th></tr></thead><tbody><tr><td>1</td><td>GND</td></tr><tr><td>2</td><td>-</td></tr><tr><td>3</td><td>VCC</td></tr><tr><td>4</td><td>RX</td></tr><tr><td>5</td><td>TX</td></tr><tr><td>6</td><td>-</td></tr></tbody></table><h2 id="매개변수-설정" tabindex="-1">매개변수 설정 <a class="header-anchor" href="#매개변수-설정" aria-label="Permalink to &quot;매개변수 설정&quot;">​</a></h2><p><a href="./../peripherals/serial_configuration.html">Configure the serial port</a> on which the lidar will run using <a href="./../advanced_config/parameter_reference.html#SENS_LEDDAR1_CFG">SENS_LEDDAR1_CFG</a>. 포트 전송속도는 드라이버에 의해 설정되므로, 추가로 설정하지 않아도 됩니다.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>If the configuration parameter is not available in <em>QGroundControl</em> then you may need to <a href="./../peripherals/serial_configuration.html#parameter_not_in_firmware">add the driver to the firmware</a>:</p><div class="language-plain vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">plain</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>CONFIG_DRIVERS_DISTANCE_SENSOR_LEDDAR_ONE=y</span></span></code></pre></div></div><h2 id="추가-정보" tabindex="-1">추가 정보 <a class="header-anchor" href="#추가-정보" aria-label="Permalink to &quot;추가 정보&quot;">​</a></h2><ul><li><a href="https://leddartech.com/app/uploads/dlm_uploads/2021/04/Spec-Sheet_LeddarOne_V10.0_EN-1.pdf" target="_blank" rel="noreferrer">LeddarOne Spec sheet</a></li></ul>',12)]))}const _=t(o,[["render",l]]);export{u as __pageData,_ as default};
