import{_ as a}from"./chunks/fc_orientation_qgc_v2.DeCoF_NT.js";import{_ as r,c as _,a8 as t,o as n}from"./chunks/framework.BDnHobkS.js";const O=JSON.parse('{"title":"Розширене налаштування орієнтації контролера польоту","description":"","frontmatter":{},"headers":[],"relativePath":"uk/advanced_config/advanced_flight_controller_orientation_leveling.md","filePath":"uk/advanced_config/advanced_flight_controller_orientation_leveling.md"}'),o={name:"uk/advanced_config/advanced_flight_controller_orientation_leveling.md"};function i(l,e,c,d,f,h){return n(),_("div",null,e[0]||(e[0]=[t('<h1 id="розширене-налаштування-орієнтаціі-контролера-польоту" tabindex="-1">Розширене налаштування орієнтації контролера польоту <a class="header-anchor" href="#розширене-налаштування-орієнтаціі-контролера-польоту" aria-label="Permalink to &quot;Розширене налаштування орієнтації контролера польоту&quot;">​</a></h1><p>Ці інструкції можна використовувати для ручного налаштування орієнтації та рівня горизонту, наприклад, для виправлення невеликих відхилень датчиків або незначних помилок під час калібрування.</p><p>Якщо існує постійне відхилення (часто спостерігається у багатороторних апаратах, але не обмежується ними), це хороша стратегія виправити його за допомогою цих параметрів зміщення дрібної настройки кута, замість використання ручок регулювання на вашому радіокеруванні. Це забезпечує, що апарат буде зберігати налаштування під час повністю автономного польоту.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Ці інструкції є &quot;продвинутими&quot; і не рекомендуються для звичайних користувачів (загальна настройка зазвичай достатня).</p></div><h2 id="налаштування-параметрів-орієнтаціі" tabindex="-1">Налаштування параметрів орієнтації <a class="header-anchor" href="#налаштування-параметрів-орієнтаціі" aria-label="Permalink to &quot;Налаштування параметрів орієнтації&quot;">​</a></h2><p>Параметр <a href="./../advanced_config/parameter_reference.html#SENS_BOARD_ROT">SENS_BOARD_ROT</a> визначає обертання плати контролера польоту відносно конструкції транспортного засобу, тоді як дрібна настройка зміщень (<a href="./../advanced_config/parameter_reference.html#SENS_BOARD_X_OFF">SENS_BOARD_X_OFF</a>, <a href="./../advanced_config/parameter_reference.html#SENS_BOARD_Y_OFF">SENS_BOARD_Y_OFF</a>, <a href="./../advanced_config/parameter_reference.html#SENS_BOARD_Z_OFF">SENS_BOARD_Z_OFF</a>) встановлює обертання датчиків відносно самої плати. Дрібні настройки зміщень додаються до кута <code>SENS_BOARD_ROT</code>, щоб визначити загальні кутові зміщення для орієнтації по крену, тангажу та рулону контролера польоту. Спочатку виконайте звичайну калібрування для [Орієнтації контролера польоту](../config/flight_controller_orientation. md) та [Калібрування горизонту на рівні](../config/level_horizon_calibration. md), щоб встановити параметр [SENS_BOARD_ROT](../advanced_config/parameter_reference. md#SENS_BOARD_ROT).</p><p>Спочатку виконайте звичайну калібрування для <a href="./../config/flight_controller_orientation.html">Орієнтації контролера польоту</a> та <a href="./../config/level_horizon_calibration.html">Калібрування горизонту на рівні</a>, щоб встановити параметр <a href="./../advanced_config/parameter_reference.html#SENS_BOARD_ROT">SENS_BOARD_ROT</a>.</p><p>Інші параметри можна встановити для точної настройки орієнтації датчиків ІМПУ відносно самої плати.</p><p>Ви можете знайти параметри в QGroundControl, як показано нижче:</p><ol><li><p>Відкрийте меню QGroundControl: <strong>Settings &gt; Parameters &gt; Sensor Calibration</strong>.</p></li><li><p>Параметри, розташовані в розділі, як показано нижче (або ви можете знайти їх):</p><p><img src="'+a+'" alt="FC Orientation QGC v2"></p></li></ol><h2 id="підсумок-параметра" tabindex="-1">Підсумок параметра <a class="header-anchor" href="#підсумок-параметра" aria-label="Permalink to &quot;Підсумок параметра&quot;">​</a></h2><ul><li><a href="./../advanced_config/parameter_reference.html#SENS_BOARD_ROT">SENS_BOARD_ROT</a>: Поворот плати FMU відносно рами транспортного засобу.</li><li><a href="./../advanced_config/parameter_reference.html#SENS_BOARD_X_OFF">SENS_BOARD_X_OFF</a>: Поворот, у градусах, навколо осі X PX4FMU або вісі Крену. Позитивні кути збільшуються в протипротивна годинниковій стрілці (CCW) напрямку, від&#39;ємні кути збільшуються в напрямку за годинниковою стрілкою (CW).</li><li><a href="./../advanced_config/parameter_reference.html#SENS_BOARD_Y_OFF">SENS_BOARD_Y_OFF</a>: Поворот, у градусах, навколо осі X PX4FMU або вісі Крену. Позитивні кути збільшуються в протипротивна годинниковій стрілці (CCW) напрямку, від&#39;ємні кути збільшуються в напрямку за годинниковою стрілкою (CW). Позитивні кути збільшуються в протипротивна годинниковій стрілці (CCW) напрямку, від&#39;ємні кути збільшуються в напрямку за годинниковою стрілкою (CW).</li><li><a href="./../advanced_config/parameter_reference.html#SENS_BOARD_Z_OFF">SENS_BOARD_Z_OFF</a>: Поворот, у градусах, навколо осі Z PX4FMU або вісі Yaw. Позитивні кути збільшуються в протипротивна годинниковій стрілці (CCW) напрямку, від&#39;ємні кути збільшуються в напрямку за годинниковою стрілкою (CW).</li></ul>',12)]))}const m=r(o,[["render",i]]);export{O as __pageData,m as default};
