import{_ as t,a as r,b as a,c as i,d as l,e as s,f as n,g as o,h as p,i as h}from"./chunks/pix32_v5_sd_card._CPTUtPA.js";import{_ as c,c as d,a8 as m,o as u}from"./chunks/framework.BDnHobkS.js";const y=JSON.parse('{"title":"Швидкий старт Pix32 v5 Wiring","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/quick_start_holybro_pix32_v5.md","filePath":"uk/assembly/quick_start_holybro_pix32_v5.md"}'),f={name:"uk/assembly/quick_start_holybro_pix32_v5.md"};function b(g,e,P,_,v,w){return u(),d("div",null,e[0]||(e[0]=[m('<h1 id="швидкии-старт-pix32-v5-wiring" tabindex="-1">Швидкий старт Pix32 v5 Wiring <a class="header-anchor" href="#швидкии-старт-pix32-v5-wiring" aria-label="Permalink to &quot;Швидкий старт Pix32 v5 Wiring&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>This quick start guide shows how to power the <a href="./../flight_controller/holybro_pix32_v5.html">Holybro Pix32v5</a><sup>®</sup> flight controller and connect its most important peripherals.</p><p><img src="'+t+'" alt="Pix32 v5 With Base"></p><h2 id="розпаковка" tabindex="-1">Розпаковка <a class="header-anchor" href="#розпаковка" aria-label="Permalink to &quot;Розпаковка&quot;">​</a></h2><p>Pix32 v5 is sold bundled with a number of different combinations of accessories, including the <em>pix32 v5 Base board</em>, power module <em>PM02 V3</em>, and the <a href="https://holybro.com/collections/gps/products/m8n-gps" target="_blank" rel="noreferrer">Holybro M8N GPS</a> (UBLOX NEO-M8N).</p><p>The content of the box with the <em>PM02 V3</em> power module and <em>Pixhawk 4 GPS/Compass</em> is shown below. У коробці також є керівництво з роз&#39;ємів та інструкції щодо модуля живлення, а також базова плата (не показана на схемі нижче).</p><p><img src="'+r+'" alt="Pix32 v5 Box"></p><h2 id="огляд-схеми-підключення" tabindex="-1">Огляд схеми підключення <a class="header-anchor" href="#огляд-схеми-підключення" aria-label="Permalink to &quot;Огляд схеми підключення&quot;">​</a></h2><p>На зображенні нижче показано, як під&#39;єднати найважливіші датчики та периферійні пристрої (за винятком виходів мотора та сервоприводів). Ми розглянемо кожну з них докладно в наступних розділах.</p><p><img src="'+a+'" alt="Pix32 v5 Wiring Overview"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>More information about available ports can be found <a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_Pix32-V5-Base-Mini-Pinouts.pdf" target="_blank" rel="noreferrer">here</a>.</p></div><h2 id="монтаж-та-орієнтація-контролера" tabindex="-1">Монтаж та орієнтація контролера <a class="header-anchor" href="#монтаж-та-орієнтація-контролера" aria-label="Permalink to &quot;Монтаж та орієнтація контролера&quot;">​</a></h2><p><em>Pix32 v5</em> should be mounted on the frame positioned as close to your vehicle’s center of gravity as possible, oriented top-side up with the arrow pointing towards the front of the vehicle.</p><p><img src="'+i+'" alt="Pix32 v5 With Orientation"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Якщо контролер не може бути змонтований у рекомендованому/стандартному положенні (наприклад, через обмеження місця), вам потрібно буде налаштувати програмне забезпечення автопілота з орієнтацією, яку ви фактично використовували: <a href="./../config/flight_controller_orientation.html">Орієнтація контролера польоту</a>.</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The board has internal vibration-isolation. Не використовуйте віброізоляційну пінку для монтажу контролера (подвійна стрічка на клейовій основі зазвичай достатня).</p></div><h2 id="gps-компас-зумер-захиснии-вимикач-світлодіод" tabindex="-1">GPS + компас + зумер + захисний вимикач + світлодіод <a class="header-anchor" href="#gps-компас-зумер-захиснии-вимикач-світлодіод" aria-label="Permalink to &quot;GPS + компас + зумер + захисний вимикач + світлодіод&quot;">​</a></h2><p>Pix32 v5 is designed to work well with the <a href="https://holybro.com/collections/gps/products/m8n-gps" target="_blank" rel="noreferrer">Holybro M8N GPS</a>, which has an integrated compass, safety switch, buzzer and LED. It connects directly to the <strong>GPS port</strong> using the 10 pin cable.</p><p><img src="'+l+'" alt="Pix32 v5 with GPS"></p><p>GPS/Компас слід монтувати на раму якомога подалі від інших електронних пристроїв, з напрямком вперед транспортного засобу (відокремлення компаса від інших електронних пристроїв зменшить втручання).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Вбудований безпечний вимикач в GPS-модулі увімкнений <em>за замовчуванням</em> (коли включений, PX4 не дозволить вам готувати до польоту). Щоб вимкнути безпеку, натисніть і утримуйте безпечний вимикач протягом 1 секунди. Ви можете натиснути безпечний вимикач знову, щоб увімкнути безпеку та відключити транспортний засіб (це може бути корисно, якщо, з якихось причин, ви не можете вимкнути транспортний засіб за допомогою вашого пульта дистанційного керування або наземної станції).</p></div><h2 id="power" tabindex="-1">Power <a class="header-anchor" href="#power" aria-label="Permalink to &quot;Power&quot;">​</a></h2><p>Ви можете використовувати модуль живлення або розподільник живлення для живлення двигунів/сервоприводів та виміру споживаної потужності. Рекомендовані модулі живлення показані нижче.</p><p><a id="pm02_v3"></a></p><h3 id="pm02d-power-module" tabindex="-1">PM02D Power Module <a class="header-anchor" href="#pm02d-power-module" aria-label="Permalink to &quot;PM02D Power Module&quot;">​</a></h3><p>The <a href="./../power_module/holybro_pm02.html">Power Module (PM02 v3)</a> can be bundled with <em>pix32 v5</em>. Він надає регульоване живлення контролеру польоту та надсилає напругу/силу струму акумулятора контролеру польоту.</p><p>Connect the output of the <em>Power Module</em> as shown.</p><p><img src="'+s+'" alt="Pix32 v5 With Power Module"></p><ul><li>PM voltage/current port: connect to POWER1 port (or <code>POWER2</code>) using the 6-wire GH cable supplied.</li><li>Вхід PM (роз&#39;єм XT60): підключіть до ліпо-акумулятора (2~12S).</li><li>Вихід живлення PM (роз&#39;єм XT60): підведіть до будь-якого контролера регулятора обертів мотора.</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>As this power module does not include power distribution wiring, you would normally just connect all the ESCs in parallel to the power module output (the ESC must be appropriate for the supplied voltage level).</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The 8 pin power (+) rail of <strong>MAIN/AUX</strong> is not powered by the power module supply to the flight controller. Якщо вона повинна бути окремо живленою для керування сервоприводами для рульових поверхонь, елеронами тощо, лінію живлення потрібно підключити до ESC зі вбудованим BEC або окремого BEC напругою 5V або акумулятора LiPo 2S. Переконайтеся, що напруга сервопривода, яку ви збираєтеся використовувати, відповідає.</p></div><p>Модуль живлення має наступні характеристики/обмеження:</p><ul><li>Максимальна вхідна напруга: 60V</li><li>Максимальне вимірювання струму: 120A Voltage</li><li>Вимірювання струму, налаштоване для SV АЦП Перемикання виходів регулятора 5,2 В і 3А макс</li><li>Вага: 20г</li><li>Пакет включає: <ul><li>Плата PM02</li><li>6-контактний кабель MLX (1)</li><li>6pin GH cable (1)</li></ul></li></ul><h3 id="конфігурація-батареі" tabindex="-1">Конфігурація батареї <a class="header-anchor" href="#конфігурація-батареі" aria-label="Permalink to &quot;Конфігурація батареї&quot;">​</a></h3><p>The battery/power setup must be configured in <a href="./../config/battery.html">Battery Estimation Tuning</a>. For either Power Module you will need to configure the <em>Number of Cells</em>.</p><p>You will not need to update the <em>voltage divider</em> unless you are using some other power module (e.g. the one from the Pixracer).</p><h2 id="радіоуправління" tabindex="-1">Радіоуправління <a class="header-anchor" href="#радіоуправління" aria-label="Permalink to &quot;Радіоуправління&quot;">​</a></h2><p>Для того щоб керувати транспортним засобом <em>вручну</em>, потрібна система радіоуправління (RC) (PX4 не потребує системи радіоуправління для автономних режимів польоту).</p><p>Вам потрібно <a href="./../getting_started/rc_transmitter_receiver.html">вибрати сумісний передавач/приймач</a> і <em>зв&#39;язати</em> їх таким чином, щоб вони взаємодіяли (ознайомтеся з інструкціями, що додаються до вашого конкретного передавача/приймача).</p><p>The instructions below show how to connect the different types of receivers to <em>Pix32 v5</em> with Baseboard:</p><ul><li><p>Spektrum/DSM receivers connect to the <strong>DSM RC</strong> input shown below.</p><p><img src="'+n+'" alt="Pix32v5 rc receivers"></p></li><li><p>PPM and S.Bus receivers connect to the <strong>SBUS_IN/PPM_IN</strong> input port (marked as RC IN):</p><p><img src="'+o+'" alt="Pinouts"></p></li><li><p>PPM and PWM receivers that have an <em>individual wire for each channel</em> must connect to the <strong>PPM RC</strong> port <em>via a PPM encoder</em> <a href="http://www.getfpv.com/radios/radio-accessories/holybro-ppm-encoder-module.html" target="_blank" rel="noreferrer">like this one</a> (PPM-Sum receivers use a single signal wire for all channels).</p></li></ul><p>Для отримання додаткової інформації про вибір радіосистеми, сумісність приймача та зв&#39;язок вашої передавача/приймача, див. статтю: <a href="./../getting_started/rc_transmitter_receiver.html">Пульт керування передавачів &amp; приймачів</a>.</p><h2 id="телеметричні-радіостанціі-опціонально" tabindex="-1">Телеметричні радіостанції (Опціонально) <a class="header-anchor" href="#телеметричні-радіостанціі-опціонально" aria-label="Permalink to &quot;Телеметричні радіостанції (Опціонально)&quot;">​</a></h2><p>Телеметричні радіостанції можуть використовуватися для зв&#39;язку та управління транспортним засобом у польоті з наземної станції (наприклад, ви можете направляти БПЛА до певної позиції або завантажувати нове завдання).</p><p>The vehicle-based radio should be connected to the <strong>TELEM1</strong> port as shown below (if connected to this port, no further configuration is required). Інша радіостанція підключається до вашого комп&#39;ютера або мобільного пристрою наземної станції (зазвичай за допомогою USB).</p><p><img src="'+p+'" alt="Pix32 v5 With Telemetry Radios"></p><h2 id="sd-карта-опціонально" tabindex="-1">SD-карта (Опціонально) <a class="header-anchor" href="#sd-карта-опціонально" aria-label="Permalink to &quot;SD-карта (Опціонально)&quot;">​</a></h2><p>SD cards are most commonly used to <a href="./../getting_started/flight_reporting.html">log and analyse flight details</a>. A micro SD card should come preinstalled on the pix32 v5, if you have your own micro SD card, insert the card into <em>pix32 v5</em> as shown below.</p><p><img src="'+h+'" alt="Pix32 v5 With SD Card"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The SanDisk Extreme U3 32GB is <a href="./../dev_log/logging.html#sd-cards">highly recommended</a>.</p></div><h2 id="двигуни" tabindex="-1">Двигуни <a class="header-anchor" href="#двигуни" aria-label="Permalink to &quot;Двигуни&quot;">​</a></h2><p>Motors/servos control signals are connected to the <strong>I/O PWM OUT</strong> (<strong>MAIN</strong>) and <strong>FMU PWM OUT</strong> (<strong>AUX</strong>) ports in the order specified for your vehicle in the <a href="./../airframes/airframe_reference.html">Airframe Reference</a>.</p><p><img src="'+o+'" alt="Pix32 v5 - Back Pinouts (Schematic)"></p><p>The motors must be separately <a href="#power">powered</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>If your frame is not listed in the airframe reference then use a &quot;generic&quot; airframe of the correct type.</p></div><h2 id="інші-периферіині-пристроі" tabindex="-1">Інші периферійні пристрої <a class="header-anchor" href="#інші-периферіині-пристроі" aria-label="Permalink to &quot;Інші периферійні пристрої&quot;">​</a></h2><p>Підключення та конфігурація додаткових/менш поширених компонентів описано в темах для окремих <a href="./../peripherals/">периферійних пристроїв</a>.</p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_Pix32-V5-Base-Mini-Pinouts.pdf" target="_blank" rel="noreferrer">Pix32 v5 Pinouts</a> (Holybro)</p><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><p>Загальну інформацію про конфігурацію описано в: <a href="./../config/">Конфігурація автопілота</a>.</p><p>Конкретні конфігурації QuadPlane тут: <a href="./../config_vtol/vtol_quad_configuration.html">QuadPlane VTOL налаштування</a></p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="./../flight_controller/holybro_pix32_v5.html">Pix32 v5 Overview</a> (Overview page)</li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_PIX32-V5_technical_data_sheet_v1.1.pdf" target="_blank" rel="noreferrer">Pix32 v5 Technical Data Sheet</a></li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_Pix32-V5-Base-Mini-Pinouts.pdf" target="_blank" rel="noreferrer">Pix32 v5 Pinouts</a></li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Holybro_PIX32-V5-BASE-Schematic_diagram.pdf" target="_blank" rel="noreferrer">Pix32 v5 Base Schematic Diagram</a></li><li><a href="https://holybro.com/manual/Holybro_PIX32-V5-BASE-ComponentsLayout.pdf" target="_blank" rel="noreferrer">Pix32 v5 Base Components Layout</a></li><li><a href="https://docs.google.com/spreadsheets/d/1-n0__BYDedQrc_2NHqBenG1DNepAgnHpSGglke-QQwY/edit#gid=912976165" target="_blank" rel="noreferrer">FMUv5 reference design pinout</a>.</li></ul>',65)]))}const q=c(f,[["render",b]]);export{y as __pageData,q as default};
