import{_ as n,a as h,b as d}from"./chunks/pixhawk6x_vehicle_front1.BptV5iKM.js";import{_ as p}from"./chunks/pixhawk6x_wiring_diagram.BpS__0X1.js";import{_ as c}from"./chunks/pixhawk5x_gps_front.Byd-8jGV.js";import{_ as m}from"./chunks/pixhawk5x_sd_slot.BS4580eU.js";import{_ as b,c as u,a8 as s,G as a,w as r,B as i,o as f,j as e}from"./chunks/framework.BDnHobkS.js";const C=JSON.parse('{"title":"Короткий посібник з підключення Holybro Pixhawk 6X","description":"","frontmatter":{},"headers":[],"relativePath":"uk/assembly/quick_start_pixhawk6x.md","filePath":"uk/assembly/quick_start_pixhawk6x.md"}'),k={name:"uk/assembly/quick_start_pixhawk6x.md"};function g(P,t,w,x,_,y){const o=i("tab"),l=i("tabs");return f(),u("div",null,[t[2]||(t[2]=s('<h1 id="короткии-посібник-з-підключення-holybro-pixhawk-6x" tabindex="-1">Короткий посібник з підключення Holybro Pixhawk 6X <a class="header-anchor" href="#короткии-посібник-з-підключення-holybro-pixhawk-6x" aria-label="Permalink to &quot;Короткий посібник з підключення Holybro Pixhawk 6X&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>This quick start guide shows how to power the <a href="./../flight_controller/pixhawk6x.html">Pixhawk 6X<sup>®</sup></a> flight controller and connect its most important peripherals.</p><h2 id="вміст-набору" tabindex="-1">Вміст набору <a class="header-anchor" href="#вміст-набору" aria-label="Permalink to &quot;Вміст набору&quot;">​</a></h2>',4)),a(l,null,{default:r(()=>[a(o,{name:"Набір Pixhawk 6X Standard"},{default:r(()=>t[0]||(t[0]=[e("p",null,[e("img",{src:n,alt:"Pixhawk 6x standard set"})],-1)])),_:1}),a(o,{name:"Набір Pixhawk 6X Mini"},{default:r(()=>t[1]||(t[1]=[e("p",null,[e("img",{src:h,alt:"Pixhawk 6x mini standard set"})],-1)])),_:1})]),_:1}),t[3]||(t[3]=s('<h2 id="огляд-схеми-підключення" tabindex="-1">Огляд схеми підключення <a class="header-anchor" href="#огляд-схеми-підключення" aria-label="Permalink to &quot;Огляд схеми підключення&quot;">​</a></h2><p>На зображенні нижче показано, як під&#39;єднати найважливіші датчики та периферійні пристрої.</p><p><img src="'+p+'" alt="Pixhawk 6x Wiring Overview"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>More information about available ports can be found here: <a href="./../flight_controller/pixhawk6x.html#connections">Pixhawk 6X &gt; Connections</a>.</p></div><h2 id="монтаж-та-орієнтація-контролера" tabindex="-1">Монтаж та орієнтація контролера <a class="header-anchor" href="#монтаж-та-орієнтація-контролера" aria-label="Permalink to &quot;Монтаж та орієнтація контролера&quot;">​</a></h2><p><em>Pixhawk 6X</em> can be mounted on the frame using double side tape included in the kit. Вона повинна розташовуватися якомога ближче до центру тяжіння вашого апарату верхньою стороною вгору зі стрілкою, що вказує в напрямку передньої частини апарату.</p><img src="'+d+'" width="400px" title="Pixhawk6x"><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Якщо контролер не може бути змонтований у рекомендованому/стандартному положенні (наприклад, через обмеження місця), вам потрібно буде налаштувати програмне забезпечення автопілота з орієнтацією, яку ви фактично використовували: <a href="./../config/flight_controller_orientation.html">Орієнтація контролера польоту</a>.</p></div><h2 id="gps-компас-зумер-захиснии-вимикач-світлодіод" tabindex="-1">GPS + компас + зумер + захисний вимикач + світлодіод <a class="header-anchor" href="#gps-компас-зумер-захиснии-вимикач-світлодіод" aria-label="Permalink to &quot;GPS + компас + зумер + захисний вимикач + світлодіод&quot;">​</a></h2><p>The <em>Pixhawk6X Standard Set</em> &amp; <em>Pixhawk6X Mini Set</em> can be purchased with M8N or M9N GPS (10-pin connector) that should be connected to the <strong>GPS1</strong> port. Ці модулі GNSS мають вбудований компас, безпечний перемикач, дзвіночок та світлодіод.</p><p>A secondary <a href="https://holybro.com/collections/gps" target="_blank" rel="noreferrer">M8N or M9N GPS</a> (6-pin connector) can be purchased separately and connected to the <strong>GPS2</strong> port.</p><p>The GPS/Compass should be <a href="./../assembly/mount_gps_compass.html">mounted on the frame</a> as far away from other electronics as possible, with the direction marker towards the front of the vehicle (separating the compass from other electronics will reduce interference).</p><img src="'+c+'" width="200px" title="Pixhawk5x standard set"><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Вбудований безпечний вимикач в GPS-модулі увімкнений <em>за замовчуванням</em> (коли включений, PX4 не дозволить вам готувати до польоту). Щоб вимкнути безпеку, натисніть і утримуйте безпечний вимикач протягом 1 секунди. Ви можете натиснути безпечний вимикач знову, щоб увімкнути безпеку та відключити транспортний засіб (це може бути корисно, якщо, з якихось причин, ви не можете вимкнути транспортний засіб за допомогою вашого пульта дистанційного керування або наземної станції).</p></div><h2 id="power" tabindex="-1">Power <a class="header-anchor" href="#power" aria-label="Permalink to &quot;Power&quot;">​</a></h2><p>Connect the output of the <em>PM02D Power Module</em> (PM board) that comes with the Standard Set to one of the <strong>POWER</strong> port of <em>Pixhawk 6X</em> using the 6-wire cable. The PM02D and Power ports on the Pixhawk 6X uses the 6 circuit <a href="https://www.molex.com/molex/products/part-detail/pcb_receptacles/5024430670" target="_blank" rel="noreferrer">2.00mm Pitch CLIK-Mate Wire-to-Board PCB Receptacle</a> &amp; <a href="https://www.molex.com/molex/products/part-detail/crimp_housings/5024390600" target="_blank" rel="noreferrer">Housing</a>.</p><p>The PM02D Power Module supports <strong>2~6S</strong> battery, the board input should be connected to your LiPo battery. Note that the PM board does not supply power to the + and - pins of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong>.</p><p>If using a plane or rover, the <strong>FMU PWM-OUT</strong> will need to be separately powered in order to drive servos for rudders, elevons etc. This can be done by connecting the 8 pin power (+) rail of the <strong>FMU PWM-OUT</strong> to a voltage regulator (for example, a BEC equipped ESC or a standalone 5V BEC or a 2S LiPo battery).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Напруга шини живлення повинна бути відповідною для використаного сервоприводу!</p></div><table tabindex="0"><thead><tr><th>PIN &amp; Connector</th><th>Функція</th></tr></thead><tbody><tr><td>I/O PWM Out</td><td>Підключіть сигнальні та земельні проводи двигуна тут.</td></tr><tr><td>FMU PWM Out</td><td>Підключіть сигнальні, позитивні та GND-проводи сервоприводу сюди.</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">INFO</p><p><strong>MAIN</strong> outputs in PX4 firmware map to <strong>I/O PWM OUT</strong> port of <em>Pixhawk 6X</em> whereas <strong>AUX outputs</strong> map to <strong>FMU PWM OUT</strong> of <em>Pixhawk 6X</em>. For example, <strong>MAIN1</strong> maps to IO_CH1 pin of <strong>I/O PWM OUT</strong> and <strong>AUX1</strong> maps to FMU_CH1 pin of <strong>FMU PWM OUT</strong>.</p></div><p>The pinout of <em>Pixhawk 6X</em>’s power ports is shown below. Роз&#39;єми живлення приймають цифровий сигнал I2C від модуля живлення PM02D для даних про напругу та силу струму. Лінії VCC повинні пропонувати принаймні 3A безперервного струму і за замовчуванням повинні мати напругу 5,2 В. Нижчий напруга 5V все ще прийнятний, але не рекомендується.</p><table tabindex="0"><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1(red)</td><td>VCC</td><td>+5V</td></tr><tr><td>2(black)</td><td>VCC</td><td>+5V</td></tr><tr><td>3(black)</td><td>SCL</td><td>+3.3V</td></tr><tr><td>4(black)</td><td>SDA</td><td>+3.3V</td></tr><tr><td>5(black)</td><td>GND</td><td>GND</td></tr><tr><td>6(black)</td><td>GND</td><td>GND</td></tr></tbody></table><h2 id="радіоуправління" tabindex="-1">Радіоуправління <a class="header-anchor" href="#радіоуправління" aria-label="Permalink to &quot;Радіоуправління&quot;">​</a></h2><p>Для того щоб керувати транспортним засобом <em>вручну</em>, потрібна система радіоуправління (RC) (PX4 не потребує системи радіоуправління для автономних режимів польоту).</p><p>Вам потрібно <a href="./../getting_started/rc_transmitter_receiver.html">вибрати сумісний передавач/приймач</a> і <em>зв&#39;язати</em> їх таким чином, щоб вони взаємодіяли (ознайомтеся з інструкціями, що додаються до вашого конкретного передавача/приймача).</p><ul><li>Spektrum/DSM receivers connect to the <strong>DSM/SBUS RC</strong> input.</li><li>PPM or SBUS receivers connect to the <strong>RC IN</strong> input port.</li></ul><p>PPM and PWM receivers that have an <em>individual wire for each channel</em> must connect to the <strong>RC IN</strong> port <em>via a PPM encoder</em> <a href="http://www.getfpv.com/radios/radio-accessories/holybro-ppm-encoder-module.html" target="_blank" rel="noreferrer">like this one</a> (PPM-Sum receivers use a single signal wire for all channels).</p><p>Для отримання додаткової інформації про вибір радіосистеми, сумісність приймача та зв&#39;язок вашої передавача/приймача, див. статтю: <a href="./../getting_started/rc_transmitter_receiver.html">Пульт керування передавачів &amp; приймачів</a>.</p><h2 id="телеметричні-радіостанціі-опціонально" tabindex="-1">Телеметричні радіостанції (Опціонально) <a class="header-anchor" href="#телеметричні-радіостанціі-опціонально" aria-label="Permalink to &quot;Телеметричні радіостанції (Опціонально)&quot;">​</a></h2><p><a href="./../telemetry/">Телеметричні радіостанції</a> можуть використовуватися для зв&#39;язку та управління транспортним засобом у польоті з наземної станції (наприклад, ви можете направляти БПЛА до певної позиції або завантажувати нове завдання).</p><p>The vehicle-based radio should be connected to the <strong>TELEM1</strong> port as shown below (if connected to this port, no further configuration is required). Інша радіостанція підключається до вашого комп&#39;ютера або мобільного пристрою наземної станції (зазвичай за допомогою USB).</p><p>Radios are also available for purchase on <a href="https://holybro.com/collections/telemetry-radios" target="_blank" rel="noreferrer">Holybro&#39;s website</a> .</p><h2 id="sd-карта-опціонально" tabindex="-1">SD-карта (Опціонально) <a class="header-anchor" href="#sd-карта-опціонально" aria-label="Permalink to &quot;SD-карта (Опціонально)&quot;">​</a></h2><p>SD cards are highly recommended as they are needed to <a href="./../getting_started/flight_reporting.html">log and analyse flight details</a>, to run missions, and to use UAVCAN-bus hardware. Insert the card (included in Pixhawk 6X) into <em>Pixhawk 6X</em> as shown below.</p><img src="'+m+'" width="420px" title="Pixhawk5x standard set"><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Для отримання додаткової інформації див. <a href="./../getting_started/px4_basic_concepts.html#sd-cards-removable-memory">Основні концепції &gt; SD-карти (знімна пам&#39;ять)</a>.</p></div><h2 id="двигуни" tabindex="-1">Двигуни <a class="header-anchor" href="#двигуни" aria-label="Permalink to &quot;Двигуни&quot;">​</a></h2><p>Motors/servos are connected to the <strong>I/O PWM OUT</strong> (<strong>MAIN</strong>) and <strong>FMU PWM OUT</strong> (<strong>AUX</strong>) ports in the order specified for your vehicle in the <a href="./../airframes/airframe_reference.html">Airframe Reference</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Цей довідник містить зіставлення портів виводу до моторів/сервоприводів для всіх підтримуваних повітряних та наземних шасі (якщо ваше шасі не вказане в довіднику, то використовуйте &quot;загальний&quot; планер відповідного типу).</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Відображення не є однорідним для всіх конструкцій (наприклад, ви не можете покладатися на те, що ручка газу буде на тому ж вихідному порту для всіх повітряних конструкцій). Переконайтеся, що ви використовуєте правильне зіставлення для вашого апарату.</p></div><h2 id="інші-периферіині-пристроі" tabindex="-1">Інші периферійні пристрої <a class="header-anchor" href="#інші-периферіині-пристроі" aria-label="Permalink to &quot;Інші периферійні пристрої&quot;">​</a></h2><ul><li><a href="https://holybro.com/collections/telemetry-radios?orderby=date" target="_blank" rel="noreferrer">Telemetry Radio Modules</a></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li><li><a href="https://holybro.com/collections/sensors" target="_blank" rel="noreferrer">Holybro Sensors</a></li><li><a href="https://holybro.com/collections/gps-rtk-systems" target="_blank" rel="noreferrer">Holybro GPS &amp; RTK Systems</a></li><li><a href="https://holybro.com/collections/power-modules-pdbs" target="_blank" rel="noreferrer">Power Modules &amp; PDBs</a></li></ul><p>Підключення та конфігурація додаткових/менш поширених компонентів описано в темах для окремих <a href="./../peripherals/">периферійних пристроїв</a>.</p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Baseboard Pinout</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-mini-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Mini-Baseboard Pinout</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-baseboards/pixhawk-jetson-baseboard" target="_blank" rel="noreferrer">Holybro Pixhawk Jetson Baseboard</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-baseboards/pixhawk-rpi-cm4-baseboard" target="_blank" rel="noreferrer">Holybro Pixhawk RPi CM4 Baseboard</a></li></ul><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><p>Загальну інформацію про конфігурацію описано в: <a href="./../config/">Конфігурація автопілота</a>.</p><p>Конкретні конфігурації QuadPlane тут: <a href="./../config_vtol/vtol_quad_configuration.html">QuadPlane VTOL налаштування</a></p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/" target="_blank" rel="noreferrer">Holybro Docs</a> (Holybro)</li><li><a href="./../flight_controller/pixhawk6x.html">Pixhawk 6X</a> (PX4 Doc Overview page)</li><li><a href="./../flight_controller/pixhawk6x_pro.html">Pixhawk 6X Pro</a> (PX4 Doc Overview page)</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot FMUv6X Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a>.</li></ul>',51))])}const X=b(k,[["render",g]]);export{C as __pageData,X as default};
