import{_ as e,c as r,a8 as t,o as l}from"./chunks/framework.BDnHobkS.js";const f=JSON.parse('{"title":"Камери","description":"","frontmatter":{},"headers":[],"relativePath":"uk/camera/index.md","filePath":"uk/camera/index.md"}'),i={name:"uk/camera/index.md"};function n(m,a,c,h,o,s){return l(),r("div",null,a[0]||(a[0]=[t('<h1 id="камери" tabindex="-1">Камери <a class="header-anchor" href="#камери" aria-label="Permalink to &quot;Камери&quot;">​</a></h1><p>Камери є важливими для багатьох <a href="./../payloads/use_cases.html">використання вантажів</a>, включаючи картографування та обстеження, нагляд, пошук та рятування, визначення стану та виявлення шкідників у посівах і.т.д. Вони зазвичай монтувалися на <a href="./../advanced/gimbal_control.html">шарнірну підвіску</a>, яка може забезпечувати стабілізацію камери, відстеження об&#39;єкту та рух незалежно від транспортного засобу, на якому вони розміщені.</p><h2 id="типи-камер" tabindex="-1">Типи камер <a class="header-anchor" href="#типи-камер" aria-label="Permalink to &quot;Типи камер&quot;">​</a></h2><p>PX4 інтегрується з трьома типами камер:</p><ul><li><a href="./../camera/mavlink_v2_camera.html">Камери MAVLink</a>, які підтримують <a href="https://mavlink.io/en/services/camera.html" target="_blank" rel="noreferrer">Протокол Камери v2</a> (<strong>РЕКОМЕНДОВАНО</strong>).</li><li><a href="./../camera/mavlink_v1_camera.html">Прості камери MAVLink</a>, які підтримують старший <a href="https://mavlink.io/en/services/camera.html" target="_blank" rel="noreferrer">Протокол камери v1</a>.</li><li><a href="./../camera/fc_connected_camera.html">Камери, підключені до виходів контролера польоту</a>, які керуються з використанням <a href="https://mavlink.io/en/services/camera.html" target="_blank" rel="noreferrer">протоколу камери v1</a>.</li></ul><p><a href="./../camera/mavlink_v2_camera.html">Камери MAVLink</a> рекомендовані, оскільки вони забезпечують широкий доступ до функцій камери, використовуючи простий та послідовний набір команд/повідомлень. Якщо камера не підтримує цей протокол, можна використовувати <a href="./../camera/mavlink_v2_camera.html#camera-managers">менеджер камери</a>, який працює на супутниковому комп&#39;ютері, щоб забезпечити взаємодію між MAVLink та власним протоколом камери.</p><h2 id="дивіться-також" tabindex="-1">Дивіться також <a class="header-anchor" href="#дивіться-також" aria-label="Permalink to &quot;Дивіться також&quot;">​</a></h2><ul><li><a href="./../advanced/gimbal_control.html">Gimbal (кріплення для камери)</a></li><li><a href="./../camera/camera_architecture.html">Інтеграція/Архітектура камери</a> (Розробники PX4)</li></ul>',8)]))}const _=e(i,[["render",n]]);export{f as __pageData,_ as default};
