import{_ as a,c as e,a8 as o,o as i}from"./chunks/framework.BDnHobkS.js";const d=JSON.parse('{"title":"Motion Capture (MoCap)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/computer_vision/motion_capture.md","filePath":"uk/computer_vision/motion_capture.md"}'),r={name:"uk/computer_vision/motion_capture.md"};function n(p,t,m,s,l,c){return i(),e("div",null,t[0]||(t[0]=[o('<h1 id="motion-capture-mocap" tabindex="-1">Motion Capture (MoCap) <a class="header-anchor" href="#motion-capture-mocap" aria-label="Permalink to &quot;Motion Capture (MoCap)&quot;">​</a></h1><p>Захоплення руху (MoCap) — це техніка <a href="https://en.wikipedia.org/wiki/Computer_vision" target="_blank" rel="noreferrer">комп’ютерного бачення</a> для оцінки 3D <em>pose</em> (положення та орієнтації) транспортного засобу за допомогою механізму позиціонування, який є <em>зовнішнім</em> до засобу. Він зазвичай використовується для навігації транспортним засобом у ситуаціях, коли GPS відсутній (наприклад, у приміщенні), і надає положення відносно <em>локальної</em> системи координат.</p><p>Системи <em>MoCap</em> найчастіше виявляють рух за допомогою інфрачервоних камер, але також можна використовувати інші типи камер, Lidar або Ultra Wideband (UWB).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><em>MoCap</em> концептуально схожий на <a href="./../computer_vision/visual_inertial_odometry.html">Visual Inertial Odometry (VIO)</a>. Основна відмінність полягає в тому, що у VIO система бачення працює на транспортному засобі та додатково використовує IMU транспортного засобу для надання інформації про швидкість.</p></div><h2 id="mocap-ресурси" tabindex="-1">MoCap Ресурси <a class="header-anchor" href="#mocap-ресурси" aria-label="Permalink to &quot;MoCap Ресурси&quot;">​</a></h2><p>Для отримання інформації про MoCap, перегляньте:</p><ul><li><a href="./../ros/external_position_estimation.html">Використання Vision або Motion Capture систем для Position Estimation</a>. </li><li><a href="./../tutorials/motion-capture.html">Політ з Motion Capture (VICON, NOKOV, Optitrack)</a>. </li><li><a href="./../advanced_config/tuning_the_ecl_ekf.html#external-vision-system">EKF &gt; Зовнішня візійна система </a></li></ul>',7)]))}const _=a(r,[["render",n]]);export{d as __pageData,_ as default};
