import{_ as t,a as r}from"./chunks/px4_arch_fc_companion.CUIGLCMq.js";import{_ as a,c as o,a8 as s,o as l}from"./chunks/framework.BDnHobkS.js";const _=JSON.parse('{"title":"Архітектура системи PX4","description":"","frontmatter":{},"headers":[],"relativePath":"uk/concept/px4_systems_architecture.md","filePath":"uk/concept/px4_systems_architecture.md"}'),i={name:"uk/concept/px4_systems_architecture.md"};function n(c,e,m,h,p,u){return l(),o("div",null,e[0]||(e[0]=[s('<h1 id="архітектура-системи-px4" tabindex="-1">Архітектура системи PX4 <a class="header-anchor" href="#архітектура-системи-px4" aria-label="Permalink to &quot;Архітектура системи PX4&quot;">​</a></h1><p>Нижче наведено огляд апаратного та програмного забезпечення на PX4 для двох &quot;типових&quot; систем PX4; перша має лише польотний контролер, а друга має польотний контролер і супутній комп&#39;ютер (також відомий як &quot;комп&#39;ютер політних завдань&quot;).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><a href="./../concept/architecture.html">Огляд архітектури PX4</a> надає інформацію про набір польотного програмного забезпечення та проміжного ПЗ. Зовнішні API охоплено в розділах <a href="./../ros/README.html">ROS</a> та <a href="https://mavsdk.mavlink.io/main/en/" target="_blank" rel="noreferrer">MAVSDK</a>.</p></div><h2 id="лише-польотнии-контролер" tabindex="-1">Лише польотний контролер <a class="header-anchor" href="#лише-польотнии-контролер" aria-label="Permalink to &quot;Лише польотний контролер&quot;">​</a></h2><p>На діаграмі нижче показано загальний огляд типової &quot;простої&quot; системи PX4 на основі польотного контролера.</p><p><img src="'+t+'" alt="PX4 architecture - FC only system"></p><p>Апаратне забезпечення складається з</p><ul><li><a href="./../flight_controller/README.html">Політного контролера</a> (запускає набір польотного ПЗ PX4). Часто включає внутрішні ІВП, компас та барометр.</li><li><a href="./../peripherals/esc_motors.html">Електронного регулятора ходу двигунів</a> під&#39;єднаного до <a href="./../peripherals/pwm_escs_and_servo.html">виводів ШІМ</a>, <a href="./../dronecan/escs.html">DroneCAN</a> (DroneCAN дозволяє двонапрямну комунікацію, не в одному напрямку як показано) або іншої шини.</li><li>Sensors (<a href="./../gps_compass/">GPS</a>, <a href="./../gps_compass/">compass</a>, distance sensors, barometers, optical flow, barometers, ADSB transponders, etc.) connected via I2C, SPI, CAN, UART etc.</li><li><a href="./../camera/">Камера</a> або інше корисне навантаження. Камери можуть бути підключені до ШІМ виходів або за допомогою MAVLink.</li><li><a href="./../telemetry/">Telemetry radios</a> for connecting to a ground station computer/software.</li><li><a href="./../getting_started/rc_transmitter_receiver.html">Система радіо керування</a> для ручного керування</li></ul><p>Ліва частина діаграми показує набір програмного забезпечення, що по горизонталі (приблизно) вирівняно згідно з апаратними частинами діаграми.</p><ul><li>На комп&#39;ютері наземної станції зазвичай працює <a href="./../getting_started/px4_basic_concepts.html#qgc">QGroundControl</a> (або інше програмне забезпечення наземної станції). It may also run robotics software like <a href="https://mavsdk.mavlink.io/" target="_blank" rel="noreferrer">MAVSDK</a> or <a href="./../ros/">ROS</a>.</li><li>The PX4 flight stack running on the flight controller includes <a href="./../modules/modules_driver.html">drivers</a>, <a href="./../modules/modules_communication.html">comms modules</a>, <a href="./../modules/modules_controller.html">controllers</a>, <a href="./../modules/modules_controller.html">estimators</a> and other <a href="./../modules/modules_main.html">middleware and system modules</a>.</li></ul><h2 id="польотнии-контролер-та-супутніи-комп-ютер" tabindex="-1">Польотний контролер та супутній комп&#39;ютер <a class="header-anchor" href="#польотнии-контролер-та-супутніи-комп-ютер" aria-label="Permalink to &quot;Польотний контролер та супутній комп&#39;ютер&quot;">​</a></h2><p>На діаграмі показано систему PX4, яка включає як політний контролер, так і супутній комп&#39;ютер (тут згадується як &quot;комп&#39;ютер політного завдання&quot;).</p><p><img src="'+r+'" alt="PX4 architecture - FC + Companion Computer"></p><p>Польотний контролер виконує звичайний набір ПЗ PX4, тоді як супутній комп&#39;ютер забезпечує просунуті функції наприклад <a href="./../computer_vision/obstacle_avoidance.html">уникнення об&#39;єктів</a> та <a href="./../computer_vision/collision_prevention.html">запобігання зіткненням</a>. Дві системи з&#39;єднані за допомогою швидкого послідовного або IP-з&#39;єднання і зазвичай взаємодіють за допомогою протоколу <a href="https://mavlink.io/en/" target="_blank" rel="noreferrer">MAVLink</a>. Зв&#39;язок з наземними станціями та хмарою зазвичай направляється через супутній комп&#39;ютер (наприклад, за допомогою <a href="https://github.com/mavlink-router/mavlink-router" target="_blank" rel="noreferrer">MAVLink Router</a> від Intel).</p><p>Системи PX4 зазвичай виконують ОС Linux на супутньому комп&#39;ютері (тому що проєкт [PX4/PX4-Avoidance](<a href="https://github" target="_blank" rel="noreferrer">https://github</a>. com/PX4/PX4-Avoidance) поставляє засновані на ROS бібліотеки уникнення розроблені для Linux). Linux є набагато кращою платформою для &quot;загальної&quot; розробки програмного забезпечення, ніж NuttX; у Linux багато розробників і вже написано багато корисного програмного забезпечення (наприклад для комп&#39;ютерного бачення, зв&#39;язку, інтеграції з хмарою, апаратні драйвери). Супутні комп&#39;ютери іноді працюють на Android з тієї ж причини.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Діаграма показує підключення до хмарних або наземних станцій через LTE, підхід, який був використаний в ряді систем заснованих на PX4. PX4 не надає програмного забезпечення для LTE та/або хмарної інтеграції (це потребує додаткової розробки).</p></div>',16)]))}const k=a(i,[["render",n]]);export{_ as __pageData,k as default};
