import{_ as t,a as o,b as a,c as r}from"./chunks/qgc_choose_firmware.DPwNFxD5.js";import{_ as i}from"./chunks/bootloader_update.Xd2jMk9_.js";import{_ as l,c as s,a8 as n,o as d}from"./chunks/framework.BDnHobkS.js";const P=JSON.parse('{"title":"Завантаження прошивки","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config/firmware.md","filePath":"uk/config/firmware.md"}'),c={name:"uk/config/firmware.md"};function p(m,e,u,h,f,g){return d(),s("div",null,e[0]||(e[0]=[n('<h1 id="завантаження-прошивки" tabindex="-1">Завантаження прошивки <a class="header-anchor" href="#завантаження-прошивки" aria-label="Permalink to &quot;Завантаження прошивки&quot;">​</a></h1><p><em>QGroundControl</em> <strong>desktop</strong> versions can be used to install PX4 firmware onto <a href="./../getting_started/flight_controller_selection.html">Pixhawk-series</a> flight-controller boards.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p><strong>Before you start installing Firmware</strong> all USB connections to the vehicle must be <em>disconnected</em> (both direct or through a telemetry radio). The vehicle must <em>not be</em> powered by a battery.</p></div><h2 id="встановити-стабільну-px4" tabindex="-1">Встановити стабільну PX4 <a class="header-anchor" href="#встановити-стабільну-px4" aria-label="Permalink to &quot;Встановити стабільну PX4&quot;">​</a></h2><p>Generally you should use the most recent <em>released</em> version of PX4, in order to benefit from bug fixes and get the latest and greatest features.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This is the version that is installed by default.</p></div><p>Щоб встановити PX4:</p><ol><li><p>Start <em>QGroundControl</em> and connect the vehicle.</p></li><li><p>Select <strong>&quot;Q&quot; icon &gt; Vehicle Setup &gt; Firmware</strong> (sidebar) to open <em>Firmware Setup</em>.</p><p><img src="'+t+'" alt="Firmware disconnected"></p></li><li><p>Підключіть польотний контролер безпосередньо до вашого комп&#39;ютера через USB.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Connect directly to a powered USB port on your machine (do not connect through a USB hub).</p></div></li></ol><p>:::</p><ol start="4"><li><p>Select the <strong>PX4 Pro Stable Release vX.x.x</strong> option to install the latest stable version of PX4 <em>for your flight controller</em> (autodetected).</p><p><img src="'+o+'" alt="Install PX4 default"></p></li><li><p>Click the <strong>OK</strong> button to start the update.</p><p>Прошивка потім пройде кілька етапів оновлення (завантаження нової прошивки, видалення старої прошивки тощо). Кожен крок виводиться на екран та загальний прогрес відображається на панелі прогресу.</p><p><img src="'+a+'" alt="Firmware upgrade complete"></p><p>Після завершення завантаження прошивки пристрій/транспортний засіб перезавантажиться та встановить з&#39;єднання знову.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>If <em>QGroundControl</em> installs the FMUv2 target (see console during installation) and you have a newer board, you may need to <a href="#bootloader">update the bootloader</a> in order to access all the memory on your flight controller.</p></div></li></ol><p>:::</p><p>Next you will need to specify the <a href="./../config/airframe.html">vehicle airframe</a> (and then sensors, radio, etc.)</p><p><a id="custom"></a></p><h2 id="встановлення-px4-main-beta-або-custom-firmware" tabindex="-1">Встановлення PX4 Main, Beta або Custom Firmware <a class="header-anchor" href="#встановлення-px4-main-beta-або-custom-firmware" aria-label="Permalink to &quot;Встановлення PX4 Main, Beta або Custom Firmware&quot;">​</a></h2><p>Щоб встановити іншу версію PX4:</p><ol><li>Connect the vehicle as above, and select <strong>PX4 Pro Stable Release vX.x.x</strong>. <img src="'+r+'" alt="Install PX4 version"></li><li>Check <strong>Advanced settings</strong> and select the version from the dropdown list: <ul><li><strong>Standard Version (stable):</strong> The default version (i.e. no need to use advanced settings to install this!)</li><li><strong>Beta Testing (beta):</strong> A beta/candidate release. Лише доступно, коли готується новий реліз.</li><li><strong>Developer Build (master):</strong> The latest build of PX4/PX4-Autopilot <em>main</em> branch.</li><li><strong>Custom Firmware file...:</strong> A custom firmware file (e.g. <a href="./../dev_setup/building_px4.html">that you have built locally</a>). Якщо ви виберете це, вам доведеться вибрати власну прошивку з файлової системи на наступному кроці.</li></ul></li></ol><p>Оновлення прошивки потім продовжується, як і раніше.</p><p><a id="bootloader"></a></p><h2 id="оновлення-завантажувача" tabindex="-1">Оновлення завантажувача <a class="header-anchor" href="#оновлення-завантажувача" aria-label="Permalink to &quot;Оновлення завантажувача&quot;">​</a></h2><p>Апаратне забезпечення Pixhawk зазвичай має відповідну версію завантажувача.</p><p>Якщо оновлення може знадобитися - це новіші дошки Pixhawk, які встановлюють прошивку FMUv2. Якщо <em>QGroundControl</em> встановлює ціль FMUv2 (див. консоль під час встановлення), і у вас є новіша плата, вам може знадобитися оновити завантажувальник, щоб мати доступ до всієї пам&#39;яті на вашому контролері польоту.</p><p><img src="'+i+'" alt="FMUv2 update"></p><p>You can update it by following the instructions in <a href="./../advanced_config/bootloader_update.html#fmuv2-bootloader-update">Bootloader update &gt; FMUv2 Bootloader Update</a>.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/firmware.html" target="_blank" rel="noreferrer">QGroundControl User Guide &gt; Firmware</a>.</li><li><a href="https://youtu.be/91VGmdSlbo4" target="_blank" rel="noreferrer">PX4 Setup Video</a> (Youtube)</li></ul>',25)]))}const w=l(c,[["render",p]]);export{P as __pageData,w as default};
