import{_ as o}from"./chunks/menu_setup.dwyqbE0U.js";import{_ as i,c as t,a8 as a,j as r,o as l}from"./chunks/framework.BDnHobkS.js";const g=JSON.parse('{"title":"Стандартна конфігурація","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config/index.md","filePath":"uk/config/index.md"}'),n={name:"uk/config/index.md"};function h(u,e,c,s,f,d){return l(),t("div",null,e[0]||(e[0]=[a('<h1 id="стандартна-конфігурація" tabindex="-1">Стандартна конфігурація <a class="header-anchor" href="#стандартна-конфігурація" aria-label="Permalink to &quot;Стандартна конфігурація&quot;">​</a></h1><p>У цьому розділі описано загальну конфігурацію програмного забезпечення та калібрування, необхідні для більшості пристроїв PX4.</p><p>You must first <a href="#firmware-vehicle-selection">load firmware and select your vehicle frame/type</a>. Most other steps can be done out of order, except for <a href="#tuning">tuning</a>, which must be done last.</p><h2 id="передумови" tabindex="-1">Передумови <a class="header-anchor" href="#передумови" aria-label="Permalink to &quot;Передумови&quot;">​</a></h2><p>Before starting you should <a href="http://qgroundcontrol.com/downloads/" target="_blank" rel="noreferrer">Download QGroundControl</a> and install it on your <strong>desktop</strong> computer. Then open the QGC application menu (&quot;Q&quot; icon in the top-left corner) and choose <strong>Vehicle Setup</strong> in the <em>Select Tool</em> popup:</p><p><img src="'+o+'" alt="QGC Main Menu Popup: highlighting Vehicle Setup"></p><h2 id="етапи-конфігураціі" tabindex="-1">Етапи конфігурації <a class="header-anchor" href="#етапи-конфігураціі" aria-label="Permalink to &quot;Етапи конфігурації&quot;">​</a></h2><h3 id="вибір-прошивки-апарату" tabindex="-1">Вибір прошивки/апарату <a class="header-anchor" href="#вибір-прошивки-апарату" aria-label="Permalink to &quot;Вибір прошивки/апарату&quot;">​</a></h3><ul><li><a href="./../config/firmware.html">Loading Firmware</a></li><li><a href="./../config/airframe.html">Vehicle (Frame) Selection</a></li></ul><h3 id="налаштування-двигуна-приводу" tabindex="-1">Налаштування двигуна/приводу <a class="header-anchor" href="#налаштування-двигуна-приводу" aria-label="Permalink to &quot;Налаштування двигуна/приводу&quot;">​</a></h3><ul><li><a href="./../advanced_config/esc_calibration.html">ESC Calibration</a></li><li><a href="./../config/actuators.html">Actuator Configuration and Testing</a></li></ul><h3 id="калібрування-сенсорів" tabindex="-1">Калібрування сенсорів <a class="header-anchor" href="#калібрування-сенсорів" aria-label="Permalink to &quot;Калібрування сенсорів&quot;">​</a></h3><ul><li><a href="./../config/flight_controller_orientation.html">Sensor Orientation</a></li><li><a href="./../config/compass.html">Magnetometer (Compass)</a></li><li><a href="./../config/gyroscope.html">Гіроскоп</a></li><li><a href="./../config/accelerometer.html">Акселерометр</a></li><li><a href="./../config/level_horizon_calibration.html">Level Horizon</a></li><li><a href="./../config/airspeed.html">Airspeed</a> (Fixed-wing/VTOL only)</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Setup for these and other sensors is located in <a href="./../sensor/">Sensor Hardware &amp; Setup</a>.</p></div><h3 id="налаштування-керування-вручному-режимі" tabindex="-1">Налаштування керування вручному режимі <a class="header-anchor" href="#налаштування-керування-вручному-режимі" aria-label="Permalink to &quot;Налаштування керування вручному режимі&quot;">​</a></h3><p>Радіоуправління:</p><ul><li><a href="./../config/radio.html">Radio Controller (RC) Setup</a></li><li><a href="./../config/flight_mode.html">Flight Mode Configuration</a></li></ul><p>Джойстик/Ґеймпад:</p><ul><li><a href="./../config/joystick.html">Joystick Setup</a></li></ul><h3 id="конфігурація-безпеки" tabindex="-1">Конфігурація безпеки <a class="header-anchor" href="#конфігурація-безпеки" aria-label="Permalink to &quot;Конфігурація безпеки&quot;">​</a></h3><ul><li><a href="./../config/battery.html">Battery Estimation Tuning</a> (requires <a href="./../power_module/">Power Module</a>)</li><li><a href="./../config/safety.html">Safety Configuration (Failsafes)</a></li></ul><h3 id="вдосконалення" tabindex="-1">Вдосконалення <a class="header-anchor" href="#вдосконалення" aria-label="Permalink to &quot;Вдосконалення&quot;">​</a></h3><p>Auto-tuning is supported, and recommended, on the following frames:</p><ul><li><a href="./../config/autotune_mc.html">Autotune (Multicopter)</a></li><li><a href="./../config/autotune_fw.html">Autotune (Fixed Wing)</a></li><li><a href="./../config/autotune_vtol.html">Autotune (VTOL)</a></li></ul><h2 id="відеоінструкція" tabindex="-1">Відеоінструкція <a class="header-anchor" href="#відеоінструкція" aria-label="Permalink to &quot;Відеоінструкція&quot;">​</a></h2><p>The video below shows most of the calibration process (it uses an older version of <em>QGroundControl</em>, but most of the process is unchanged).</p>',26),r("lite-youtube",{videoid:"91VGmdSlbo4",title:"PX4 Autopilot Setup Tutorial Preview"},null,-1),a('<h2 id="підтримка" tabindex="-1">Підтримка <a class="header-anchor" href="#підтримка" aria-label="Permalink to &quot;Підтримка&quot;">​</a></h2><p>If you need help with the configuration you can ask for help on the <a href="https://discuss.px4.io//c/qgroundcontrol/qgroundcontrol-usage" target="_blank" rel="noreferrer">QGroundControl Support forum</a>.</p><h2 id="дивіться-також" tabindex="-1">Дивіться також <a class="header-anchor" href="#дивіться-також" aria-label="Permalink to &quot;Дивіться також&quot;">​</a></h2><ul><li><p><a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/setup_view.html" target="_blank" rel="noreferrer">QGroundControl &gt; Setup</a></p></li><li><p><a href="./../peripherals/README.html">Периферія контролера польоту</a> - налаштування конкретних датчиків, опціональних датчиків, приводів тощо.</p></li><li><p><a href="./../advanced_config/">Advanced Configuration</a> - Factory/OEM calibration, configuring advanced features, less-common configuration.</p></li><li><p>Конфігурація/налаштування, що залежать від апарату:</p><ul><li><a href="./../config_mc/">Конфігурація/налаштування мультикоптера</a></li><li><a href="./../config_heli/">Конфігурація/налаштування гелікоптера</a></li><li><a href="./../config_fw/">Конфігурація/налаштування літака (з нерухомим крилом)</a></li><li><a href="./../config_vtol/">Конфігурація/налаштування VTOL</a></li></ul></li></ul>',4)]))}const b=i(n,[["render",h]]);export{g as __pageData,b as default};
