import{_ as a}from"./chunks/jerk_limited_trajectory_1d.9dHOHo1K.js";import{_ as r,c as i,a8 as _,o as t}from"./chunks/framework.BDnHobkS.js";const M=JSON.parse('{"title":"Траєкторія Jerk-limited типу для Мультикоперів","description":"","frontmatter":{},"headers":[],"relativePath":"uk/config_mc/mc_jerk_limited_type_trajectory.md","filePath":"uk/config_mc/mc_jerk_limited_type_trajectory.md"}'),o={name:"uk/config_mc/mc_jerk_limited_type_trajectory.md"};function c(d,e,l,n,m,f){return t(),i("div",null,e[0]||(e[0]=[_('<h1 id="траєкторія-jerk-limited-типу-для-мультикоперів" tabindex="-1">Траєкторія Jerk-limited типу для Мультикоперів <a class="header-anchor" href="#траєкторія-jerk-limited-типу-для-мультикоперів" aria-label="Permalink to &quot;Траєкторія Jerk-limited типу для Мультикоперів&quot;">​</a></h1><p>Тип траєкторії з обмеженим рухом джерка забезпечує плавний рух у відповідь на введення палиці користувача або зміни місії (наприклад: для зйомки, картографування, вантажу). Він генерує симетричні плавні S-криві, де обмеження різкості та прискорення завжди гарантовані.</p><p>This trajectory type is always enabled in <a href="./../flight_modes_mc/mission.html">Mission mode</a>. To enable it in <a href="./../flight_modes_mc/position.html">Position mode</a> set the parameter <a href="./../advanced_config/parameter_reference.html#MPC_POS_MODE">MPC_POS_MODE</a> to <code>Smoothed velocity</code>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The jerk-limited type is not used <em>by default</em> in position mode. Це може бути не підходить для транспортних засобів / випадків використання, які вимагають швидкої відповіді - наприклад, гонщицькі квадрокоптери.</p></div><h2 id="генератор-траєкторіі" tabindex="-1">Генератор траєкторії <a class="header-anchor" href="#генератор-траєкторіі" aria-label="Permalink to &quot;Генератор траєкторії&quot;">​</a></h2><p>Графік нижче показує типовий профіль обмеження ривка з наступними обмеженнями:</p><ul><li><code>jMax</code>: maximum jerk</li><li><code>a0</code>: initial acceleration</li><li><code>aMax</code>: maximum acceleration</li><li><code>a3</code>: final acceleration (always 0)</li><li><code>v0</code>: initial velocity</li><li><code>vRef</code>: desired velocity</li></ul><p>The constraints <code>jMax</code>, <code>aMax</code> are configurable by the user via parameters and can be different in manual position control and auto mode.</p><p>Отриманий профіль швидкості часто називають &quot;S-кривою&quot;.</p><p><img src="'+a+'" alt="Jerk-limited trajectory"></p><h2 id="ручнии-режим" tabindex="-1">Ручний режим <a class="header-anchor" href="#ручнии-режим" aria-label="Permalink to &quot;Ручний режим&quot;">​</a></h2><p>In manual position mode, the sticks are mapped to velocity where a full XY-stick deflection corresponds to <a href="./../advanced_config/parameter_reference.html#MPC_VEL_MANUAL">MPC_VEL_MANUAL</a> and a full Z-stick deflection corresponds to <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_MAX_UP">MPC_Z_VEL_MAX_UP</a> (upward motion) or <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_MAX_DN">MPC_Z_VEL_MAX_DN</a> (downward motion).</p><h3 id="обмеження" tabindex="-1">Обмеження <a class="header-anchor" href="#обмеження" aria-label="Permalink to &quot;Обмеження&quot;">​</a></h3><p>XY-plane:</p><ul><li><code>jMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_JERK_MAX">MPC_JERK_MAX</a></li><li><code>aMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_ACC_HOR_MAX">MPC_ACC_HOR_MAX</a></li></ul><p>Z-axis:</p><ul><li><code>jMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_JERK_MAX">MPC_JERK_MAX</a></li><li><code>aMax</code> (upward motion): <a href="./../advanced_config/parameter_reference.html#MPC_ACC_UP_MAX">MPC_ACC_UP_MAX</a></li><li><code>aMax</code> (downward motion): <a href="./../advanced_config/parameter_reference.html#MPC_ACC_DOWN_MAX">MPC_ACC_DOWN_MAX</a></li></ul><h2 id="автоматичнии-режим" tabindex="-1">Автоматичний режим <a class="header-anchor" href="#автоматичнии-режим" aria-label="Permalink to &quot;Автоматичний режим&quot;">​</a></h2><p>In auto mode, the desired velocity is <a href="./../advanced_config/parameter_reference.html#MPC_XY_CRUISE">MPC_XY_CRUISE</a> but this value is automatically adjusted depending on the distance to the next waypoint, the maximum possible velocity in the waypoint and the maximum desired acceleration and jerk. The vertical speed is defined by <a href="./../advanced_config/parameter_reference.html#MPC_Z_V_AUTO_UP">MPC_Z_V_AUTO_UP</a> (upward motion) and <a href="./../advanced_config/parameter_reference.html#MPC_Z_V_AUTO_DN">MPC_Z_V_AUTO_DN</a> (downward motion).</p><h3 id="обмеження-1" tabindex="-1">Обмеження <a class="header-anchor" href="#обмеження-1" aria-label="Permalink to &quot;Обмеження&quot;">​</a></h3><p>XY-plane:</p><ul><li><code>jMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_JERK_AUTO">MPC_JERK_AUTO</a></li><li><code>aMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_ACC_HOR">MPC_ACC_HOR</a></li></ul><p>Z-axis:</p><ul><li><code>jMax</code>: <a href="./../advanced_config/parameter_reference.html#MPC_JERK_AUTO">MPC_JERK_AUTO</a></li><li><code>aMax</code> (upward motion): <a href="./../advanced_config/parameter_reference.html#MPC_ACC_UP_MAX">MPC_ACC_UP_MAX</a></li><li><code>aMax</code> (downward motion): <a href="./../advanced_config/parameter_reference.html#MPC_ACC_DOWN_MAX">MPC_ACC_DOWN_MAX</a></li></ul><p>Відстань до отримання швидкості, коли близько до точки маршруту:</p><ul><li><a href="./../advanced_config/parameter_reference.html#MPC_XY_TRAJ_P">MPC_XY_TRAJ_P</a></li></ul><h3 id="зв-язані-параметри" tabindex="-1">Зв&#39;язані параметри <a class="header-anchor" href="#зв-язані-параметри" aria-label="Permalink to &quot;Зв&#39;язані параметри&quot;">​</a></h3><ul><li><a href="./../advanced_config/parameter_reference.html#MPC_XY_VEL_MAX">MPC_XY_VEL_MAX</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_MAX_UP">MPC_Z_VEL_MAX_UP</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_MAX_DN">MPC_Z_VEL_MAX_DN</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_TKO_SPEED">MPC_TKO_SPEED</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_LAND_SPEED">MPC_LAND_SPEED</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_LAND_ALT1">MPC_LAND_ALT1</a></li><li><a href="./../advanced_config/parameter_reference.html#MPC_LAND_ALT2">MPC_LAND_ALT2</a></li></ul>',28)]))}const s=r(o,[["render",c]]);export{M as __pageData,s as default};
