import{_ as e}from"./chunks/avionics_anon_altimeter_uavcan_interface.DVGtiwH4.js";import{_ as t,c as r,a8 as n,o}from"./chunks/framework.BDnHobkS.js";const p=JSON.parse('{"title":"Інтерфейс лазерного альтиметра Avionics Anonymous DroneCan","description":"","frontmatter":{},"headers":[],"relativePath":"uk/dronecan/avanon_laser_interface.md","filePath":"uk/dronecan/avanon_laser_interface.md"}'),i={name:"uk/dronecan/avanon_laser_interface.md"};function d(l,a,s,c,h,m){return o(),r("div",null,a[0]||(a[0]=[n('<h1 id="інтерфеис-лазерного-альтиметра-avionics-anonymous-dronecan" tabindex="-1">Інтерфейс лазерного альтиметра Avionics Anonymous DroneCan <a class="header-anchor" href="#інтерфеис-лазерного-альтиметра-avionics-anonymous-dronecan" aria-label="Permalink to &quot;Інтерфейс лазерного альтиметра Avionics Anonymous DroneCan&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">INFO</p><p>In 2022, UAVCAN (v0) was forked and is maintained as <code>DroneCAN</code>. Хоча цей продукт все ще згадує &quot;UAVCAN&quot;, він повністю сумісний з підтримкою DroneCAN в PX4.</p></div><p>The <a href="https://www.tindie.com/products/avionicsanonymous/uavcan-laser-altimeter-interface/" target="_blank" rel="noreferrer">Avionics Anonymous Laser Altimeter Interface</a> allows a <a href="#supported_rangefinders">number of common rangefinders</a> to be connected via the CAN bus (this is a more robust interface than I2C).</p><p><img src="'+e+'" alt="Avionics Anonymous Laser Altimeter DroneCAN Interface"></p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://www.tindie.com/products/avionicsanonymous/uavcan-laser-altimeter-interface/" target="_blank" rel="noreferrer">AvAnon Laser Interface</a></li></ul><p><a id="supported_rangefinders"></a></p><h2 id="підтримувані-дальномери" tabindex="-1">Підтримувані дальномери <a class="header-anchor" href="#підтримувані-дальномери" aria-label="Permalink to &quot;Підтримувані дальномери&quot;">​</a></h2><p>Повний список підтримуваних далекомірів можна знайти за посиланням вище.</p><p>Наступні далекоміри підтримуються на момент написання:</p><ul><li>Lightware SF30/D</li><li>Lightware SF10/a</li><li>Lightware SF10/b</li><li>Lightware SF10/c</li><li>Lightware SF11/c</li><li>Lightware SF/LW20/b</li><li>Lightware SF/LW20/c</li></ul><h2 id="налаштування-програмного-забезпечення" tabindex="-1">Налаштування програмного забезпечення <a class="header-anchor" href="#налаштування-програмного-забезпечення" aria-label="Permalink to &quot;Налаштування програмного забезпечення&quot;">​</a></h2><h3 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h3><p>Далекомір (лазер) підключений до плати інтерфейсу AvAnon, яка підключена до одного з CAN-портів вашого автопілота. Проводка відбувається згідно з виведенням контактів вище, або необхідні кабелі можна придбати, щоб підключити їх безпосередньо до вашої системи. These are available at the links <a href="https://www.tindie.com/products/avionicsanonymous/uavcan-laser-altimeter-interface/" target="_blank" rel="noreferrer">here</a>.</p><p>Інтерфейсна плата забезпечує фільтрований вихід живлення для лазера, але не забезпечує власного регулювання. Отже, лазер повинен бути сумісним з напругою, яка подається на плату.</p><h3 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h3><h3 id="конектор-шини-can" tabindex="-1">Конектор шини CAN <a class="header-anchor" href="#конектор-шини-can" aria-label="Permalink to &quot;Конектор шини CAN&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Pin</th><th>Назва</th><th>Опис</th></tr></thead><tbody><tr><td>1</td><td>POWER_IN</td><td>Джерело живлення. Підтримується 4.0-5.5В, але також повинно бути сумісним з підключеним лазером.</td></tr><tr><td>2</td><td>TX/SCL</td><td>TX для послідовного режиму, Clock для режиму I2C.</td></tr><tr><td>3</td><td>RX/SDA</td><td>RX для послідовного режиму, Data для режиму I2C.</td></tr><tr><td>4</td><td>GND</td><td>Сигнальна / заземлювальна земля.</td></tr></tbody></table><h3 id="з-єднувач-лазеру" tabindex="-1">З&#39;єднувач лазеру <a class="header-anchor" href="#з-єднувач-лазеру" aria-label="Permalink to &quot;З&#39;єднувач лазеру&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Pin</th><th>Назва</th><th>Опис</th></tr></thead><tbody><tr><td>1</td><td>POWER_OUT</td><td>Фільтрована потужність при напрузі живлення.</td></tr><tr><td>2</td><td>CAN+</td><td>TX для послідовного режиму, Clock для режиму I2C.</td></tr><tr><td>3</td><td>RX/SDA</td><td>RX для послідовного режиму, Data для режиму I2C.</td></tr><tr><td>4</td><td>GND</td><td>Сигнальна / заземлювальна земля.</td></tr></tbody></table><h2 id="конфігурація-px4" tabindex="-1">Конфігурація PX4 <a class="header-anchor" href="#конфігурація-px4" aria-label="Permalink to &quot;Конфігурація PX4&quot;">​</a></h2><p>To enable the laser altimeter you will need to <a href="./../advanced_config/parameters.html">set the following parameters</a> (in QGroundControl):</p><ul><li>Enable DroneCAN by setting <a href="./../advanced_config/parameter_reference.html#UAVCAN_ENABLE">UAVCAN_ENABLE</a> non zero.</li><li>Enable DroneCAN rangefinder subscription by setting <a href="./../advanced_config/parameter_reference.html#UAVCAN_SUB_RNG">UAVCAN_SUB_RNG</a></li><li>Set the minimum and maximum range of the rangefinder using <a href="./../advanced_config/parameter_reference.html#UAVCAN_RNG_MIN">UAVCAN_RNG_MIN</a> and <a href="./../advanced_config/parameter_reference.html#UAVCAN_RNG_MAX">UAVCAN_RNG_MAX</a>.</li></ul>',23)]))}const A=t(i,[["render",d]]);export{p as __pageData,A as default};
