import{_ as r}from"./chunks/ark_fpv.DatM7d9B.js";import{_ as t,c as e,a8 as i,o as a}from"./chunks/framework.BDnHobkS.js";const f=JSON.parse('{"title":"ARK FPV Flight Controller","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/ark_fpv.md","filePath":"uk/flight_controller/ark_fpv.md"}'),o={name:"uk/flight_controller/ark_fpv.md"};function n(s,l,u,c,h,p){return a(),e("div",null,l[0]||(l[0]=[i('<h1 id="ark-fpv-flight-controller" tabindex="-1">ARK FPV Flight Controller <a class="header-anchor" href="#ark-fpv-flight-controller" aria-label="Permalink to &quot;ARK FPV Flight Controller&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://arkelectron.com/contact-us/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The USA-built ARK FPV flight controller is based on the <a href="https://arkelectron.com/product/arkv6x/" target="_blank" rel="noreferrer">ARKV6X</a> in a 30.5mm standard mounting pattern, supporting a 3-12s battery input with a regulated 12V 2A output for video transmitters and payloads.</p><p><img src="'+r+'" alt="ARK FPV Main Photo"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Order from <a href="https://arkelectron.com/product/arkv6x/" target="_blank" rel="noreferrer">Ark Electronics</a> (US)</p><h2 id="документація" tabindex="-1">Документація <a class="header-anchor" href="#документація" aria-label="Permalink to &quot;Документація&quot;">​</a></h2><p>See the documentation <a href="https://arkelectron.gitbook.io/ark-documentation/flight-controllers/ark-fpv" target="_blank" rel="noreferrer">Ark Electronics GitBook</a></p><h2 id="датчики" tabindex="-1">Датчики <a class="header-anchor" href="#датчики" aria-label="Permalink to &quot;Датчики&quot;">​</a></h2><ul><li><a href="https://invensense.tdk.com/products/motion-tracking/6-axis/iim-42653/" target="_blank" rel="noreferrer">Invensense IIM-42653 Industrial IMU</a></li><li><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp390/" target="_blank" rel="noreferrer">Bosch BMP390 Barometer</a></li><li><a href="https://www.st.com/en/magnetic-sensors/iis2mdc.html" target="_blank" rel="noreferrer">ST IIS2MDC Magnetometer</a></li></ul><h2 id="мікропроцесор" tabindex="-1">Мікропроцесор <a class="header-anchor" href="#мікропроцесор" aria-label="Permalink to &quot;Мікропроцесор&quot;">​</a></h2><ul><li><a href="https://www.st.com/en/microcontrollers-microprocessors/stm32h743ii.html" target="_blank" rel="noreferrer">STM32H743IIK6 MCU</a><ul><li>480 MHz</li><li>2 MB Flash</li><li>1 МБ RAM</li></ul></li></ul><h2 id="з-єднання" tabindex="-1">З’єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З’єднання&quot;">​</a></h2><ul><li>USB C <ul><li>VBUS In, USB</li></ul></li><li>PWM <ul><li>VBAT In, Analog Current Input, Telem RX, 4x PWM</li><li>JST-GH 8 Pin</li></ul></li><li>PWM EXTRA <ul><li>5x PWM</li><li>JST-SH 6 Pin</li></ul></li><li>RC INPUT <ul><li>5V Out, UART</li><li>JST-GH 4 Pin</li></ul></li><li>POWER AUX <ul><li>12V Out, VBAT In/Out</li><li>JST-GH 3 Pin</li></ul></li><li>TELEM <ul><li>5V Out, UART with flow control</li><li>JST-GH 6 Pin</li></ul></li><li>GPS <ul><li>5V Out, UART, I2C</li><li>JST-GH 6 Pin</li></ul></li><li>CAN <ul><li>5V Out, CAN</li><li>JST-GH 4 Pin</li></ul></li><li>VTX <ul><li>12V Out, UART TX/RX, UART RX</li><li>JST-GH 6 Pin</li></ul></li><li>SPI (OSD or External IMU) <ul><li>5V Out, SPI</li><li>JST-SH 8 Pin</li></ul></li><li>DEBUG <ul><li>3.3V Out, UART, SWD</li><li>JST-SH 6 Pin</li></ul></li></ul><h2 id="вимоги-до-живлення" tabindex="-1">Вимоги до живлення <a class="header-anchor" href="#вимоги-до-живлення" aria-label="Permalink to &quot;Вимоги до живлення&quot;">​</a></h2><ul><li>5.5V - 54V</li><li>500 mA (300 mA main system, 200 mA heater)</li></ul><h2 id="додаткова-інформація" tabindex="-1">Додаткова інформація <a class="header-anchor" href="#додаткова-інформація" aria-label="Permalink to &quot;Додаткова інформація&quot;">​</a></h2><ul><li>Weight: 7.5 g g with MicroSD card</li><li>Dimensions: 3.6 x 3.6 x 0.8 cm</li><li>USA Built - NDAA compliant</li><li>Heater: 1W for warming sensors in extreme cold</li><li>LED індикатори</li><li>Слот MicroSD</li></ul><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p>See the <a href="https://arkelectron.gitbook.io/ark-documentation/flight-controllers/ark-fpv/pinout" target="_blank" rel="noreferrer">DS-10 Pixhawk Autopilot Bus Standard</a></p>',21)]))}const k=t(o,[["render",n]]);export{f as __pageData,k as default};
