import{_ as a}from"./chunks/pixhawk_v6x.CLqvvgiD.js";import{_ as e,a as r}from"./chunks/core.vaKwc9xW.js";import{_ as i}from"./chunks/pixhawk_v6x_pinouts.BSlXeHIR.js";import{_ as l,c as o,a8 as d,o as n}from"./chunks/framework.BDnHobkS.js";const x=JSON.parse('{"title":"CUAV Pixhawk V6X","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/cuav_pixhawk_v6x.md","filePath":"uk/flight_controller/cuav_pixhawk_v6x.md"}'),s={name:"uk/flight_controller/cuav_pixhawk_v6x.md"};function h(p,t,u,c,m,b){return n(),o("div",null,t[0]||(t[0]=[d('<h1 id="cuav-pixhawk-v6x" tabindex="-1">CUAV Pixhawk V6X <a class="header-anchor" href="#cuav-pixhawk-v6x" aria-label="Permalink to &quot;CUAV Pixhawk V6X&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://store.cuav.net/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><em>Pixhawk V6X</em><sup>®</sup> is the latest update to the successful family of Pixhawk® flight controllers designed and made in collaboration with CUAV<sup>®</sup> and the PX4 team.</p><p>It is based on the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk​​® Autopilot FMUv6X Standard</a>, <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Autopilot Bus Standard</a>, and <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Connector Standard</a>.</p><p><img src="'+a+'" alt="Pixhawk V6X"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><p>Pixhawk<sup>®</sup> V6X принесе вам найвищий рівень продуктивності, стабільності та надійності у всіх аспектах.</p><ul><li>Процесор Arm® Cortex®-M7 (STM32H753) з операційною точкою (FPU), високошвидкісні операції на частоті 480 МГц та 2 МБ флеш-пам&#39;яті. Розробники можуть бути більш продуктивними та ефективними, що дозволяє використовувати складніші алгоритми та моделі.</li><li>Високопродуктивний бортовий, низькозвуковий ІНС та автомобільний магнітний компас на основі відкритого стандарту FMUv6X. Мета полягає в досягненні кращої стабільності та антиінтерференційної здатності.</li><li>Triple redundant IMU &amp; double redundant barometer on separate buses. Коли автопілот PX4 виявляє відмову датчика, система безперервно переключається на інший, щоб забезпечити надійність керування польотом.</li><li>Кожен незалежний LDO живить кожен набір сенсорів з незалежним керуванням живленням. Система віброізоляції для фільтрації високочастотної вібрації та зменшення шуму для забезпечення точних показань, що дозволяє транспортним засобам досягати кращих загальних характеристик польоту.</li><li>Зовнішня шина датчика (SPI5) має дві лінії вибору мікросхем та сигнали готовності даних для додаткових датчиків та навантаження з інтерфейсом SPI.</li><li>Інтегрований мікросхемний Ethernet PHY для високошвидкісного зв&#39;язку по Ethernet з пристроями на борту, такими як комп&#39;ютери місій.</li><li>Нова система ізоляції вібрацій, призначена для фільтрації високочастотних вібрацій та зменшення шуму для забезпечення точних вимірювань.</li><li>IMUs are temperature-controlled by onboard heating resistors, allowing optimum working temperature of IMUs&amp;#x20</li><li>Modular flight controller: separated IMU, FMU, and Base system connected by a 100-pin &amp; a 50-pin Pixhawk®​ Autopilot Bus connector.</li></ul><p>Pixhawk® V6X ідеально підходить для корпоративних дослідницьких лабораторій, академічних досліджень та комерційних застосувань.</p><h3 id="processors-sensors" tabindex="-1">Processors &amp; Sensors <a class="header-anchor" href="#processors-sensors" aria-label="Permalink to &quot;Processors &amp; Sensors&quot;">​</a></h3><ul><li>Процесор FMU: STM32H753 <ul><li>32 Bit Arm® Cortex®-M7, 480MHz, 2MB flash memory, 1MB RAM</li></ul></li><li>IO Processor: STM32F103 <ul><li>32 Bit Arm® Cortex®-M3, 72MHz, 20KB SRAM</li></ul></li><li>Сенсори на платі <ul><li>Акселератор/гіроскоп: BMI088</li><li>Accel/Gyro: ICM-42688-P</li><li>Accel/Gyro: ICM-20649</li><li>Mag: RM3100</li><li>Барометр: 2x ICP-20100</li></ul></li></ul><h3 id="електричні-дані" tabindex="-1">Електричні дані <a class="header-anchor" href="#електричні-дані" aria-label="Permalink to &quot;Електричні дані&quot;">​</a></h3><ul><li>Номінальна напруга: <ul><li>Максимальна вхідна напруга: 5,7 В</li><li>Вхід USB Power: 4.75~5.25V</li><li>Вхід на серворейку: 0~9.9В</li></ul></li><li>Номінальний струм: <ul><li>Комбінований обмежувач вихідного струму TELEM1 і GPS2: 1,5 А</li><li>Комбінований обмежувач вихідного струму всіх інших портів: 1.5A</li></ul></li></ul><h3 id="інтерфеиси" tabindex="-1">Інтерфейси <a class="header-anchor" href="#інтерфеиси" aria-label="Permalink to &quot;Інтерфейси&quot;">​</a></h3><ul><li>16 PWM виводів сервоприводів</li><li>1 Виділений R/C вхід для Spektrum / DSM та S.Bus з аналоговим / PWM RSSI входом</li><li>3 TELEM-порти (з повним контролем потоку)</li><li>1 UART4(Seial та I2C)</li><li>2 порти GPS <ul><li>1 повноцінний порт GPS плюс порт перемикача безпеки (GPS1)</li><li>1 базовий GPS-порт (з I2C, GPS2)</li></ul></li><li>2 USB порти <ul><li>1 TYPE-C</li><li>JST GH1.25</li></ul></li><li>1 порт Ethernet <ul><li>Transformerless Applications</li><li>100Mbps</li></ul></li><li>1 шина SPI <ul><li>2 лінії вибору чіпу</li><li>2 лінії готових даних</li><li>1 SPI SYNC лінія</li><li>1 лінія SPI reset</li></ul></li><li>2 CAN шини для CAN периферії <ul><li>CAN шина має individual silent controls або ESC RX-MUX control</li></ul></li><li>4 порти вводу живлення <ul><li>2 входи живлення Dronecan/UAVCAN</li><li>2 входи живлення SMBUS/I2C</li></ul></li><li>1 AD &amp; IO port <ul><li>2 додаткових аналогових входи (3,3 і 6,6 В)</li><li>1 PWM/Capture вхід</li></ul></li><li>2 виділених для відлагодження <ul><li>FMU Debug</li><li>IO debug</li></ul></li></ul><h3 id="механічні-характеристики" tabindex="-1">Механічні характеристики <a class="header-anchor" href="#механічні-характеристики" aria-label="Permalink to &quot;Механічні характеристики&quot;">​</a></h3><ul><li><p>Вага</p><ul><li>Модуль політного контролера: 99г</li><li>Основний модуль: 43г</li><li>Baseboard: 56г</li></ul></li><li><p>Operating &amp; storage temperature: -20 ~ 85°c</p></li><li><p>Розмір</p><ul><li><p>Політний контролер</p><p><img src="'+e+'" alt="Pixhawk V6X"></p></li><li><p>Основний модуль</p><p><img src="'+r+'" alt="Pixhawk V6X"></p></li></ul></li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Order from <a href="https://store.cuav.net/" target="_blank" rel="noreferrer">CUAV</a>.</p><h2 id="зборка-інсталяція" tabindex="-1">Зборка/інсталяція <a class="header-anchor" href="#зборка-інсталяція" aria-label="Permalink to &quot;Зборка/інсталяція&quot;">​</a></h2><p>The <a href="./../assembly/quick_start_cuav_pixhawk_v6x.html">Pixhawk V6X Wiring Quick Start</a> provides instructions on how to assemble required/important peripherals including GPS, Power Module etc.</p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p><img src="'+i+'" alt="Pixhawk V6x Pinout"></p><p>Примітки:</p><ul><li>The <a href="./../camera/fc_connected_camera.html#camera-capture-configuration">camera capture pin</a> (<code>PI0</code>) is pin 2 on the AD&amp;IO port, marked above as <code>FMU_CAP1</code>.</li></ul><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Debug Console</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>UART4</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>TELEM2</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>PX4IO/RC</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>GPS2</td></tr></tbody></table><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>Pixhawk V6X</em> can be triple-redundant on the power supply if three power sources are supplied. The three power rails are: <strong>POWERC1/POWER1</strong>, <strong>POWERC2/POWER2</strong> and <strong>USB</strong>.</p><ul><li><strong>POWER C1</strong> and <strong>POWER C2</strong> are DroneCAN/UAVCAN battery interfaces (recommended)；<strong>POWER1</strong> and <strong>POWER2</strong> are SMbus/I2C battery interfaces (backup).</li><li><strong>POWER C1</strong> and <strong>POWER1</strong> use the same power switch, <strong>POWER C2</strong> and <strong>POWER2</strong> use the same power switch.</li></ul><p><strong>Normal Operation Maximum Ratings</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWER C1</strong>, <strong>POWER C2</strong>, <strong>POWER1</strong> and <strong>POWER2</strong> inputs (4.75V to 5.7V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>За таких умов система не буде витрачати жодної потужності (не буде працювати), але залишиться неушкодженою.</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (operational range 4.7V to 5.7V, 0V to 10V undamaged)</li><li><strong>USB input</strong> (operational range 4.7V to 5.7V, 0V to 6V undamaged)</li><li><strong>Servo input:</strong> <code>VDD_SERVO</code> pin of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 42V undamaged)</li></ol><p><strong>Voltage monitoring</strong></p><p>Digital DroneCAN/UAVCAN battery monitoring is enabled by default (see <a href="./../assembly/quick_start_cuav_pixhawk_v6x.html#power">Quickstart &gt; Power</a>).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Analog battery monitoring via an ADC is not supported on this particular board, but may be supported in variations of this flight controller with a different baseboard.</p></div><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v6x_default</span></span></code></pre></div><p><a id="debug_port"></a></p><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port.</p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-full">Pixhawk Debug Full</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> interface (JST SM10B connector).</p><table tabindex="0"><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td><code>Vtref</code></td><td>+3.3V</td></tr><tr><td>2 (blk)</td><td>Console TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>Console RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td><code>SWDIO</code></td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td><code>SWCLK</code></td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td><code>SWO</code></td><td>+3.3V</td></tr><tr><td>7 (blk)</td><td>NFC GPIO</td><td>+3.3V</td></tr><tr><td>8 (чорний)</td><td>PH11</td><td>+3.3V</td></tr><tr><td>9 (чорний)</td><td>nRST</td><td>+3.3V</td></tr><tr><td>10 (чорний)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><p>Інформацію про підключення та використання цього порту див:</p><ul><li><a href="./../debug/system_console.html#pixhawk_debug_port">PX4 System Console</a> (Note, the FMU console maps to USART3).</li><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li></ul><h2 id="периферіині-пристроі" tabindex="-1">Периферійні пристрої <a class="header-anchor" href="#периферіині-пристроі" aria-label="Permalink to &quot;Периферійні пристрої&quot;">​</a></h2><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Digital Airspeed Sensor</a></li><li><a href="https://holybro.com/collections/telemetry-radios?orderby=date" target="_blank" rel="noreferrer">Telemetry Radio Modules</a></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li></ul><h2 id="підтримувані-платформи-конструкціі" tabindex="-1">Підтримувані платформи / Конструкції <a class="header-anchor" href="#підтримувані-платформи-конструкціі" aria-label="Permalink to &quot;Підтримувані платформи / Конструкції&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа / човен, який може керуватися звичайними RC сервоприводами або сервоприводами Futaba S-Bus. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://doc.cuav.net/" target="_blank" rel="noreferrer">Документація CUAV</a> (CUAV)</li><li><a href="./../assembly/quick_start_cuav_pixhawk_v6x.html">Pixhawk V6X Wiring QuickStart</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Автопілот FMUv6X Стандартний</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт шини автопілота Pixhawk</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Стандарт Pixhawk Connector Standard</a></li></ul>',56)]))}const S=l(s,[["render",h]]);export{x as __pageData,S as default};
