import{_ as e,a,b as r}from"./chunks/x7-pinouts.B_L5lLEJ.js";import{_ as o}from"./chunks/debug_port_dsu7.Dgj2juP5.js";import{_ as l}from"./chunks/cuav_v5_debug_cable.CjGsjR_g.js";import{_ as i,c as s,a8 as n,o as c}from"./chunks/framework.BDnHobkS.js";const S=JSON.parse('{"title":"Польотний контролер CUAV X7","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/cuav_x7.md","filePath":"uk/flight_controller/cuav_x7.md"}'),p={name:"uk/flight_controller/cuav_x7.md"};function d(h,t,u,m,b,f){return c(),s("div",null,t[0]||(t[0]=[n('<h1 id="польотнии-контролер-cuav-x7" tabindex="-1">Польотний контролер CUAV X7 <a class="header-anchor" href="#польотнии-контролер-cuav-x7" aria-label="Permalink to &quot;Польотний контролер CUAV X7&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://www.cuav.net" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <a href="http://doc.cuav.net/flight-controller/x7/en/x7.html" target="_blank" rel="noreferrer">X7</a><sup>®</sup> flight controller is a high-performance autopilot. Це ідеальний вибір для промислових дронів і великомасштабних важких дронів. В основному постачається комерційним виробникам.</p><p><img src="'+e+'" alt="CUAV x7"></p><p>Контролер польоту використовує модульний дизайн і може бути поєднаний з різними базовими платами. Ви можете розробити спеціалізовану плату-носій для вашого БПЛА, щоб покращити інтеграцію комерційних систем, зменшити кількість проводів, підвищити надійність системи та підвищити конкурентоспроможність вашого БПЛА (наприклад, інтегруючи датчики швидкості повітря, телеметрію або навіть супутниковий комп&#39;ютер на платі-носії). CUAV також надає вам на вибір різноманітні несучі плати.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="функціі" tabindex="-1">Функції <a class="header-anchor" href="#функціі" aria-label="Permalink to &quot;Функції&quot;">​</a></h2><ul><li>Внутрішнє поглинання ударів</li><li>Модульна конструкція, може бути саморобною несучою платою</li><li>Підтримка USB_HS, швидше завантаження журналів (PX4 ще не підтримується)</li><li>Підтримка більшої кількості виходів DShot</li><li>Підтримка нагріву IMU, покращення роботи датчика</li><li>Виділений порт для акумулятора CAN</li><li>3 комплекти датчиків IMU</li><li>Автомобільний компас RM3100</li><li>Високопродуктивний процесор</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The manufacturer <a href="https://doc.cuav.net/flight-controller/x7/en/" target="_blank" rel="noreferrer">CUAV Docs</a> are the canonical reference for the X7. Вони повинні використовуватися за перевагою, оскільки вони містять найбільш повну та актуальну інформацію.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li><p>Головний FMU процесор: STM32H743</p></li><li><p>Бортові сенсори:</p><ul><li>Акселерометр/Гіроскоп: ICM-20689</li><li>Прискорювач/гіроскоп: ICM-20649</li><li>Акселерометр/Гіроскоп: BMI088</li><li>Магнітометр: RM3100</li><li>Барометр: MS5611*2</li></ul></li><li><p>Інтерфейси:</p><ul><li>14 ШІМ-виходів （12 підтримує Dshot）</li><li>Підтримка декількох входів RC (SBU / CPPM / DSM)</li><li>Аналоговий / PWM вхід RSSI</li><li>2 GPS порти (GPS і UART4 порти)</li><li>4 шини i2c (два виділені порти i2c)</li><li>2 порти CAN шини</li><li>2 порти живлення (Power A - загальний інтерфейс адаптера, Power C - інтерфейс акумулятора DroneCAN)</li><li>2 входи АЦП</li><li>1 USB порт</li></ul></li><li><p>Система живлення:</p><ul><li>Живлення: 4.3~5.4В</li><li>Вхід USB: 4.75~5.25В</li><li>Вхід сервоприводу: 0~36V</li></ul></li><li><p>Вага та розміри:</p><ul><li>Вага: 101 g</li></ul></li><li><p>Інші характеристики:</p><ul><li>Робоча температура: -20 ~ 80°c (виміряне значення)</li><li>Три імуси</li><li>Підтримка компенсації температури</li><li>Внутрішнє поглинання ударів</li></ul></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>When it runs PX4 firmware, only 8 pwm works, the remaining 6 pwm are still being adapted, so it is not compatible with VOLT now.</p></div><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p><a href="https://store.cuav.net" target="_blank" rel="noreferrer">CUAV Store</a></p><p><a href="https://www.aliexpress.com/item/4001042683738.html?spm=a2g0o.detail.1000060.2.1ebb2a9d3WDryi&amp;gps-id=pcDetailBottomMoreThisSeller&amp;scm=1007.13339.169870.0&amp;scm_id=1007.13339.169870.0&amp;scm-url=1007.13339.169870.0&amp;pvid=f0df2481-1c0a-44eb-92a4-9c11c6cb3d06&amp;_t=gps-id:pcDetailBottomMoreThisSeller,scm-url:1007.13339.169870.0,pvid:f0df2481-1c0a-44eb-92a4-9c11c6cb3d06,tpp_buckets:668%230%23131923%2320_668%23808%234094%23518_668%23888%233325%2319_668%234328%2319934%23630_668%232846%238115%23807_668%232717%237566%23827_668%231000022185%231000066058%230_668%233468%2315607%2376" target="_blank" rel="noreferrer">CUAV aliexpress</a></p><h2 id="з-єднання-проводка" tabindex="-1">З&#39;єднання (Проводка) <a class="header-anchor" href="#з-єднання-проводка" aria-label="Permalink to &quot;З&#39;єднання (Проводка)&quot;">​</a></h2><p><a href="http://doc.cuav.net/flight-controller/x7/en/quick-start/quick-start-x7.html" target="_blank" rel="noreferrer">CUAV X7 Wiring Quickstart</a></p><h2 id="розмір-та-роз-єми" tabindex="-1">Розмір та роз&#39;єми <a class="header-anchor" href="#розмір-та-роз-єми" aria-label="Permalink to &quot;Розмір та роз&#39;єми&quot;">​</a></h2><p><img src="'+a+'" alt="CUAV x7"></p><p><img src="'+r+'" alt="X7 pinouts"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>The <code>RCIN</code> port is limited to powering the RC receiver and cannot be connected to any power/load.</p></div><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p>The <em>X7 AutoPilot</em> can be triple-redundant on the power supply if three power sources are supplied. The power rails are: <strong>POWERA</strong>, <strong>POWERC</strong> and <strong>USB</strong>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The output power rails <strong>PWM OUT</strong> (0V to 36V) do not power the flight controller board (and are not powered by it). You must supply power to one of <strong>POWERA</strong>, <strong>POWERC</strong> or <strong>USB</strong> or the board will be unpowered.</p></div><p><strong>Normal Operation Maximum Ratings</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWERA</strong> and <strong>POWERC</strong> inputs (4.3V to 5.4V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make cuav_x7pro_default</span></span></code></pre></div><h2 id="захист-від-перенапруги" tabindex="-1">Захист від перенапруги <a class="header-anchor" href="#захист-від-перенапруги" aria-label="Permalink to &quot;Захист від перенапруги&quot;">​</a></h2><p>The <em>X7</em> has over-current protection on the 5 Volt Peripheral and 5 Volt high power, which limits the current to 2.5A. The <em>X7</em> has short circuit protection.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Up to 2.5 A can be delivered to the connectors listed as pin 1 (although these are only rated at 1 A).</p></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>The system&#39;s serial console and SWD interface operate on the <strong>DSU7</strong> port. Просто підключіть кабель FTDI до роз&#39;єму DSU7 (у списку продуктів є кабель CUAV FTDI).</p><p><img src="'+o+'" alt="Debug port (DSU7)"></p><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> operate on the <strong>FMU Debug</strong> port (<code>DSU7</code>).</p><p>The debug port (<code>DSU7</code>) uses a <a href="https://www.digikey.com.au/product-detail/en/jst-sales-america-inc/BM06B-GHS-TBT-LF-SN-N/455-1582-1-ND/807850" target="_blank" rel="noreferrer">JST BM06B</a> connector and has the following pinout:</p><table tabindex="0"><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>5V+</td><td>+5V</td></tr><tr><td>2 (blk)</td><td>DEBUG TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>DEBUG RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td>FMU_SWDIO</td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td>FMU_SWCLK</td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><p>CUAV provides a dedicated debugging cable, which can be connected to the <code>DSU7</code> port. This splits out an FTDI cable for connecting the <a href="./../debug/system_console.html">PX4 System Console</a> to a computer USB port, and SWD pins used for SWD/JTAG debugging. The provided debug cable does not connect to the SWD port <code>Vref</code> pin (1).</p><p><img src="'+l+'" alt="CUAV Debug cable"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>The SWD Vref pin (1) uses 5V as Vref but the CPU is run at 3.3V!</p><p>Деякі JTAG-адаптери (SEGGER J-Link) використовують напругу Vref для встановлення напруги на лініях SWD. For direct connection to <em>Segger Jlink</em> we recommended you use the 3.3 Volts from pin 4 of the connector marked <code>DSM</code>/<code>SBUS</code>/<code>RSSI</code> to provide <code>Vtref</code> to the JTAG (i.e. providing 3.3V and <em>NOT</em> 5V).</p></div><h2 id="підтримувані-платформи-конструкціі" tabindex="-1">Підтримувані платформи / Конструкції <a class="header-anchor" href="#підтримувані-платформи-конструкціі" aria-label="Permalink to &quot;Підтримувані платформи / Конструкції&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа / човен, який може керуватися звичайними RC сервоприводами або сервоприводами Futaba S-Bus. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="http://doc.cuav.net/flight-controller/x7/en/quick-start/quick-start-x7.html" target="_blank" rel="noreferrer">Quick start</a></li><li><a href="http://doc.cuav.net" target="_blank" rel="noreferrer">CUAV docs</a></li><li><a href="https://github.com/cuav/hardware/tree/master/X7_Autopilot" target="_blank" rel="noreferrer">x7 schematic</a></li></ul>',47)]))}const w=i(p,[["render",d]]);export{S as __pageData,w as default};
