import{_ as r}from"./chunks/pix32_hero.BGwOX7hT.js";import{_ as l,c as i,j as e,a as o,G as s,a8 as h,B as n,o as d}from"./chunks/framework.BDnHobkS.js";const _=JSON.parse('{"title":"Контролер польоту Holybro pix32 (знято з виробництва)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/holybro_pix32.md","filePath":"uk/flight_controller/holybro_pix32.md"}'),p={name:"uk/flight_controller/holybro_pix32.md"};function c(u,t,b,m,f,g){const a=n("Badge");return d(),i("div",null,[t[0]||(t[0]=e("h1",{id:"контролер-польоту-holybro-pix32-знято-з-виробництва",tabindex:"-1"},[o("Контролер польоту Holybro pix32 (знято з виробництва) "),e("a",{class:"header-anchor",href:"#контролер-польоту-holybro-pix32-знято-з-виробництва","aria-label":'Permalink to "Контролер польоту Holybro pix32 (знято з виробництва)"'},"​")],-1)),s(a,{type:"info",text:"Discontinued"}),t[1]||(t[1]=h('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The Holybro<sup>®</sup> <a href="https://holybro.com/collections/autopilot-flight-controllers/products/pix32pixhawk-flight-controller" target="_blank" rel="noreferrer">pix32 autopilot</a> (also known as &quot;Pixhawk 2&quot;, and formerly as HKPilot32) is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk<sup>®</sup>-project</a> <strong>FMUv2</strong> open hardware design. This board is based on hardware version Pixhawk 2.4.6. It runs the PX4 flight stack on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><p><img src="'+r+'" alt="pix32"></p><p>As a CC-BY-SA 3.0 licensed Open Hardware design, schematics and design files should be <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">available here</a>.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The Holybro pix32 is software compatible with the <a href="./../flight_controller/pixhawk.html">3DR Pixhawk 1</a>. It is not connector compatible, but is otherwise physically very similar to the 3DR Pixhawk or mRo Pixhawk.</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="основні-характеристики" tabindex="-1">Основні характеристики <a class="header-anchor" href="#основні-характеристики" aria-label="Permalink to &quot;Основні характеристики&quot;">​</a></h2><ul><li>Main System-on-Chip: <a href="http://www.st.com/web/en/catalog/mmc/FM141/SC1169/SS1577/LN1789" target="_blank" rel="noreferrer">STM32F427</a><ul><li>Процесор: 32-розрядний ядро STM32F427 Cortex<sup>®</sup> M4 з FPU</li><li>ОЗП: 168 МГц/256 КБ</li><li>Flash: 2 МБ</li></ul></li><li>Failsafe System-on-Chip: STM32F103</li><li>Датчики: <ul><li>ST Micro L3GD20 3-axis 16-бітний гіроскоп</li><li>ST Micro LSM303D 3-вісний 14-бітний акселерометр / магнітометр</li><li>Invensense<sup>®</sup> MPU 6000 3-вісний акселерометр/гіроскоп</li><li>MEAS MS5611 барометр</li></ul></li><li>Розміри/Вага <ul><li>Розмір: 81x44x15 мм</li><li>Вага: 33.1гр</li></ul></li><li>GPS: u-blox<sup>®</sup> супер точний Neo-7M з компасом</li><li>Вхідна напруга: 2~10s (7.4~37V)</li></ul><h3 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h3><ul><li>1x I2C</li><li>2x CAN</li><li>3.3 та 6.6V ADC входи</li><li>5x UART (послідовні порти), один високої потужності, 2x з контролем потоку ГВП</li><li>Вхід, сумісний з приймачами Spektrum DSM / DSM2 / DSM-X® Satellite до DX8 (DX9 та вище не підтримуються)</li><li>Futaba<sup>®</sup> S.BUS сумісний вхід та вихід</li><li>Сигнал суми PPM</li><li>Вхід RSSI (ШІМ або напруга)</li><li>SPI</li><li>Зовнішній порт microUSB</li><li>Раз&#39;єми Molex PicoBlade</li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p><a href="https://holybro.com/collections/autopilot-flight-controllers/products/pix32pixhawk-flight-controller" target="_blank" rel="noreferrer">shop.holybro.com</a></p><h3 id="аксесуари" tabindex="-1">Аксесуари <a class="header-anchor" href="#аксесуари" aria-label="Permalink to &quot;Аксесуари&quot;">​</a></h3><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Digital airspeed sensor</a></li><li><a href="https://hobbyking.com/en_us/apm-pixhawk-wireless-wifi-radio-module.html" target="_blank" rel="noreferrer">Hobbyking<sup>®</sup> Wifi Telemetry</a></li><li><a href="./../telemetry/holybro_sik_radio.html">HolyBro SiK Telemetry Radio (EU 433 MHz, US 915 MHz)</a></li></ul><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v3_default</span></span></code></pre></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>See <a href="./../flight_controller/pixhawk.html#debug-ports">3DR Pixhawk 1 &gt; Debug Ports</a>.</p><h2 id="розпіновки-та-схеми" tabindex="-1">Розпіновки та схеми <a class="header-anchor" href="#розпіновки-та-схеми" aria-label="Permalink to &quot;Розпіновки та схеми&quot;">​</a></h2><p>The board is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk project</a> <strong>FMUv2</strong> open hardware design.</p><ul><li><a href="https://raw.githubusercontent.com/PX4/Hardware/master/FMUv2/PX4FMUv2.4.5.pdf" target="_blank" rel="noreferrer">FMUv2 + IOv2 schematic</a> -- Schematic and layout</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>As a CC-BY-SA 3.0 licensed Open Hardware design, all schematics and design files are <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">available</a>.</p></div><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (керування потоком)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',26))])}const S=l(p,[["render",c]]);export{_ as __pageData,S as default};
