import{_ as e,a,b as r,c as i,d as l,e as d}from"./chunks/hardware-mindpx-pin.BkT1f1JP.js";import{_ as n,c as s,a8 as o,o as p}from"./chunks/framework.BDnHobkS.js";const P=JSON.parse('{"title":"Апаратне забезпечення MindPX","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/mindpx.md","filePath":"uk/flight_controller/mindpx.md"}'),c={name:"uk/flight_controller/mindpx.md"};function h(u,t,m,x,g,b){return p(),s("div",null,t[0]||(t[0]=[o('<h1 id="апаратне-забезпечення-mindpx" tabindex="-1">Апаратне забезпечення MindPX <a class="header-anchor" href="#апаратне-забезпечення-mindpx" aria-label="Permalink to &quot;Апаратне забезпечення MindPX&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="http://mindpx.net" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The AirMind<sup>®</sup> <a href="http://mindpx.net" target="_blank" rel="noreferrer">MindPX</a> series is a new generation autopilot system branched from Pixhawk<sup>®</sup>.</p><p><img src="'+e+'" alt="MindPX Controller"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>These flight controllers are <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The main hardware documentation is <a href="http://mindpx.net/assets/accessories/Specification9.18_3_pdf.pdf" target="_blank" rel="noreferrer">here</a>.</p></div><p>MindPX - це нова система автопілотів, що створена з Pixhawk<sup>®</sup>, переглянута в схематиці та структурі, і вони були ще більше розширені новими можливостями, щоб безпілотний пристрій був &quot;розумнішим&quot; та простшим у користуванні.</p><p>MindPX збільшує загальну кількість каналів виведення PWM до 16 (8 основних виводів + 8 допоміжних виводів). Це означає, що MindPX може підтримувати більш складні конфігурації VTOL і кращий контроль. Це особливо важливо для тих контролерів польоту на основі FMU-V4, оскільки MindPX реалізує основний та додатковий вивід в одному FMU.</p><p><img src="'+a+'" alt=""></p><ul><li><p>Головний системний чіп: STM32F427</p><ul><li>Процесор: 32bits, 168 MHz ARM Cortex<sup>®</sup> M4 з FPU</li><li>RAM: 256 KB SRAM</li><li>2MB Flash</li><li>ST Micro LSM303D 14 бітний акселерометр/магнітометр</li><li>MEAS MS5611 барометр</li><li>Інтегровані датчики 6-осевого сенсора InvenSense<sup>®</sup> MPU6500</li></ul></li><li><p>Виділені функції:</p><ul><li>Корпус із обробленого CNC алюмінієвого сплаву, легкий і міцний</li><li>Вбудована ізольована резервна IMU</li><li>Загалом 16 каналів виведення PWM (8 основних + 8 додаткових)</li><li>1 додатковий порт I2C для підключення потоку.</li><li>1 додатковий USB-порт для підключення компаньйон-комп&#39;ютера (вбудований конвертер UART-to-USB)</li><li>Відкритий порт для налагодження</li></ul></li></ul><h2 id="швидкии-старт" tabindex="-1">Швидкий Старт <a class="header-anchor" href="#швидкии-старт" aria-label="Permalink to &quot;Швидкий Старт&quot;">​</a></h2><h3 id="встановлення" tabindex="-1">Встановлення <a class="header-anchor" href="#встановлення" aria-label="Permalink to &quot;Встановлення&quot;">​</a></h3><p><img src="'+r+'" alt="MindPX Mounting"></p><h3 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h3><p><img src="'+i+'" alt="MindPX Wiring 1"></p><p><img src="'+l+'" alt="MindPX Wiring 2"></p><h3 id="pin" tabindex="-1">Pin <a class="header-anchor" href="#pin" aria-label="Permalink to &quot;Pin&quot;">​</a></h3><p><img src="'+d+'" alt="MindPX Pinout"></p><table tabindex="0"><thead><tr><th style="text-align:center;">Номер.</th><th style="text-align:center;">Опис</th><th style="text-align:center;">Номер.</th><th style="text-align:center;">Опис</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td style="text-align:center;">Power</td><td style="text-align:center;">9</td><td style="text-align:center;">I2C2 (MindFLow)</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:center;">Відлагодження (оновлення завантажувача)</td><td style="text-align:center;">10</td><td style="text-align:center;">USB2 (Serial 2 to USB)</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:center;">USB1 (оновлення прошивки)</td><td style="text-align:center;">11</td><td style="text-align:center;">UART4,5</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:center;">Reset</td><td style="text-align:center;">12</td><td style="text-align:center;">UART1 (Телеметрія)</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:center;">UART3 (GPS)</td><td style="text-align:center;">13</td><td style="text-align:center;">CAN</td></tr><tr><td style="text-align:center;">6</td><td style="text-align:center;">I2C1(Зовнішній компас)</td><td style="text-align:center;">14</td><td style="text-align:center;">ADC</td></tr><tr><td style="text-align:center;">7</td><td style="text-align:center;">Слот для TF-карти</td><td style="text-align:center;">15</td><td style="text-align:center;">Триколорне світло</td></tr><tr><td style="text-align:center;">8</td><td style="text-align:center;">NRF/SPI(Дистанційне Управління)</td><td style="text-align:center;">16</td><td style="text-align:center;">Looper</td></tr></tbody></table><h3 id="приимач-радіо" tabindex="-1">Приймач радіо <a class="header-anchor" href="#приимач-радіо" aria-label="Permalink to &quot;Приймач радіо&quot;">​</a></h3><p>MindPX підтримує широкий спектр радіоприймачів (починаючи з версії V2.6), включаючи: PPM/SBUS/DSM/DSM2/DSMX. MindPX також підтримує бі-дирекційну телеметрію FrSky<sup>®</sup> D та S.Port.</p><p>For detailed Pin diagram, please refer to the <a href="http://mindpx.net/assets/accessories/UserGuide9.18_2_pdf.pdf" target="_blank" rel="noreferrer">User Guide</a>.</p><h3 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make airmind_mindpx-v2_default</span></span></code></pre></div><h3 id="підключення-пк-компаньиона" tabindex="-1">Підключення ПК компаньйона <a class="header-anchor" href="#підключення-пк-компаньиона" aria-label="Permalink to &quot;Підключення ПК компаньйона&quot;">​</a></h3><p>MindPX має USB-TO-UART Bridge IC на платі. Кабель micro-USB до USB type A використовується для підключення. Підключіть micro-USB кінець до порту &#39;OBC&#39; MindPX та USB type A кінець до комп&#39;ютера-компаньйона.</p><p>Максимальна швидкість BAUD така ж, як у родини px4, яка становить до 921600.</p><h2 id="посібник-користувача" tabindex="-1">Посібник користувача <a class="header-anchor" href="#посібник-користувача" aria-label="Permalink to &quot;Посібник користувача&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The user guide is <a href="http://mindpx.net/assets/accessories/UserGuide9.18_2_pdf.pdf" target="_blank" rel="noreferrer">here</a>.</p></div><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>MindRacer is available at <a href="http://drupal.xitronet.com/?q=catalog" target="_blank" rel="noreferrer">AirMind Store</a> on internet. Ви також можете знайти MindRacer на Amazon<sup>®</sup> або на eBay<sup>®</sup>.</p><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>RC</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>?</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>Debug Console</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>?</td></tr></tbody></table><h2 id="підтримка" tabindex="-1">Підтримка <a class="header-anchor" href="#підтримка" aria-label="Permalink to &quot;Підтримка&quot;">​</a></h2><p>Будь ласка, зайдіть на <a href="http://www.mindpx.org" target="_blank" rel="noreferrer">http://www.mindpx.org</a> для отримання додаткової інформації. Або ви можете надіслати email на <a href="mailto:support@mindpx.net" target="_blank" rel="noreferrer">support@mindpx.net</a> для будь-яких запитів або допомоги.</p>',38)]))}const M=n(c,[["render",h]]);export{P as __pageData,M as default};
