import{_ as e,a,b as r}from"./chunks/hardware-mindracer-conn.cXeNCZCN.js";import{_ as i,c as l,a8 as n,o as d}from"./chunks/framework.BDnHobkS.js";const x=JSON.parse('{"title":"Апаратне забезпечення MindRacer","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/mindracer.md","filePath":"uk/flight_controller/mindracer.md"}'),o={name:"uk/flight_controller/mindracer.md"};function s(c,t,p,h,m,u){return d(),l("div",null,t[0]||(t[0]=[n('<h1 id="апаратне-забезпечення-mindracer" tabindex="-1">Апаратне забезпечення MindRacer <a class="header-anchor" href="#апаратне-забезпечення-mindracer" aria-label="Permalink to &quot;Апаратне забезпечення MindRacer&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="http://mindpx.net" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The AirMind<sup>®</sup> <a href="http://mindpx.net" target="_blank" rel="noreferrer">MindRacer</a> series is a fully stackable flight <em>platform</em> for miniature UAVs. The platform currently has two RTF vehicles: <a href="./../complete_vehicles_mc/mindracer210.html">MindRacer 210</a> and <a href="./../complete_vehicles_mc/nanomind110.html">NanoMind 110</a>.</p><p><img src="'+e+'" alt="MindRacer"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><p>MindRacer - це повністю стекована платформа для польотів для мініатюрних БПЛА. Based on <a href="./../flight_controller/mindpx.html">MindPX</a>, <em>MindRacer</em> further scales down in formfactor while focused on providing modularity. MindRacer is a <em>platform</em> rather than a flight controller.</p><p>MindRacer імплементує концепт SEP (паяльно-елімінаційний-порт) та WEP (протокол-елімінації-проводів). Перед SEP та WEP паяння та проводка завжди є основним джерелом проблем та зниження ефективності під час виготовлення та налаштування БПЛА.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The main hardware documentation is <a href="http://mindpx.net/assets/accessories/mindracer_spec_v1.2.pdf" target="_blank" rel="noreferrer">here</a>.</p></div><ul><li>Ультра-міні розмір, вага ~6г</li><li>Високопродуктивний процесор з плаваючою точкою STM32F427 на частоті 168 МГц, надзвичайно швидка відповідь &quot;педалі&quot; газу</li><li>Підтримка OneShot ESC</li><li>Підтримка радіоприймачів PPM/SBUS/DSM, підтримка телеметрії D.Port/S.Port/Wifi</li><li>На борту реєстратора даних польоту</li><li>Підтримка ізоляції IMU</li><li>Cтандартний DroneCode<sup>®</sup> сумісний коннектор</li></ul><table tabindex="0"><thead><tr><th style="text-align:center;">Елемент</th><th style="text-align:center;">Опис</th></tr></thead><tbody><tr><td style="text-align:center;">Політний контролер/Процесор</td><td style="text-align:center;">F427VIT6</td></tr><tr><td style="text-align:center;">Вага</td><td style="text-align:center;">~6г</td></tr><tr><td style="text-align:center;">Розмір</td><td style="text-align:center;">35х35мм</td></tr><tr><td style="text-align:center;">PWM Виходи</td><td style="text-align:center;">максимум 6</td></tr><tr><td style="text-align:center;">IMU</td><td style="text-align:center;">10DOF</td></tr><tr><td style="text-align:center;">Ізоляція IMU</td><td style="text-align:center;">ТАК/Опціонально</td></tr><tr><td style="text-align:center;">Приймач радіо</td><td style="text-align:center;">S.BUS/PPM/DSM/DSM2/DSMX/SUMD</td></tr><tr><td style="text-align:center;">Телеметрія</td><td style="text-align:center;">FrSky<sup>&amp;reg;</sup> D.Port, S.Port, Wifi, 3DR radio</td></tr><tr><td style="text-align:center;">На борту TF-карти для запису даних польоту</td><td style="text-align:center;">ТАК</td></tr><tr><td style="text-align:center;">Підтримка OneShot ESC</td><td style="text-align:center;">ТАК</td></tr><tr><td style="text-align:center;">Розширення слотів</td><td style="text-align:center;">2x7(pin)x2</td></tr><tr><td style="text-align:center;">На борту годинник реального часу</td><td style="text-align:center;">ТАК</td></tr><tr><td style="text-align:center;">З’єднання</td><td style="text-align:center;">JST GH(відповідність стандарту DroneCode)</td></tr></tbody></table><h2 id="швидкии-старт" tabindex="-1">Швидкий Старт <a class="header-anchor" href="#швидкии-старт" aria-label="Permalink to &quot;Швидкий Старт&quot;">​</a></h2><h3 id="карта-виводів" tabindex="-1">Карта виводів <a class="header-anchor" href="#карта-виводів" aria-label="Permalink to &quot;Карта виводів&quot;">​</a></h3><p><img src="'+a+'" alt="Mindracer pinout"></p><h3 id="як-зібрати" tabindex="-1">Як зібрати <a class="header-anchor" href="#як-зібрати" aria-label="Permalink to &quot;Як зібрати&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make airmind_mindpx-v2_default</span></span></code></pre></div><h3 id="підключення-пк-компаньиона" tabindex="-1">Підключення ПК компаньйона <a class="header-anchor" href="#підключення-пк-компаньиона" aria-label="Permalink to &quot;Підключення ПК компаньйона&quot;">​</a></h3><p>MindRacer має приєднану до себе плату Adapt IO.</p><p><img src="'+r+'" alt="Attached Adapt IO board"></p><p>MindRacer має вбудований конвертер UART-to-USB. Для підключення комп&#39;ютера-компаньйона встановіть MindRacer на плату інтерфейсу та підключіть супутній комп&#39;ютер до USB-порту на платі інтерфейсу.</p><p>Максимальна швидкість BAUD така ж, як у родини px4, яка становить до 921600.</p><h3 id="посібник-користувача" tabindex="-1">Посібник користувача <a class="header-anchor" href="#посібник-користувача" aria-label="Permalink to &quot;Посібник користувача&quot;">​</a></h3><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The user guide is <a href="http://mindpx.net/assets/accessories/mindracer_user_guide_v1.2.pdf" target="_blank" rel="noreferrer">here</a></p></div><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>MindRacer is available at <a href="http://drupal.xitronet.com/?q=catalog" target="_blank" rel="noreferrer">AirMind Store</a>. Ви також можете знайти MindRacer на Amazon<sup>®</sup> або на eBay<sup>®</sup>.</p><h2 id="підтримка" tabindex="-1">Підтримка <a class="header-anchor" href="#підтримка" aria-label="Permalink to &quot;Підтримка&quot;">​</a></h2><p>Будь ласка, зайдіть на <a href="http://www.mindpx.org" target="_blank" rel="noreferrer">http://www.mindpx.org</a> для отримання додаткової інформації. Or you can send email to <a href="mailto::support@mindpx.net" target="_blank" rel="noreferrer">support@mindpx.net</a> for any inquiries or help.</p>',29)]))}const b=i(o,[["render",s]]);export{x as __pageData,b as default};
