import{_ as r,a as l,b as o,c as d,d as i}from"./chunks/bottom.Bi9sn-SE.js";import{_ as n,c as s,j as e,a as h,G as c,a8 as f,B as m,o as g}from"./chunks/framework.BDnHobkS.js";const P=JSON.parse('{"title":"ModalAI Flight Core v1","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/modalai_fc_v1.md","filePath":"uk/flight_controller/modalai_fc_v1.md"}'),p={name:"uk/flight_controller/modalai_fc_v1.md"};function b(u,t,x,y,_,k){const a=m("Badge");return g(),s("div",null,[t[0]||(t[0]=e("h1",{id:"modalai-flight-core-v1",tabindex:"-1"},[h("ModalAI Flight Core v1 "),e("a",{class:"header-anchor",href:"#modalai-flight-core-v1","aria-label":'Permalink to "ModalAI Flight Core v1"'},"​")],-1)),c(a,{type:"tip",text:"PX4 v1.11"}),t[1]||(t[1]=f('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://forum.modalai.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The ModalAI <a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core v1</a> (<a href="https://docs.modalai.com/flight-core-datasheet" target="_blank" rel="noreferrer">Datasheet</a>) is a flight controller for PX4, made in the USA. The Flight Core can be paired with ModalAI <a href="https://modalai.com/voxl" target="_blank" rel="noreferrer">VOXL</a> (<a href="https://docs.modalai.com/voxl-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>) for obstacle avoidance and GPS-denied navigation, or used independently as a standalone flight controller.</p><p><img src="'+r+'" alt="FlightCoreV1"></p><p>Flight Core is identical to the PX4 Flight Controller portion of <a href="https://www.modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight</a> (<a href="https://docs.modalai.com/voxl-flight-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>) which integrates the VOXL Companion Computer and Flight Core into a single PCB.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="специфікація" tabindex="-1">Специфікація <a class="header-anchor" href="#специфікація" aria-label="Permalink to &quot;Специфікація&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Характеристика</th><th style="text-align:left;">Подробиці</th></tr></thead><tbody><tr><td style="text-align:left;">Вага</td><td style="text-align:left;">6 г</td></tr><tr><td style="text-align:left;">MCU</td><td style="text-align:left;">216MHz, 32-bit ARM M7 <a href="https://www.st.com/en/microcontrollers-microprocessors/stm32f765ii.html" target="_blank" rel="noreferrer">STM32F765II</a></td></tr><tr><td style="text-align:left;">Оперативна Пам&#39;ять</td><td style="text-align:left;">256Kb FRAM</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">2Mbit Flash</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">512Kbit SRAM</td></tr><tr><td style="text-align:left;">Прошивка</td><td style="text-align:left;"><a href="https://github.com/PX4/PX4-Autopilot/tree/main/boards/modalai/fc-v1" target="_blank" rel="noreferrer">PX4</a></td></tr><tr><td style="text-align:left;">IMUs</td><td style="text-align:left;"><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">ICM-20602</a> (SPI1)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">ICM-42688 (SPI2)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">BMI088</a> (SPI6)</td></tr><tr><td style="text-align:left;">Барометр</td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp388/" target="_blank" rel="noreferrer">BMP388</a> (I2C4)</td></tr><tr><td style="text-align:left;">Елемент захисту</td><td style="text-align:left;"><a href="https://www.nxp.com/products/security-and-authentication/authentication/plug-and-trust-the-fast-easy-way-to-deploy-secure-iot-connections:A71CH" target="_blank" rel="noreferrer">A71CH</a> (I2C4)</td></tr><tr><td style="text-align:left;">Карта microSD</td><td style="text-align:left;"><a href="./../dev_log/logging.html#sd-cards">Information on supported cards</a></td></tr><tr><td style="text-align:left;">Вхідні дані</td><td style="text-align:left;">GPS/Mag</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Spektrum</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Телеметрія</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">CAN шина</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PPM</td></tr><tr><td style="text-align:left;">Виводи</td><td style="text-align:left;">6 світлодіодів (2xRGB)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">8 каналів PWM</td></tr><tr><td style="text-align:left;">Додаткові Інтерфейси</td><td style="text-align:left;">3 послідовні порти</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">I2C</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">GPIO</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">INFO</p><p>More detailed hardware documentation can be found <a href="https://docs.modalai.com/flight-core-datasheet/" target="_blank" rel="noreferrer">here</a>.</p></div><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><img src="'+l+'" alt="FlightCoreV1Dimensions"></p><h2 id="сумісність-прошивки-px4" tabindex="-1">Сумісність прошивки PX4 <a class="header-anchor" href="#сумісність-прошивки-px4" aria-label="Permalink to &quot;Сумісність прошивки PX4&quot;">​</a></h2><p><em>Flight Core v1</em> is fully compatible with the official PX4 Firmware from PX4 v1.11.</p><p>ModalAI maintains a <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.11" target="_blank" rel="noreferrer">branched PX4 version</a> for PX4 v1.11. Це включає підтримку UART ESC та поліпшення в VIO та VOA, які планується включити в основний код.</p><p>More information about the firmware can be found <a href="https://docs.modalai.com/flight-core-firmware/" target="_blank" rel="noreferrer">here</a>.</p><h2 id="qgroundcontrol-підтримка" tabindex="-1">QGroundControl Підтримка <a class="header-anchor" href="#qgroundcontrol-підтримка" aria-label="Permalink to &quot;QGroundControl Підтримка&quot;">​</a></h2><p>Ця плата підтримується QGroundControl 4.0 та пізнішими версіями.</p><h2 id="доступність" tabindex="-1">Доступність <a class="header-anchor" href="#доступність" aria-label="Permalink to &quot;Доступність&quot;">​</a></h2><ul><li><a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core Complete Kit</a></li><li><a href="https://modalai.com/flight-core" target="_blank" rel="noreferrer">Flight Core integrated with VOXL Companion Computer on a single PCB</a></li><li><a href="https://modalai.com/flight-deck" target="_blank" rel="noreferrer">Flight Core integrated with VOXL Companion Computer and Obstacle Avoidance Cameras (VOXL Flight Deck)</a> (<a href="https://docs.modalai.com/voxl-flight-deck-platform-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li><li><a href="https://shop.modalai.com/products/voxl-flight-deck-r1" target="_blank" rel="noreferrer">Flight Core assembled with VOXL and cameras</a></li></ul><h2 id="швидкии-старт" tabindex="-1">Швидкий Старт <a class="header-anchor" href="#швидкии-старт" aria-label="Permalink to &quot;Швидкий Старт&quot;">​</a></h2><h3 id="орієнтація" tabindex="-1">Орієнтація <a class="header-anchor" href="#орієнтація" aria-label="Permalink to &quot;Орієнтація&quot;">​</a></h3><p>The diagram below shows the recommended orientation, which corresponds to <code>ROTATION_NONE</code> starting with PX4 v1.11.</p><p><img src="'+o+'" alt="FlightCoreV1Orientation"></p><h3 id="з-єднання" tabindex="-1">З’єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З’єднання&quot;">​</a></h3><p>Detailed information about the pinouts can be found <a href="https://docs.modalai.com/flight-core-datasheet-connectors" target="_blank" rel="noreferrer">here</a>.</p><p><img src="'+d+'" alt="FlightCoreV1Top"></p><table tabindex="0"><thead><tr><th>З’єднання</th><th>Опис</th></tr></thead><tbody><tr><td>J1</td><td>Роз&#39;єм інтерфейсу зв&#39;язку VOXL (TELEM2)</td></tr><tr><td>J2</td><td>Програмний та відлагоджувальний роз&#39;єм</td></tr><tr><td>J3</td><td>USB конектор</td></tr><tr><td>J4</td><td>UART2, UART ESC (TELEM3)</td></tr><tr><td>J5</td><td>Конектор телеметрії (TELEM1)</td></tr><tr><td>J6</td><td>Введенням VOXL-Power Management / розширення</td></tr><tr><td>J7</td><td>Роз&#39;єм виводу з 8 каналами PWM</td></tr><tr><td>J8</td><td>Конектор шини CAN</td></tr><tr><td>J9</td><td>PPM RC In</td></tr><tr><td>J10</td><td>External GPS &amp; Magnetometer Connector</td></tr><tr><td>J12</td><td>Вхід RC, Spektrum/SBus/UART конектор</td></tr><tr><td>J13</td><td>I2C Дисплей (роз&#39;єм запасного датчика) / Вхід кнопки безпеки</td></tr></tbody></table><p><img src="'+i+'" alt="FlightCoreV1Bottom"></p><h3 id="посібник-користувача" tabindex="-1">Посібник користувача <a class="header-anchor" href="#посібник-користувача" aria-label="Permalink to &quot;Посібник користувача&quot;">​</a></h3><p>The full user guide is available <a href="https://docs.modalai.com/flight-core-manual/" target="_blank" rel="noreferrer">here</a>.</p><h3 id="як-зібрати" tabindex="-1">Як зібрати <a class="header-anchor" href="#як-зібрати" aria-label="Permalink to &quot;Як зібрати&quot;">​</a></h3><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make modalai_fc-v1</span></span></code></pre></div><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1 (J10)</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3 (J4)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Консоль відлагодження (J2)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>Розширення UART (J6)</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>TELEM2, Основні зв&#39;язки VOXL (J1)</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>RC (J12)</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1 (J5)</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>N/A</td></tr></tbody></table><h2 id="підтримка" tabindex="-1">Підтримка <a class="header-anchor" href="#підтримка" aria-label="Permalink to &quot;Підтримка&quot;">​</a></h2><p>Please visit the <a href="https://forum.modalai.com/category/10/flight-core" target="_blank" rel="noreferrer">ModalAI Forum</a> for more information.</p>',36))])}const w=n(p,[["render",b]]);export{P as __pageData,w as default};
