import{_ as r,a as l,b as d,c as o}from"./chunks/voxl-flight-bottom.2H30pHYz.js";import{_ as i,c as n,j as e,a as s,G as h,a8 as f,B as c,o as g}from"./chunks/framework.BDnHobkS.js";const k=JSON.parse('{"title":"ModalAI VOXL Flight","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/modalai_voxl_flight.md","filePath":"uk/flight_controller/modalai_voxl_flight.md"}'),p={name:"uk/flight_controller/modalai_voxl_flight.md"};function m(x,t,u,b,y,S){const a=c("Badge");return g(),n("div",null,[t[0]||(t[0]=e("h1",{id:"modalai-voxl-flight",tabindex:"-1"},[s("ModalAI VOXL Flight "),e("a",{class:"header-anchor",href:"#modalai-voxl-flight","aria-label":'Permalink to "ModalAI VOXL Flight"'},"​")],-1)),h(a,{type:"tip",text:"PX4 v1.11"}),t[1]||(t[1]=f('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://forum.modalai.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The ModalAI <a href="https://modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight</a> (<a href="https://docs.modalai.com/voxl-flight-datasheet" target="_blank" rel="noreferrer">Datasheet</a>) is one of the first computing platforms to combine the power and sophistication of Snapdragon with the flexibility and ease of use of PX4 on an STM32F7. Виготовлений у США, VOXL Flight підтримує уникнення перешкод та навігацію без сигналу GPS (в приміщенні), поєднуючи це з контролером польоту PX4 на одній платі.</p><p><img src="'+r+'" alt="VOXL-Flight"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="характеристики" tabindex="-1">Характеристики <a class="header-anchor" href="#характеристики" aria-label="Permalink to &quot;Характеристики&quot;">​</a></h2><h3 id="система" tabindex="-1">Система <a class="header-anchor" href="#система" aria-label="Permalink to &quot;Система&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">Характеристика</th><th style="text-align:left;">Подробиці</th></tr></thead><tbody><tr><td style="text-align:left;">Вага</td><td style="text-align:left;">26 г</td></tr></tbody></table><h3 id="супутні-комп-ютери" tabindex="-1">Супутні комп&#39;ютери <a class="header-anchor" href="#супутні-комп-ютери" aria-label="Permalink to &quot;Супутні комп&#39;ютери&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">Характеристика</th><th style="text-align:left;">Подробиці</th></tr></thead><tbody><tr><td style="text-align:left;">Базова операційна система</td><td style="text-align:left;">Linux Yocto Jethro з ядром 3.18. Additional Linux Operating Systems can be used by running Docker on VOXL, details <a href="https://docs.modalai.com/docker-on-voxl/" target="_blank" rel="noreferrer">here</a></td></tr><tr><td style="text-align:left;">Обчислення</td><td style="text-align:left;">Qualcomm Snapdragon 821 w/ 4GB LPDDR4 1866MHz, Snapdragon 821 <a href="https://developer.qualcomm.com/download/sd820e/qualcomm-snapdragon-820e-processor-apq8096sge-device-specification.pdf" target="_blank" rel="noreferrer">Datasheet</a>, <a href="https://developer.qualcomm.com/hardware/apq-8096sg/tools" target="_blank" rel="noreferrer">Docs</a></td></tr><tr><td style="text-align:left;">CPU</td><td style="text-align:left;">Чотирьохядерний процесор до 2,15 ГГц</td></tr><tr><td style="text-align:left;">GPU</td><td style="text-align:left;">Графічний процесор Adreno 530 на частоті 624МГц</td></tr><tr><td style="text-align:left;">Обчислення DSP</td><td style="text-align:left;">Шестизначний цифровий сигнальний процесор (cDSP) 825МГц</td></tr><tr><td style="text-align:left;">Сенсор DSP</td><td style="text-align:left;">Шестизначний сенсор DSP (sDSP) 700МГц</td></tr><tr><td style="text-align:left;">Відео</td><td style="text-align:left;">Захоплення відео 4k30 h.264/5 w/ 720p FPV</td></tr><tr><td style="text-align:left;">Інтерфейси камери</td><td style="text-align:left;">Підтримка MIPI-CSI2, USB UVC, HDMI</td></tr><tr><td style="text-align:left;">Wi-Fi</td><td style="text-align:left;">Pre-certified Wi-Fi module <a href="https://fccid.io/PPD-QCNFA324" target="_blank" rel="noreferrer">QCNFA324 FCC ID:PPD-QCNFA324</a>, QCA6174A modem, 802.11ac 2x2 Dual-band, Bluetooth 4.2 (dual-mode)</td></tr><tr><td style="text-align:left;">4G LTE</td><td style="text-align:left;"><a href="https://www.modalai.com/collections/voxl-add-ons/products/voxl-lte" target="_blank" rel="noreferrer">Optional add-on module</a></td></tr><tr><td style="text-align:left;">Microhard pDDL</td><td style="text-align:left;"><a href="https://www.modalai.com/collections/voxl-add-ons/products/voxl-microhard-modem-usb-hub" target="_blank" rel="noreferrer">Optional add-on module</a></td></tr><tr><td style="text-align:left;">GNSS</td><td style="text-align:left;">WGR7640 10Гц</td></tr><tr><td style="text-align:left;">I/O</td><td style="text-align:left;">1x USB3.0 OTG (порт ADB), 1x USB2.0 (порт розширення), 2x UART, 3x I2C, додаткові GPIO та SPI можуть бути налаштовані</td></tr><tr><td style="text-align:left;">Сховище</td><td style="text-align:left;">32ГБ (UFS 2.0), картка Micro SD</td></tr><tr><td style="text-align:left;">Програмне забезпечення</td><td style="text-align:left;">Docker, OpenCV 2.4.11, 3.4.6, 4.2, ROS Indigo, Qualcomm Machine Vision SDK, see <a href="https://gitlab.com/voxl-public" target="_blank" rel="noreferrer">GitLab</a> for lots of open source examples!</td></tr><tr><td style="text-align:left;">IMUs</td><td style="text-align:left;">ICM-42688 (SPI10), ICM-20948 (SPI1)</td></tr><tr><td style="text-align:left;">Барометр</td><td style="text-align:left;">BMP280</td></tr></tbody></table><h3 id="політнии-контролер" tabindex="-1">Політний контролер <a class="header-anchor" href="#політнии-контролер" aria-label="Permalink to &quot;Політний контролер&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">Характеристика</th><th style="text-align:left;">Подробиці</th></tr></thead><tbody><tr><td style="text-align:left;">MCU</td><td style="text-align:left;">216MHz, 32-bit ARM M7 <a href="https://www.st.com/en/microcontrollers-microprocessors/stm32f765ii.html" target="_blank" rel="noreferrer">STM32F765II</a></td></tr><tr><td style="text-align:left;">Оперативна Пам&#39;ять</td><td style="text-align:left;">256Kb FRAM</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">2Mbit Flash</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">512Kbit SRAM</td></tr><tr><td style="text-align:left;">Прошивка</td><td style="text-align:left;"><a href="https://github.com/PX4/PX4-Autopilot/tree/main/boards/modalai/fc-v1" target="_blank" rel="noreferrer">PX4</a></td></tr><tr><td style="text-align:left;">IMUs</td><td style="text-align:left;"><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">ICM-20602</a> (SPI1)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">ICM-42688 (SPI2)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">BMI088</a> (SPI6)</td></tr><tr><td style="text-align:left;">Барометр</td><td style="text-align:left;"><a href="https://www.bosch-sensortec.com/products/environmental-sensors/pressure-sensors/bmp388/" target="_blank" rel="noreferrer">BMP388</a> (I2C4)</td></tr><tr><td style="text-align:left;">Елемент захисту</td><td style="text-align:left;"><a href="https://www.nxp.com/products/security-and-authentication/authentication/plug-and-trust-the-fast-easy-way-to-deploy-secure-iot-connections:A71CH" target="_blank" rel="noreferrer">A71CH</a> (I2C4)</td></tr><tr><td style="text-align:left;">Карта microSD</td><td style="text-align:left;"><a href="./../dev_log/logging.html#sd-cards">Information on supported cards</a></td></tr><tr><td style="text-align:left;">Вхідні дані</td><td style="text-align:left;">GPS/Mag</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Spektrum</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">Телеметрія</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">CAN шина</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">PPM</td></tr><tr><td style="text-align:left;">Виводи</td><td style="text-align:left;">6 світлодіодів (2xRGB)</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">8 каналів PWM</td></tr><tr><td style="text-align:left;">Додаткові Інтерфейси</td><td style="text-align:left;">3 послідовні порти</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">I2C</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">GPIO</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">INFO</p><p>More detailed hardware documentation can be found <a href="https://docs.modalai.com/voxl-flight-datasheet/" target="_blank" rel="noreferrer">here</a>.</p></div><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><img src="'+l+'" alt="FlightCoreV1Dimensions"></p><p><a href="https://storage.googleapis.com/modalai_public/modal_drawings/M0019_VOXL-Flight.zip" target="_blank" rel="noreferrer">3D STEP File</a></p><h2 id="сумісність-прошивки-px4" tabindex="-1">Сумісність прошивки PX4 <a class="header-anchor" href="#сумісність-прошивки-px4" aria-label="Permalink to &quot;Сумісність прошивки PX4&quot;">​</a></h2><p><em>VOXL Flight</em> is fully compatible with the official PX4 Firmware from PX4 v1.11.</p><p>ModalAI maintains a <a href="https://github.com/modalai/px4-firmware/tree/modalai-1.11" target="_blank" rel="noreferrer">branched PX4 version</a> for PX4 v1.11. Це включає підтримку UART ESC та поліпшення в VIO та VOA, які планується включити в основний код.</p><p>More information about the firmware can be found <a href="https://docs.modalai.com/flight-core-firmware/" target="_blank" rel="noreferrer">here</a>.</p><h2 id="qgroundcontrol-підтримка" tabindex="-1">QGroundControl Підтримка <a class="header-anchor" href="#qgroundcontrol-підтримка" aria-label="Permalink to &quot;QGroundControl Підтримка&quot;">​</a></h2><p>Ця плата підтримується QGroundControl 4.0 та пізнішими версіями.</p><h2 id="доступність" tabindex="-1">Доступність <a class="header-anchor" href="#доступність" aria-label="Permalink to &quot;Доступність&quot;">​</a></h2><ul><li><a href="https://modalai.com/voxl-flight" target="_blank" rel="noreferrer">VOXL Flight Complete Kit</a></li><li><a href="https://www.modalai.com/products/voxl-flight?variant=31707275362355" target="_blank" rel="noreferrer">VOXL Flight Board</a> (only)</li><li><a href="https://modalai.com/flight-deck" target="_blank" rel="noreferrer">VOXL Flight integrated with Obstacle Avoidance Cameras (VOXL Flight Deck)</a> (<a href="https://docs.modalai.com/voxl-flight-deck-platform-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li><li><a href="https://www.modalai.com/collections/development-drones/products/voxl-m500" target="_blank" rel="noreferrer">VOXL Flight in a ready to fly VOXL m500 Development Drone</a> (<a href="https://docs.modalai.com/voxl-m500-reference-drone-datasheet/" target="_blank" rel="noreferrer">Datasheet</a>)</li></ul><h2 id="швидкии-старт" tabindex="-1">Швидкий Старт <a class="header-anchor" href="#швидкии-старт" aria-label="Permalink to &quot;Швидкий Старт&quot;">​</a></h2><p>A quickstart from the vendor is located <a href="https://docs.modalai.com/voxl-flight-quickstart/" target="_blank" rel="noreferrer">here</a>.</p><h3 id="voxl-vision-px4" tabindex="-1">voxl-vision-px4 <a class="header-anchor" href="#voxl-vision-px4" aria-label="Permalink to &quot;voxl-vision-px4&quot;">​</a></h3><p>The VOXL Flight runs <a href="https://gitlab.com/voxl-public/modal-pipe-architecture/voxl-vision-px4" target="_blank" rel="noreferrer">voxl-vision-px4</a> on the companion computer portion of the hardware serving as a sort of MAVLink proxy. For details, the source code is available <a href="https://gitlab.com/voxl-public/modal-pipe-architecture/voxl-vision-px4" target="_blank" rel="noreferrer">here</a></p><h3 id="з-єднання" tabindex="-1">З’єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З’єднання&quot;">​</a></h3><p>Detailed information about the pinouts can be found <a href="https://docs.modalai.com/voxl-flight-datasheet-connectors/" target="_blank" rel="noreferrer">here</a>.</p><h4 id="верх" tabindex="-1">Верх <a class="header-anchor" href="#верх" aria-label="Permalink to &quot;Верх&quot;">​</a></h4><p><img src="'+d+'" alt="VOXLFlightTop"></p><p><em>Note: 1000 Series connectors accessible from the STM32/PX4</em></p><table tabindex="0"><thead><tr><th>З’єднання</th><th>Опис</th><th>Використовується</th></tr></thead><tbody><tr><td>J2</td><td>Датчик зображення 4k (CSI0) високої якості</td><td>Snapdragon - Linux</td></tr><tr><td>J3</td><td>Стерео Датчик Зображення (CSI1)</td><td>Snapdragon - Linux</td></tr><tr><td>J6</td><td>Конектор вентилятора охолодження</td><td>Snapdragon - Linux</td></tr><tr><td>J7</td><td>BLSP6 (GPIO) та BLSP9 (UART)</td><td>Snapdragon - Linux</td></tr><tr><td>J13</td><td>Expansion B2B</td><td>Snapdragon - Linux</td></tr><tr><td>J14</td><td>Підключення вбудованої антени GNSS</td><td>Snapdragon - Linux</td></tr><tr><td>J1001</td><td>Програмний та відлагоджувальний/UART3</td><td>STM32 - PX4</td></tr><tr><td>J1002</td><td>UART ESC, UART2/TELEM3</td><td>STM32 - PX4</td></tr><tr><td>J1003</td><td>PPM RC In</td><td>STM32 - PX4</td></tr><tr><td>J1004</td><td>Вхід RC, Spektrum/SBus/UART6</td><td>STM32 - PX4</td></tr><tr><td>J1006</td><td>Роз&#39;єм USB 2.0 (PX4/QGroundControl)</td><td>STM32 - PX4</td></tr><tr><td>J1007</td><td>8-Канальний вихід PWM/DShot</td><td>STM32 - PX4</td></tr><tr><td>J1008</td><td>CAN шина</td><td>STM32 - PX4</td></tr><tr><td>J1009</td><td>I2C3, UART4</td><td>STM32 - PX4</td></tr><tr><td>J1010</td><td>Телеметрія (TELEM1)</td><td>STM32 - PX4</td></tr><tr><td>J1011</td><td>I2C2, кнопка безпеки</td><td>STM32 - PX4</td></tr><tr><td>J1012</td><td>External GPS &amp; Mag, UART1, I2C1</td><td>STM32 - PX4</td></tr><tr><td>J1013</td><td>Вхід живлення, I2C3</td><td>STM32 - PX4 (живить весь систему)</td></tr></tbody></table><h4 id="низ" tabindex="-1">Низ <a class="header-anchor" href="#низ" aria-label="Permalink to &quot;Низ&quot;">​</a></h4><p><img src="'+o+'" alt="VOXLFlightBottom"></p><p><em>Note: 1000 Series connectors accessible from the STM32/PX4</em></p><table tabindex="0"><thead><tr><th>З’єднання</th><th>Опис</th><th>Використовується</th></tr></thead><tbody><tr><td>J4</td><td>Датчик відстеження / оптичного потоку зображення (CSI2)</td><td>Snapdragon - Linux</td></tr><tr><td>J8</td><td>USB 3.0 OTG</td><td>Snapdragon - Linux, <strong>adb</strong></td></tr><tr><td>J10</td><td>BLSP7 UART та I2C off-board</td><td>Snapdragon - Linux</td></tr><tr><td>J11</td><td>BLSP12 UART та I2C off-board</td><td>Snapdragon - Linux</td></tr><tr><td>VOXL microSD</td><td></td><td>Snapdragon - Linux</td></tr><tr><td>PX4 microSD</td><td>32Gb Max</td><td>STM32 - PX4</td></tr><tr><td>Wi-Fi Antennas</td><td>У комплекті</td><td>Snapdragon - Linux</td></tr></tbody></table><h3 id="посібник-користувача" tabindex="-1">Посібник користувача <a class="header-anchor" href="#посібник-користувача" aria-label="Permalink to &quot;Посібник користувача&quot;">​</a></h3><p>The full user guide is available <a href="https://docs.modalai.com/voxl-flight-quickstart" target="_blank" rel="noreferrer">here</a>.</p><h3 id="як-зібрати" tabindex="-1">Як зібрати <a class="header-anchor" href="#як-зібрати" aria-label="Permalink to &quot;Як зібрати&quot;">​</a></h3><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make modalai_fc-v1</span></span></code></pre></div><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><p><em>Note: mappings shown are for the PX4 controlled interfaces only</em></p><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1 (J1012)</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3 (J1002)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Консоль відладки (J1001)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>Розширення UART (J6)</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>UART між PX4 та Комп&#39;ютером-компаньйоном</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>RC (J1004)</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1 (J1010)</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>N/A</td></tr></tbody></table><h2 id="підтримка" tabindex="-1">Підтримка <a class="header-anchor" href="#підтримка" aria-label="Permalink to &quot;Підтримка&quot;">​</a></h2><p>Please visit the <a href="https://forum.modalai.com/category/8/voxl-flight" target="_blank" rel="noreferrer">ModalAI Forum</a> for more information.</p>',47))])}const _=i(p,[["render",m]]);export{k as __pageData,_ as default};
