import{_ as e,a as r,b as o,c as a}from"./chunks/mro_control_pinouts.COJ70kob.js";import{_ as l,c as i,a8 as n,o as s}from"./chunks/framework.BDnHobkS.js";const _=JSON.parse('{"title":"Контролер польоту mRo Control Zero F7","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/mro_control_zero_f7.md","filePath":"uk/flight_controller/mro_control_zero_f7.md"}'),d={name:"uk/flight_controller/mro_control_zero_f7.md"};function c(h,t,p,u,m,b){return s(),i("div",null,t[0]||(t[0]=[n('<h1 id="контролер-польоту-mro-control-zero-f7" tabindex="-1">Контролер польоту mRo Control Zero F7 <a class="header-anchor" href="#контролер-польоту-mro-control-zero-f7" aria-label="Permalink to &quot;Контролер польоту mRo Control Zero F7&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <em>mRo Control Zero F7<sup>®</sup></em> is a new flight controller from mRo.</p><p><img src="'+e+'" alt="mRo Control Zero F7"></p><p>Це безкомпромісний трикомпонентний комерційний контролер польоту з трьома IMU. Це включає 8 виходів PWM (здатні до DShot), 3x IMU, 1x Магнітомер, 1x Датчик атмосферного тиску (Висотомір), 6x UART та SD-карту, все це зберігається на PCB розміром 32 мм x 20 мм. PWM-сигнали здійснюють двосторонню комунікацію, захищені від EMI і переносяться до рівнів логіки 5В. Усі елементи доступні за допомогою передніх та задніх роз&#39;ємів Molex PicoClasp з 30 контактами. Міцний пластиковий корпус, конформне покриття плати та опціональне калібрування температури включені.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="основні-характеристики" tabindex="-1">Основні характеристики <a class="header-anchor" href="#основні-характеристики" aria-label="Permalink to &quot;Основні характеристики&quot;">​</a></h2><ul><li><p>Мікропроцесор:</p><ul><li>32-bit STM32F777 Cortex<sup>®</sup> M4 core with FPU rev. 3</li><li>216 MHz/512 KB RAM/2 MB Flash</li><li>F-RAM Cypress MF25V02-G 256-Кбіт неволатильна пам&#39;ять (Flash-пам&#39;ять, яка працює так же швидко, як RAM)</li></ul></li><li><p>Датчики:</p><ul><li><a href="https://www.bosch-sensortec.com/bst/products/all_products/bmi088_1" target="_blank" rel="noreferrer">Bosch BMI088</a> 3-axis accelerometer/gyroscope (internally vibration dampened)</li><li><a href="https://www.invensense.com/products/motion-tracking/6-axis/icm-20602/" target="_blank" rel="noreferrer">Invensense ICM-20602</a> 3-axis accelerometer/gyroscope</li><li><a href="https://www.invensense.com/products/motion-tracking/9-axis/icm-20948/" target="_blank" rel="noreferrer">Invensense ICM-20948</a> 3-axis accelerometer/gyroscope/magnetometer</li><li><a href="https://www.infineon.com/cms/en/product/sensor/pressure-sensors/pressure-sensors-for-iot/dps310/" target="_blank" rel="noreferrer">Infineon DPS310 barometer</a> (So smooth and NO more light sensitivity)</li></ul></li><li><p>Інтерфейси:</p><ul><li>6x UART (загальна кількість послідовних портів), 3x з HW керуванням потоком, 1x FRSky Telemetry (типи D або Х), 1x Консоль та 1x GPS+I2C</li><li>8x Виходи PWM (всі здатні до DShot)</li><li>1x CAN</li><li>1x I2C</li><li>1x SPI</li><li>Spektrum DSM / DSM2 / DSM-X® Satellite compatible вхід та біндинг</li><li>Futaba S.BUS® &amp; S.BUS2® compatible input</li><li>Вивід телеметрії порту FRSky</li><li>Graupner SUMD</li><li>Yuneec ST24</li><li>PPM сигнал входу суми</li><li>1x JTAG (Роз&#39;єм TC2030)</li><li>1x RSSI (PWM чи вольтаж) вхід</li><li>LED триколор</li></ul></li><li><p>Вага та розміри (без кейса):</p><ul><li>Вага: 5.3г (0.19oz)</li><li>Ширина: 20 мм (0.79&quot;)</li><li>Довжина: 32 мм (1,26&quot;)</li></ul></li><li><p>Система живлення:</p><ul><li>3x Низькозатратний лінійний регулятор напруги Ultra Low Noise LDO</li></ul></li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><ul><li><a href="https://store.mrobotics.io/mRo-Control-Zero-F7-p/mro-ctrl-zero-f7.htm" target="_blank" rel="noreferrer">mRo Control Zero</a></li></ul><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make mro_ctrl-zero-f7</span></span></code></pre></div><h2 id="відладочні-порти" tabindex="-1">Відладочні порти <a class="header-anchor" href="#відладочні-порти" aria-label="Permalink to &quot;Відладочні порти&quot;">​</a></h2><h3 id="порт-консолі" tabindex="-1">Порт Консолі <a class="header-anchor" href="#порт-консолі" aria-label="Permalink to &quot;Порт Консолі&quot;">​</a></h3><p>The <a href="./../debug/system_console.html">PX4 System Console</a> runs on <code>USART7</code> using the pins listed below. This is a standard serial pinout, designed to connect to a <a href="https://www.digikey.com/en/products/detail/TTL-232R-3V3/768-1015-ND/1836393" target="_blank" rel="noreferrer">3.3V FTDI</a> cable (5V tolerant).</p><table tabindex="0"><thead><tr><th>mRo control zero f7</th><th></th><th>FTDI</th><th></th></tr></thead><tbody><tr><td>17</td><td>USART7 Tx</td><td>5</td><td>FTDI RX (yellow)</td></tr><tr><td>19</td><td>USART7 Rx</td><td>4</td><td>FTDI TX (orange)</td></tr><tr><td>6</td><td>USART21 GND</td><td>1</td><td>FTDI GND (black)</td></tr></tbody></table><h3 id="swd-port" tabindex="-1">SWD Port <a class="header-anchor" href="#swd-port" aria-label="Permalink to &quot;SWD Port&quot;">​</a></h3><p>The <a href="./../debug/swd_debug.html">SWD port</a> (JTAG) for FMU debugging is a TC2030 debug connector, as shown below.</p><p><img src="'+r+'" alt="mro swd port"></p><p>You can use the <a href="https://www.tag-connect.com/" target="_blank" rel="noreferrer">Tag Connect</a> cable <a href="https://www.tag-connect.com/product/tc2030-idc-nl" target="_blank" rel="noreferrer">TC2030 IDC NL</a> below (with associated <a href="https://www.tag-connect.com/product/tc2030-clip-retaining-clip-board-for-tc2030-nl-cables" target="_blank" rel="noreferrer">retaining clip</a>) to attach to either a BlackMagic probe or a ST-LINK V2 debugger.</p><p><img src="'+o+'" alt="tc2030 idc nl cable"></p><p>There is also an <a href="https://www.tag-connect.com/product/arm20-ctx-20-pin-to-tc2030-idc-adapter-for-cortex" target="_blank" rel="noreferrer">ARM20-CTX 20-Pin to TC2030-IDC adapter</a> that can be used with other debug probes.</p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p><img src="'+a+'" alt="mRo Control Zero F7"></p><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART2</td><td>/dev/ttyS0</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS1</td><td>TELEM2 (керування потоком)</td></tr><tr><td>UART4</td><td>/dev/ttyS2</td><td>GPS1</td></tr><tr><td>USART6</td><td>/dev/ttyS3</td><td>Flex порт (можна налаштувати як SPI або UART із Flow Control).</td></tr><tr><td>UART7</td><td>/dev/ttyS4</td><td>CONSOLE</td></tr><tr><td>UART8</td><td>/dev/ttyS5</td><td>Вільний послідовний порт (зазвичай для телеметрії FrSky)</td></tr></tbody></table><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://mrobotics.io/introducing-the-new-mro-control-zero-autopilot/" target="_blank" rel="noreferrer">Introducing the new mRo Control Zero Autopilot</a> (blog)</li><li><a href="https://mrobotics.io/mrocontrolzero/" target="_blank" rel="noreferrer">Quick Start Guide</a></li></ul>',30)]))}const w=l(d,[["render",c]]);export{_ as __pageData,w as default};
