import{_ as a}from"./chunks/mro_pixhawk.TeT2w4oe.js";import{_ as e,c as i,a8 as r,o as l}from"./chunks/framework.BDnHobkS.js";const k=JSON.parse('{"title":"mRo Pixhawk Flight Controller (Pixhawk 1)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/mro_pixhawk.md","filePath":"uk/flight_controller/mro_pixhawk.md"}'),o={name:"uk/flight_controller/mro_pixhawk.md"};function s(h,t,n,d,p,c){return l(),i("div",null,t[0]||(t[0]=[r('<h1 id="mro-pixhawk-flight-controller-pixhawk-1" tabindex="-1">mRo Pixhawk Flight Controller (Pixhawk 1) <a class="header-anchor" href="#mro-pixhawk-flight-controller-pixhawk-1" aria-label="Permalink to &quot;mRo Pixhawk Flight Controller (Pixhawk 1)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <em>mRo Pixhawk<sup>®</sup></em> is a hardware compatible version of the original <a href="./../flight_controller/pixhawk.html">Pixhawk 1</a>. It runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The controller can be used as a drop-in replacement for the 3DR<sup>&amp;reg;</sup> <a href="./../flight_controller/pixhawk.html">Pixhawk 1</a>. The main difference is that it is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design, which corrects a bug that limited the original Pixhawk 1 to 1MB of flash.</p></div><p><img src="'+a+'" alt="mRo Pixhawk Image"></p><p>Assembly/setup instructions for use with PX4 are provided here: <a href="./../assembly/quick_start_pixhawk.html">Pixhawk Wiring Quickstart</a></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="основні-характеристики" tabindex="-1">Основні характеристики <a class="header-anchor" href="#основні-характеристики" aria-label="Permalink to &quot;Основні характеристики&quot;">​</a></h2><ul><li><p>Мікропроцесор:</p><ul><li>32-bit STM32F427 Cortex<sup>®</sup> M4 core з FPU</li><li>168 MHz/256 KB RAM/2 MB Flash</li><li>32 bit STM32F103 відмовостійкий копроцесор</li><li>24 MHz/8 KB RAM/64 KB Flash</li></ul></li><li><p>Датчики:</p><ul><li>ST Micro L3GD20 3-axis 16-бітний гіроскоп</li><li>ST Micro LSM303D 3-вісний 14-бітний акселерометр / магнітометр</li><li>Invensense<sup>®</sup> MPU 6000 3-вісний акселерометр/гіроскоп</li><li>MEAS MS5611 барометр</li></ul></li><li><p>Інтерфейси:</p><ul><li>5x UART (послідовні порти), один високої потужності, 2x з контролем потоку ГВП</li><li>2x CAN</li><li>Вхід, сумісний з приймачами Spektrum DSM / DSM2 / DSM-X® Satellite до DX8 (DX9 та вище не підтримуються)</li><li>Futaba<sup>®</sup> S.BUS сумісний вхід та вихід</li><li>Сигнал суми PPM</li><li>Вхід RSSI (ШІМ або напруга)</li><li>I2C</li><li>SPI</li><li>3.3 та 6.6V ADC входи</li><li>Зовнішній порт microUSB</li></ul></li><li><p>Система живлення:</p><ul><li>Ідеальний діодний контролер з автоматичним перемиканням на резервне живлення</li><li>Сервопривід високої потужності (7 В) і готовий до великого струму</li><li>Усі периферійні виводи захищені від перевантаження по струму, усі входи захищені від електростатичного розряду</li></ul></li><li><p>Вага та розміри:</p><ul><li>Вага: 38г (1,31 унції)</li><li>Ширина: 50 мм (1.96&quot;)</li><li>Товщина: 15.5 мм (.613&quot;)</li><li>Довжина: 81.5 мм (3.21&quot;)</li></ul></li></ul><h2 id="доступність" tabindex="-1">Доступність <a class="header-anchor" href="#доступність" aria-label="Permalink to &quot;Доступність&quot;">​</a></h2><ul><li><a href="https://store.mrobotics.io/Genuine-PixHawk-1-Barebones-p/mro-pixhawk1-bb-mr.htm" target="_blank" rel="noreferrer">Bare Bones</a> - Just the board (useful as a 3DR Pixhawk replacement)</li><li><a href="https://store.mrobotics.io/Genuine-PixHawk-Flight-Controller-p/mro-pixhawk1-minkit-mr.htm" target="_blank" rel="noreferrer">mRo Pixhawk 2.4.6 Essential Kit!</a> - Everything except for telemetry radios</li><li><a href="https://store.mrobotics.io/product-p/mro-pixhawk1-fullkit-mr.htm" target="_blank" rel="noreferrer">mRo Pixhawk 2.4.6 Cool Kit! (Limited edition)</a> - Everything you need including telemetry radios</li></ul><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v3_default</span></span></code></pre></div><h2 id="відладочні-порти" tabindex="-1">Відладочні порти <a class="header-anchor" href="#відладочні-порти" aria-label="Permalink to &quot;Відладочні порти&quot;">​</a></h2><p>See <a href="./../flight_controller/pixhawk.html#debug-ports">3DR Pixhawk 1 &gt; Debug Ports</a></p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p>See <a href="./../flight_controller/pixhawk.html#pinouts">3DR Pixhawk 1 &gt; Pinouts</a></p><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (керування потоком)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table><h2 id="креслення" tabindex="-1">Креслення <a class="header-anchor" href="#креслення" aria-label="Permalink to &quot;Креслення&quot;">​</a></h2><p>The board is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design.</p><ul><li><a href="https://github.com/PX4/Hardware/raw/master/FMUv3_REV_D/Schematic%20Print/Schematic%20Prints.PDF" target="_blank" rel="noreferrer">FMUv3 schematic</a> -- Schematic and layout</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>As a CC-BY-SA 3.0 licensed Open Hardware design, all schematics and design files are <a href="https://github.com/PX4/Hardware" target="_blank" rel="noreferrer">available</a>.</p></div>',25)]))}const b=e(o,[["render",s]]);export{k as __pageData,b as default};
