import{_ as e,a,b as r}from"./chunks/mro_x21_wiring.0qtqNlsg.js";import{_ as i,c as o,a8 as l,o as s}from"./chunks/framework.BDnHobkS.js";const _=JSON.parse('{"title":"mRo-X2.1 Autopilot","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/mro_x2.1.md","filePath":"uk/flight_controller/mro_x2.1.md"}'),d={name:"uk/flight_controller/mro_x2.1.md"};function n(p,t,h,u,c,m){return s(),o("div",null,t[0]||(t[0]=[l('<h1 id="mro-x2-1-autopilot" tabindex="-1">mRo-X2.1 Autopilot <a class="header-anchor" href="#mro-x2-1-autopilot" aria-label="Permalink to &quot;mRo-X2.1 Autopilot&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://store.mrobotics.io/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <a href="http://www.mRobotics.io/" target="_blank" rel="noreferrer">mRo-X2.1 autopilot</a> is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk<sup>®</sup>-project</a> <strong>FMUv2</strong> open hardware design. It runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><p><img src="'+e+'" alt="mRo X2.1"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Main System-on-Chip: <a href="http://www.st.com/web/en/catalog/mmc/FM141/SC1169/SS1577/LN1789" target="_blank" rel="noreferrer">STM32F427</a><ul><li>CPU: STM32F427VIT6 ARM<sup>®</sup> мікроконтроллер - Revision 3</li><li>ІО: мікроконтролер STM32F100C8T6 ARM<sup>®</sup></li></ul></li><li>Датчики: <ul><li>Invensense<sup>®</sup> MPU9250 9DOF</li><li>Invensense ICM-20602 6DOF</li><li>MEAS MS5611 барометр</li></ul></li><li>Розміри/Вага <ul><li>Size: 36mm x 50mm (Can be ordered with vertical, horizontal or no headers installed)</li><li>Точки кріплення: 30,5 мм х 30,5 мм діаметр 3,2 мм</li><li>Вага: 10.9g</li></ul></li></ul><p>Діаграма нижче надає порівняльний аналіз з Pixhawk 1. mRo має практично ідентичне апаратне забезпечення й підключення, але має значно менший слід. Основні відмінності - це оновлені датчики та Rev 3 FMU.</p><p><img src="'+a+'" alt="Mro Pixhawk 1 vs X2.1 comparison"></p><h2 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h2><ul><li>2.54 мм заголовки:</li><li>GPS (UART4) з I2C</li><li>CAN шина</li><li>Вхід RC</li><li>PPM вхід</li><li>Вхідний спектр</li><li>RSSI вхід</li><li>вхід SBUS</li><li>sBus вихід</li><li>Вхід живлення</li><li>Вихід зумера</li><li>Вихід світлодіода</li><li>8 x Виводи сервоприводів</li><li>6 x Aux outputs</li><li>Позабортовий конектор microUSB</li><li>Kill Pin output <em>(Currently not supported by firmware)</em></li><li>AirSpeed Sensor</li><li>USART2 (Telem 1)</li><li>USART3 (Telem 2)</li><li>UART7 (Console)</li><li>UART8 (OSD)</li></ul><h2 id="проблема-px4-bootloader" tabindex="-1">Проблема PX4 BootLoader <a class="header-anchor" href="#проблема-px4-bootloader" aria-label="Permalink to &quot;Проблема PX4 BootLoader&quot;">​</a></h2><p>За замовчуванням mRo X2.1 може бути попередньо налаштований на ArduPilot<sup>®</sup>, а не на PX4. Це можна побачити під час оновлення прошивки, коли плата визнається як FMUv2 замість X2.1.</p><p>In this case you must update the BootLoader using <a href="https://github.com/PX4/PX4-user_guide/raw/main/assets/hardware/BL_Update_X21.zip" target="_blank" rel="noreferrer">BL_Update_X21.zip</a>. Якщо це виправлення не буде зроблено, ваша пеленга буде відображена неправильно і надмірний інерціальний модуль не буде виявлено.</p><p>Основні кроки:</p><ol><li>Download and extract <a href="https://github.com/PX4/PX4-user_guide/raw/main/assets/hardware/BL_Update_X21.zip" target="_blank" rel="noreferrer">BL_Update_X21.zip</a>.</li><li>Find the folder <em>BL_Update_X21</em>. This contains a <strong>bin</strong> file and a subfolder named <strong>/etc</strong> containing an <strong>rc.txt</strong> file</li><li>Скопіюйте ці файли на кореневий каталог вашої micro SD-карти та вставте її в mRO x2.1</li><li>Увімкніть mRO x2.1. Зачекайте, доки він завантажиться, а потім перезавантажте 1 раз.</li></ol><h2 id="доступність" tabindex="-1">Доступність <a class="header-anchor" href="#доступність" aria-label="Permalink to &quot;Доступність&quot;">​</a></h2><p>This product can be ordered at the <a href="https://store.mrobotics.io/mRo-X2-1-Rev-2-p/m10021a.htm" target="_blank" rel="noreferrer">mRobotics<sup>®</sup> Store</a>.</p><h2 id="посібник-з-підключення" tabindex="-1">Посібник з підключення <a class="header-anchor" href="#посібник-з-підключення" aria-label="Permalink to &quot;Посібник з підключення&quot;">​</a></h2><p><img src="'+r+'" alt="mRoX2.1Wiring"></p><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make mro_x21_default</span></span></code></pre></div><h2 id="креслення" tabindex="-1">Креслення <a class="header-anchor" href="#креслення" aria-label="Permalink to &quot;Креслення&quot;">​</a></h2><p>The board is documented on the mRo hardware repo: <a href="https://github.com/mRoboticsIO/Hardware/blob/master/X2.1/Docs/x21_V2_schematic.pdf" target="_blank" rel="noreferrer">x21_V2_schematic.pdf</a>.</p><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>SERIAL1</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>GPS/I2C</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>PX4IO</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>SERIAL5 CONSOLE</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>SERIAL4</td></tr></tbody></table>',28)]))}const g=i(d,[["render",n]]);export{_ as __pageData,g as default};
