import{_ as r}from"./chunks/hardware-pixfalcon.uMTjHTOS.js";import{_ as l,c as o,j as t,a as i,G as s,a8 as n,B as d,o as h}from"./chunks/framework.BDnHobkS.js";const y=JSON.parse('{"title":"Контролер польоту Pixfalcon (знятий з виробництва)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixfalcon.md","filePath":"uk/flight_controller/pixfalcon.md"}'),p={name:"uk/flight_controller/pixfalcon.md"};function c(u,e,m,b,f,g){const a=d("Badge");return h(),o("div",null,[e[0]||(e[0]=t("h1",{id:"контролер-польоту-pixfalcon-знятии-з-виробництва",tabindex:"-1"},[i("Контролер польоту Pixfalcon (знятий з виробництва) "),t("a",{class:"header-anchor",href:"#контролер-польоту-pixfalcon-знятии-з-виробництва","aria-label":'Permalink to "Контролер польоту Pixfalcon (знятий з виробництва)"'},"​")],-1)),s(a,{type:"info",text:"Discontinued"}),e[1]||(e[1]=n('<div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>This flight controller has been <a href="./../flight_controller/autopilot_experimental.html">discontinued</a> and is no longer commercially available.</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The Pixfalcon autopilot (designed by <a href="https://holybro.com/" target="_blank" rel="noreferrer">Holybro<sup>®</sup></a>) is binary-compatible (FMUv2) derivative of the <a href="./../flight_controller/pixhawk.html">Pixhawk 1</a> design that has been optimized for space-constrained applications such as FPV racers. Він має менше входів-виходів, що дозволяє зменшити розмір.</p><p><img src="'+r+'" alt="Pixfalcon hero image"></p><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Main System-on-Chip: <a href="http://www.st.com/web/en/catalog/mmc/FM141/SC1169/SS1577/LN1789" target="_blank" rel="noreferrer">STM32F427</a><ul><li>CPU: 180 MHz ARM<sup>®</sup> Cortex<sup>®</sup> M4 з одинарною точністю FPU</li><li>RAM: 256 KB SRAM (L1)</li></ul></li><li>Failsafe System-on-Chip: STM32F100 <ul><li>CPU: 24 MHz ARM Cortex M3</li><li>RAM: 8 KB SRAM</li></ul></li><li>GPS: u-blox<sup>®</sup> M8 (в комплекті)</li></ul><h3 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h3><ul><li>1x I2C</li><li>2x UART (один для телеметрії / OSD, без контролю потоку)</li><li>8x ШІМ з ручним управлінням</li><li>S.BUS / PPM вхід</li></ul><h2 id="наявність" tabindex="-1">Наявність: <a class="header-anchor" href="#наявність" aria-label="Permalink to &quot;Наявність:&quot;">​</a></h2><p>From distributor <a href="https://hobbyking.com/en_us/pixfalcon-micro-px4-autopilot-plus-micro-m8n-gps-and-mega-pbd-power-module.html" target="_blank" rel="noreferrer">Hobbyking<sup>®</sup></a></p><p>Опціональне обладнання:</p><ul><li>Optical flow: PX4 Flow unit from manufacturer <a href="https://holybro.com/products/px4flow" target="_blank" rel="noreferrer">Holybro</a></li><li>Digital Airspeed sensor from manufacturer <a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Holybro</a> or distributor <a href="https://hobbyking.com/en_us/hkpilot-32-digital-air-speed-sensor-and-pitot-tube-set.html" target="_blank" rel="noreferrer">Hobbyking</a></li><li>Екранний дисплей з вбудованою телеметрією: <ul><li><a href="https://hobbyking.com/en_us/micro-hkpilot-telemetry-radio-module-with-on-screen-display-osd-unit-433mhz.html" target="_blank" rel="noreferrer">Hobbyking OSD + EU Telemetry (433 MHz)</a></li></ul></li><li>Pure Telemetry опції: <ul><li><a href="https://hobbyking.com/en_us/apm-pixhawk-wireless-wifi-radio-module.html" target="_blank" rel="noreferrer">Hobbyking Wifi Telemetry</a></li><li><a href="./../telemetry/sik_radio.html">SIK Radios</a></li></ul></li></ul><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v3_default</span></span></code></pre></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>This board does not have a debug port (i.e it does not have a port for accessing the <a href="./../debug/system_console.html">System Console</a> or the <a href="./../debug/swd_debug.html">SWD interface</a> (JTAG).</p><p>Розробники повинні будуть припаяти дроти до підключених площин для SWD, а також до STM32F4 (IC) TX і RX, щоб отримати консоль.</p><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO Debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (без контролю потоку)</td></tr><tr><td>UART4</td><td>/dev/ttyS2</td><td>GPS</td></tr></tbody></table>',21))])}const _=l(p,[["render",c]]);export{y as __pageData,_ as default};
