import{_ as a}from"./chunks/pixhack_v3_157_large_default.BolSQN88.js";import{_ as e,c as i,a8 as l,o as r}from"./chunks/framework.BDnHobkS.js";const f=JSON.parse('{"title":"Pixhack V3","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhack_v3.md","filePath":"uk/flight_controller/pixhack_v3.md"}'),o={name:"uk/flight_controller/pixhack_v3.md"};function d(n,t,s,h,c,p){return r(),i("div",null,t[0]||(t[0]=[l('<h1 id="pixhack-v3" tabindex="-1">Pixhack V3 <a class="header-anchor" href="#pixhack-v3" aria-label="Permalink to &quot;Pixhack V3&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://store.cuav.net/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The CUAV <em>Pixhack V3</em> flight controller board is a flexible autopilot intended primarily for manufacturers of commercial systems.</p><p>The board is a variant of the SOLO Pixhawk<sup>®</sup> 2 (PH2) flight controller, which is in turn based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv3</strong> open hardware design. It runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS, and is fully compatible with both PX4 or ArduPilot<sup>®</sup> (APM) firmware.</p><p><em>Pixhack V3</em> has significant improvements with respect to the original design, including better interface layout and the addition of vibration damping and a thermostat system.</p><p><img src="'+a+'" alt="Pixhack v3"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This flight controller is <a href="./../flight_controller/autopilot_manufacturer_supported.html">manufacturer supported</a>.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Мікропроцесор: <ul><li>STM32F427</li><li>STM32F100 (відмовостійкий співпроцесор)</li></ul></li><li>Датчики: <ul><li>Акселерометри (3): LS303D, MPU6000, MPU9250/hmc5983</li><li>Гіроскопи (3): L3GD20, MPU6000, MPU9250</li><li>Компаси (2): LS303D, MPU9250</li><li>Барометри (2): MS5611 X2</li></ul></li><li>Інтерфейси: <ul><li>MAVLink UART (2)</li><li>GPS UART (2)</li><li>DEBUG UART (1)</li><li>RC IN (для PPM, SBUS, DSM/DSM2)</li><li>RSSI IN: PWM OR 3.3ADC</li><li>I2C (2)</li><li>CAN BUS (1)</li><li>ADC IN: 3.3В X1 , 6.6В X1</li><li>PWM OUT: 8 PWM IO + 4 IO</li></ul></li><li>Система живлення: <ul><li>PM POWER IN: 4.5 ~ 5.5 В</li><li>USB POWER IN: 5.0 В +- 0.25В</li></ul></li><li>Вага та розміри: <ul><li>Вага: 63г</li><li>Ширина: 68мм</li><li>Товщина: 17мм</li><li>Довжина: 44мм</li></ul></li><li>Інші характеристики: <ul><li>Температура роботи: -20 ~ 60°C</li></ul></li></ul><h2 id="доступність" tabindex="-1">Доступність <a class="header-anchor" href="#доступність" aria-label="Permalink to &quot;Доступність&quot;">​</a></h2><p>Плату можна придбати з:</p><ul><li><a href="http://store.cuav.net/index.php?id_product=8&amp;id_product_attribute=0&amp;rewrite=pixhack-v3-autopilot&amp;controller=product&amp;id_lang=3" target="_blank" rel="noreferrer">store.cuav.net</a></li><li><a href="https://leixun.aliexpress.com/store" target="_blank" rel="noreferrer">leixun.aliexpress.com/store</a></li></ul><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v3_default</span></span></code></pre></div><h2 id="розпіновки-та-схеми" tabindex="-1">Розпіновки та схеми <a class="header-anchor" href="#розпіновки-та-схеми" aria-label="Permalink to &quot;Розпіновки та схеми&quot;">​</a></h2><ul><li><a href="http://doc.cuav.net/flight-controller/pixhack/en/pixhack-v3.html" target="_blank" rel="noreferrer">Documentation/wiring guides</a></li></ul><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>IO debug</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (керування потоком)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',20)]))}const b=e(o,[["render",d]]);export{f as __pageData,b as default};
