import{_ as r,a as e}from"./chunks/pixhawk3_pro_debug_ports.Cr0unQys.js";import{_ as a,c as o,a8 as i,o as d}from"./chunks/framework.BDnHobkS.js";const b=JSON.parse('{"title":"Pixhawk 3 Pro (Знято з виробництва)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk3_pro.md","filePath":"uk/flight_controller/pixhawk3_pro.md"}'),l={name:"uk/flight_controller/pixhawk3_pro.md"};function s(h,t,n,p,c,u){return d(),o("div",null,t[0]||(t[0]=[i('<h1 id="pixhawk-3-pro-знято-з-виробництва" tabindex="-1">Pixhawk 3 Pro (Знято з виробництва) <a class="header-anchor" href="#pixhawk-3-pro-знято-з-виробництва" aria-label="Permalink to &quot;Pixhawk 3 Pro (Знято з виробництва)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://store-drotek.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>Pixhawk<sup>®</sup> 3 Pro базується на апаратному дизайні FMUv4 (Pixracer) з деякими оновленнями та додатковими функціями. The board was designed by <a href="https://drotek.com" target="_blank" rel="noreferrer">Drotek<sup>®</sup></a> and PX4.</p><p><img src="'+r+'" alt="Pixhawk 3 Pro hero image"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The main hardware documentation is here: <a href="https://drotek.gitbook.io/pixhawk-3-pro/hardware" target="_blank" rel="noreferrer">https://drotek.gitbook.io/pixhawk-3-pro/hardware</a></p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Microcontroller: <strong>STM32F469</strong>; Flash size is <strong>2MiB</strong>, RAM size is <strong>384KiB</strong></li><li><strong>ICM-20608-G</strong> gyro / accelerometer</li><li><strong>MPU-9250</strong> gyro / accelerometer / magnetometer</li><li><strong>LIS3MDL</strong> compass</li><li>Датчики, підключені через дві шини SPI (одна високочастотна й одна малошумна шина)</li><li>Два шини I2C</li><li>Два CAN шини</li><li>Показники напруги / батареї з двох блоків живлення</li><li>FrSky<sup>®</sup> інвертор</li><li>8 Main + 6 AUX виводів PWM (окремий IO чіп, PX4IO)</li><li>microSD (логування)</li><li>S.BUS / Spektrum / SUMD / PPM вхід</li><li>JST GH роз&#39;єми: ті самі роз&#39;єми та розводка, що й у Pixracer</li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>From <a href="https://store.drotek.com/" target="_blank" rel="noreferrer">Drotek store</a> (EU) :</p><ul><li><a href="https://store.drotek.com/autopilots/844-pixhawk-3-pro-pack.html" target="_blank" rel="noreferrer">Pixhawk 3 Pro (Pack)</a></li><li><a href="https://store.drotek.com/autopilots/821-pixhawk-pro-autopilot-8944595120557.html" target="_blank" rel="noreferrer">Pixhawk 3 Pro</a></li></ul><p>From <a href="https://www.readymaderc.com" target="_blank" rel="noreferrer">readymaderc</a> (USA) :</p><ul><li><a href="https://www.readymaderc.com/products/details/pixhawk-3-pro-flight-controller" target="_blank" rel="noreferrer">Pixhawk 3 Pro</a></li></ul><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v4pro_default</span></span></code></pre></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>Плата має порти FMU та IO для відладки, як показано нижче.</p><p><img src="'+e+'" alt="Debug Ports"></p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-mini">Pixhawk Debug Mini</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> (JST SM06B connector).</p><table tabindex="0"><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td>VCC TARGET SHIFT</td><td>+3.3V</td></tr><tr><td>2 (blk)</td><td>CONSOLE TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>CONSOLE RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td>SWDIO</td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td>SWCLK</td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td>GND</td><td>GND</td></tr></tbody></table><p>Інформацію про підключення та використання цього порту див:</p><ul><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li><li><a href="./../debug/system_console.html#pixhawk_debug_port">PX4 System Console</a> (Note, the FMU console maps to UART7).</li></ul><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>WiFi</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (керування потоком)</td></tr><tr><td>UART4</td><td></td><td></td></tr><tr><td>UART7</td><td>CONSOLE</td><td></td></tr><tr><td>UART8</td><td>SERIAL4</td><td></td></tr></tbody></table>',26)]))}const g=a(l,[["render",s]]);export{b as __pageData,g as default};
