import{_ as e,a,b as r,c as i}from"./chunks/pixhawk4_debug_port.BeXDyvwt.js";import{_ as o}from"./chunks/pixhawk4_logo_view.FppYDFPG.js";import{_ as l,c as s,a8 as n,o as h}from"./chunks/framework.BDnHobkS.js";const x=JSON.parse('{"title":"Holybro Pixhawk 4","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk4.md","filePath":"uk/flight_controller/pixhawk4.md"}'),d={name:"uk/flight_controller/pixhawk4.md"};function p(c,t,u,g,m,b){return h(),s("div",null,t[0]||(t[0]=[n('<h1 id="holybro-pixhawk-4" tabindex="-1">Holybro Pixhawk 4 <a class="header-anchor" href="#holybro-pixhawk-4" aria-label="Permalink to &quot;Holybro Pixhawk 4&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><em>Pixhawk 4</em><sup>®</sup> is an advanced autopilot designed and made in collaboration with Holybro<sup>®</sup> and the PX4 team. Він оптимізований для роботи з PX4 v1.7 та пізніших версій, і підходить для академічних та комерційних розробників.</p><p>It is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk-project</a> <strong>FMUv5</strong> open hardware design and runs PX4 on the <a href="https://nuttx.apache.org/" target="_blank" rel="noreferrer">NuttX</a> OS.</p><p><img src="'+e+'" width="200px" title="Pixhawk4 Upright Image"> <img src="'+o+'" width="420px" title="Pixhawk4 Image"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Головний FMU процесор: STM32F765 <ul><li>32 Bit Arm® Cortex®-M7, 216MHz, 2MB memory, 512KB RAM</li></ul></li><li>IO Processor: STM32F100 <ul><li>32 Bit Arm®️ Cortex®️-M3, 24MHz, 8KB SRAM</li></ul></li><li>Бортові сенсори: <ul><li>Акселератор/гіроскоп: ICM-20689</li><li>Accel/Gyro: BMI055 або ICM20602</li><li>Магнітометр: IST8310</li><li>Барометр: MS5611</li></ul></li><li>GPS: u-blox Neo-M8N GPS/ГЛОНАСС приймач; інтегрований магнетометр IST8310</li><li>Інтерфейси: <ul><li>8-16 PWM виходи (8 із IO, 8 із FMU)</li><li>3 виділених PWM/Capture входи на FMU</li><li>Виділений R/C вхід для CPPM</li><li>Виділений R/C вхід для Spektrum / DSM та S.Bus з аналоговим / PWM RSSI входом</li><li>Спеціалізований S.BUS вивід сервоприводу</li><li>5 загальних послідовних портів</li><li>3 I2C порти</li><li>4 шини SPI</li><li>До 2 CANBus для подвійного CAN з послідовним ESC</li><li>Аналогові входи для напруги / струму з 2 батарей</li></ul></li><li>Система живлення: <ul><li>Вивід енергомодуля 4.9~5.5 В</li><li>Вхід USB Power: 4.75~5.25V</li><li>Вхід сервоприводу: 0~36V</li></ul></li><li>Вага та розміри: <ul><li>Вага: 15.8g</li><li>Розміри: 44x84x12мм</li></ul></li><li>Інші характеристики: <ul><li>Температура роботи: -40 ~ 85°c</li></ul></li></ul><p>Additional information can be found in the <a href="https://github.com/PX4/PX4-user_guide/raw/main/assets/flight_controller/pixhawk4/pixhawk4_technical_data_sheet.pdf" target="_blank" rel="noreferrer">Pixhawk 4 Technical Data Sheet</a>.</p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Order from <a href="https://holybro.com/products/pixhawk-4" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="з-єднання" tabindex="-1">З’єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З’єднання&quot;">​</a></h2><p><img src="'+a+'" alt="Pixhawk 4 connectors"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>The <strong>DSM/SBUS RC</strong> and <strong>PPM RC</strong> ports are for RC receivers only. Вони працюють на електроживленні! НІКОЛИ не підключайте до нього жодних сервоприводів, джерел живлення або батарей (або до будь-якого підключеного приймача).</p></div><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p>Download <em>Pixhawk 4</em> pinouts from <a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Pixhawk4-Pinouts.pdf" target="_blank" rel="noreferrer">here</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Connector pin assignments are left to right (i.e. Pin 1 - це найлівіший контакт). The exception is the <a href="#debug_port">debug port(s)</a> (pin 1 is the right-most, as shown below).</p></div><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>UART1</td><td>/dev/ttyS0</td><td>GPS</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM1 (керування потоком)</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>TELEM2 (керування потоком)</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>TELEM4</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>RC SBUS</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>Debug Console</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>PX4IO</td></tr></tbody></table><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><img src="'+r+'" alt="Pixhawk 4 Dimensions"></p><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>Pixhawk 4</em> can be triple-redundant on the power supply if three power sources are supplied. The three power rails are: <strong>POWER1</strong>, <strong>POWER2</strong> and <strong>USB</strong>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The output power rails <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 36V) do not power the flight controller board (and are not powered by it). You must supply power to one of <strong>POWER1</strong>, <strong>POWER2</strong> or <strong>USB</strong> or the board will be unpowered.</p></div><p><strong>Normal Operation Maximum Ratings</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (4.9V to 5.5V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>За таких умов система не буде витрачати жодної потужності (не буде працювати), але залишиться неушкодженою.</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (operational range 4.1V to 5.7V, 0V to 10V undamaged)</li><li><strong>USB</strong> input (operational range 4.1V to 5.7V, 0V to 6V undamaged)</li><li>Servo input: VDD_SERVO pin of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 42V undamaged)</li></ol><h2 id="зборка-інсталяція" tabindex="-1">Зборка/інсталяція <a class="header-anchor" href="#зборка-інсталяція" aria-label="Permalink to &quot;Зборка/інсталяція&quot;">​</a></h2><p>The <a href="./../assembly/quick_start_pixhawk4.html">Pixhawk 4 Wiring Quick Start</a> provides instructions on how to assemble required/important peripherals including GPS, Power Management Board etc.</p><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v5_default</span></span></code></pre></div><p><a id="debug_port"></a></p><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port, while the I/O console and SWD interface can be accessed via <strong>I/O Debug</strong> port. In order to access these ports, the user must remove the <em>Pixhawk 4</em> casing.</p><p><img src="'+i+'" alt="Pixhawk 4 Debug Ports"></p><p>The pinout uses the standard <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk debug connector pinout</a>. Для отримання інформації про з&#39;єднання дивіться:</p><ul><li><a href="./../debug/system_console.html#pixhawk_debug_port">System Console &gt; Pixhawk Debug Port</a></li></ul><h2 id="периферіині-пристроі" tabindex="-1">Периферійні пристрої <a class="header-anchor" href="#периферіині-пристроі" aria-label="Permalink to &quot;Периферійні пристрої&quot;">​</a></h2><ul><li><a href="https://store-drotek.com/793-digital-differential-airspeed-sensor-kit-.html" target="_blank" rel="noreferrer">Digital Airspeed Sensor</a></li><li><a href="./../telemetry/">Telemetry Radio Modules</a></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li></ul><h2 id="підтримувані-платформи-конструкціі" tabindex="-1">Підтримувані платформи / Конструкції <a class="header-anchor" href="#підтримувані-платформи-конструкціі" aria-label="Permalink to &quot;Підтримувані платформи / Конструкції&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа / човен, який може керуватися звичайними RC сервоприводами або сервоприводами Futaba S-Bus. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://github.com/PX4/PX4-user_guide/raw/main/assets/flight_controller/pixhawk4/pixhawk4_technical_data_sheet.pdf" target="_blank" rel="noreferrer">Pixhawk 4 Technical Data Sheet</a></li><li><a href="https://docs.google.com/spreadsheets/d/1-n0__BYDedQrc_2NHqBenG1DNepAgnHpSGglke-QQwY/edit#gid=912976165" target="_blank" rel="noreferrer">FMUv5 reference design pinout</a>.</li><li><a href="./../assembly/quick_start_pixhawk4.html">Pixhawk 4 Wiring QuickStart</a></li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Pixhawk4-Pinouts.pdf" target="_blank" rel="noreferrer">Pixhawk 4 Pinouts</a> (Holybro)</li><li><a href="https://cdn.shopify.com/s/files/1/0604/5905/7341/files/Pixhawk4-quickstartguide.pdf" target="_blank" rel="noreferrer">Pixhawk 4 Quick Start Guide</a> (Holybro)</li></ul>',48)]))}const w=l(d,[["render",p]]);export{x as __pageData,w as default};
