import{_ as e}from"./chunks/pixhawk4mini_iso_1.C9ebUVxh.js";import{_ as a,a as i,b as r}from"./chunks/pixhawk4mini_fmu_debug.DITljfFU.js";import{_ as l,c as o,a8 as n,o as s}from"./chunks/framework.BDnHobkS.js";const k=JSON.parse('{"title":"Holybro Pixhawk 4 Mini (Знято з виробництва)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk4_mini.md","filePath":"uk/flight_controller/pixhawk4_mini.md"}'),d={name:"uk/flight_controller/pixhawk4_mini.md"};function h(c,t,p,u,m,g){return s(),o("div",null,t[0]||(t[0]=[n('<h1 id="holybro-pixhawk-4-mini-знято-з-виробництва" tabindex="-1">Holybro Pixhawk 4 Mini (Знято з виробництва) <a class="header-anchor" href="#holybro-pixhawk-4-mini-знято-з-виробництва" aria-label="Permalink to &quot;Holybro Pixhawk 4 Mini (Знято з виробництва)&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p>The <em>Pixhawk<sup>®</sup> 4 Mini</em> autopilot is designed for engineers and hobbyists who are looking to tap into the power of <em>Pixhawk 4</em> but are working with smaller drones. <em>Pixhawk 4 Mini</em> takes the FMU processor and memory resources from the <em>Pixhawk 4</em> while eliminating interfaces that are normally unused. This allows the <em>Pixhawk 4 Mini</em> to be small enough to fit in a 250mm racer drone.</p><p><em>Pixhawk 4 Mini</em> was designed and developed in collaboration with Holybro<sup>®</sup> and Auterion<sup>®</sup>. It is based on the <a href="https://pixhawk.org/" target="_blank" rel="noreferrer">Pixhawk</a> <strong>FMUv5</strong> design standard and is optimized to run PX4 flight control software.</p><p><img src="'+e+'" alt="Pixhawk4 mini"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="короткии-опис" tabindex="-1">Короткий опис <a class="header-anchor" href="#короткии-опис" aria-label="Permalink to &quot;Короткий опис&quot;">​</a></h2><ul><li>Головний FMU процесор: STM32F765 <ul><li>32 Bit Arm® Cortex®-M7, 216MHz, 2MB memory, 512KB RAM</li></ul></li><li>Бортові сенсори: <ul><li>Акселератор/гіроскоп: ICM-20689</li><li>Accel/Gyro: BMI055 або ICM20602</li><li>Магнітометр: IST8310</li><li>Барометр: MS5611</li></ul></li><li>GPS: u-blox Neo-M8N GPS/ГЛОНАСС приймач; інтегрований магнетометр IST8310</li><li>Інтерфейси: <ul><li>8 PWM виводів</li><li>4 виділених PWM/Capture входи на FMU</li><li>Виділений R/C вхід для CPPM</li><li>Виділений R/C вхід для Spektrum / DSM та S.Bus з аналоговим / PWM RSSI входом</li><li>3 загальних послідовних портів</li><li>2 I2C порти</li><li>3 SPI шини</li><li>1 CAN шина для CAN ESC</li><li>Аналогові входи для напруги / струму з батареї</li><li>2 додаткових аналогових входи</li></ul></li><li>Система живлення: <ul><li>Вхід Power Brick: 4.75~5.5V</li><li>Вхід USB Power: 4.75~5.25V</li><li>Вхід Servo Rail: 0~24V</li><li>Максимальний струм у значенні: 120 A</li></ul></li><li>Вага та розміри: <ul><li>Вага: 37.2g</li><li>Розміри: 38x55x15.5mm</li></ul></li><li>Інші характеристики: <ul><li>Температура роботи: -40 ~ 85°c</li></ul></li></ul><p>Additional information can be found in the <a href="https://github.com/PX4/PX4-user_guide/raw/main/assets/flight_controller/pixhawk4mini/pixhawk4mini_technical_data_sheet.pdf" target="_blank" rel="noreferrer"><em>Pixhawk 4 Mini</em> Technical Data Sheet</a>.</p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Order from <a href="https://holybro.com/collections/autopilot-flight-controllers/products/pixhawk4-mini" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="інтерфеиси" tabindex="-1">Інтерфейси <a class="header-anchor" href="#інтерфеиси" aria-label="Permalink to &quot;Інтерфейси&quot;">​</a></h2><p><img src="'+a+'" alt="Pixhawk 4 Mini interfaces"></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>The <strong>RC IN</strong> and <strong>PPM</strong> ports are for RC receivers only. Вони працюють на електроживленні! НІКОЛИ не підключайте до нього жодних сервоприводів, джерел живлення або батарей (або до будь-якого підключеного приймача).</p></div><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><p>Download <em>Pixhawk 4 Mini</em> pinouts from <a href="https://github.com/PX4/PX4-user_guide/raw/main/assets/flight_controller/pixhawk4mini/pixhawk4mini_pinouts.pdf" target="_blank" rel="noreferrer">here</a>.</p><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><img src="'+i+'" alt="Pixhawk 4 Mini Dimensions"></p><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>Pixhawk 4 Mini</em> can have power supply redundancy — if two power sources are supplied. The power rails are: <strong>POWER</strong> and <strong>USB</strong>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The output power rail of <strong>MAIN OUT</strong> does not power the flight controller board (and is not powered by it). You must <a href="./../assembly/quick_start_pixhawk4_mini.html#power">supply power</a> to one of <strong>POWER</strong> or <strong>USB</strong> or the board will be unpowered.</p></div><p><strong>Normal Operation Maximum Ratings</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWER</strong> (4.75V to 5.5V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>За таких умов система залишиться неушкодженою.</p><ol><li><strong>POWER</strong> input (0V to 6V undamaged)</li><li><strong>USB</strong> input (0V to 6V undamaged)</li><li>Servo input: VDD_SERVO pin of <strong>MAIN OUT</strong> (0V to 24V undamaged)</li></ol><h2 id="зборка-інсталяція" tabindex="-1">Зборка/інсталяція <a class="header-anchor" href="#зборка-інсталяція" aria-label="Permalink to &quot;Зборка/інсталяція&quot;">​</a></h2><p>The <a href="./../assembly/quick_start_pixhawk4_mini.html"><em>Pixhawk 4 Mini</em> Wiring Quick Start</a> provides instructions on how to assemble required/important peripherals including GPS, Power Management Board, etc.</p><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v5_default</span></span></code></pre></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port. In order to access these ports, the user must remove the <em>Pixhawk 4 Mini</em> casing.</p><p><img src="'+r+'" alt="Pixhawk 4 Mini FMU Debug"></p><p>The port has a standard serial pinout and can be connected to a standard FTDI cable (3.3V, but it&#39;s 5V tolerant) or a <a href="https://kb.zubax.com/display/MAINKB/Dronecode+Probe+documentation" target="_blank" rel="noreferrer">Dronecode probe</a>. The pinout uses the standard <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk debug connector</a> pinout. Please refer to the <a href="./../debug/system_console.html">wiring</a> page for details of how to wire up this port.</p><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:center;">UART</th><th style="text-align:center;">Пристрій</th><th style="text-align:center;">Опис параметра QGC</th><th style="text-align:center;">Мітка порту на FC</th></tr></thead><tbody><tr><td style="text-align:center;">UART1</td><td style="text-align:center;">/dev/ttyS0</td><td style="text-align:center;">GPS1</td><td style="text-align:center;">GPS Module</td></tr><tr><td style="text-align:center;">USART2</td><td style="text-align:center;">/dev/ttyS1</td><td style="text-align:center;">TELEM1</td><td style="text-align:center;">TELEM1</td></tr><tr><td style="text-align:center;">USART3</td><td style="text-align:center;">/dev/ttyS2</td><td style="text-align:center;">TELEM2</td><td style="text-align:center;">N/A</td></tr><tr><td style="text-align:center;">UART4</td><td style="text-align:center;">/dev/ttyS3</td><td style="text-align:center;">TELEM/SERIAL4</td><td style="text-align:center;">UART/l2C B</td></tr><tr><td style="text-align:center;">USART6</td><td style="text-align:center;">/dev/ttyS4</td><td style="text-align:center;">N/A</td><td style="text-align:center;">RC IN</td></tr><tr><td style="text-align:center;">UART7</td><td style="text-align:center;">/dev/ttyS5</td><td style="text-align:center;">N/A</td><td style="text-align:center;">Debug</td></tr><tr><td style="text-align:center;">UART8</td><td style="text-align:center;">/dev/ttyS6</td><td style="text-align:center;">N/A</td><td style="text-align:center;">Не підключено (без PX4IO)</td></tr></tbody></table><h2 id="периферіині-пристроі" tabindex="-1">Периферійні пристрої <a class="header-anchor" href="#периферіині-пристроі" aria-label="Permalink to &quot;Периферійні пристрої&quot;">​</a></h2><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Digital Airspeed Sensor</a></li><li><a href="./../telemetry/">Telemetry Radio Modules</a></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li></ul><h2 id="підтримувані-платформи" tabindex="-1">Підтримувані платформи <a class="header-anchor" href="#підтримувані-платформи" aria-label="Permalink to &quot;Підтримувані платформи&quot;">​</a></h2><p>Motors and servos are connected to the <strong>MAIN OUT</strong> ports in the order specified for your vehicle in the <a href="./../airframes/airframe_reference.html">Airframe Reference</a>. Цей довідник містить зіставлення портів виводу до моторів/сервоприводів для всіх підтримуваних повітряних та наземних шасі (якщо ваше шасі не вказане в довіднику, то використовуйте &quot;загальний&quot; планер відповідного типу).</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p><em>Pixhawk 4 Mini</em> does not have AUX ports. Плата не може використовуватися з шасі, яким необхідно більше ніж 8 портів або які використовують AUX порти для моторів або керування. Вона може бути використана для планерів, які використовують AUX для другорядних периферійних пристроїв (наприклад, &quot;feed-through of RC AUX1 channel&quot;).</p></div><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://github.com/PX4/PX4-user_guide/raw/main/assets/flight_controller/pixhawk4mini/pixhawk4mini_technical_data_sheet.pdf" target="_blank" rel="noreferrer"><em>Pixhawk 4 Mini</em> Technical Data Sheet</a></li><li><a href="https://docs.google.com/spreadsheets/d/1-n0__BYDedQrc_2NHqBenG1DNepAgnHpSGglke-QQwY/edit#gid=912976165" target="_blank" rel="noreferrer">FMUv5 reference design pinout</a>.</li></ul>',46)]))}const w=l(d,[["render",h]]);export{k as __pageData,w as default};
