import{_ as e,a}from"./chunks/pixhawk_6c_mini_dimension.f7XGKrkI.js";import{_ as r,c as i,a8 as o,o as l}from"./chunks/framework.BDnHobkS.js";const f=JSON.parse('{"title":"Holybro Pixhawk 6C Mini","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk6c_mini.md","filePath":"uk/flight_controller/pixhawk6c_mini.md"}'),n={name:"uk/flight_controller/pixhawk6c_mini.md"};function d(s,t,h,p,c,u){return l(),i("div",null,t[0]||(t[0]=[o('<h1 id="holybro-pixhawk-6c-mini" tabindex="-1">Holybro Pixhawk 6C Mini <a class="header-anchor" href="#holybro-pixhawk-6c-mini" aria-label="Permalink to &quot;Holybro Pixhawk 6C Mini&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><em>Pixhawk 6C Mini</em><sup>®</sup> is the latest update to the successful family of Pixhawk® flight controllers designed and made in collaboration with Holybro<sup>®</sup> and the PX4 team.</p><p>Він оснащений високопродуктивним процесором H7, резервуванням IMU, платою IMU з контролем температури та економічно вигідним дизайном, що забезпечує неймовірну продуктивність і надійність. It complies with the Pixhawk <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Connector Standard</a>.</p><p><img src="'+e+'" alt="Pixhawk6c mini Upright Image"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="введення" tabindex="-1">Введення <a class="header-anchor" href="#введення" aria-label="Permalink to &quot;Введення&quot;">​</a></h2><p>Pixhawk® 6C Mini є останнім оновленням успішної серії політних контролерів Pixhawk®.</p><p>Inside the Pixhawk® 6C Mini, you can find an STMicroelectronics® based STM32H743, paired with sensor technology from Bosch® &amp; InvenSense®, giving you flexibility and reliability for controlling any autonomous vehicle, suitable for both academic and commercial applications.</p><p>Мікроконтролер H7 Pixhawk® 6C Mini містить ядро Arm® Cortex®-M7 до 480 MHz, має 2MB flash пам’яті та 1MB RAM. Завдяки оновленій потужності обробки розробники можуть бути більш продуктивними та ефективними у своїй роботі з розробкою, що дозволяє використовувати складні алгоритми та моделі.</p><p>Pixhawk 6C Mini включає високопродуктивні, низькошумні IMU на борту, розроблені бути економічними, але водночас маючи резервування IMU. Система віброізоляції для фільтрації високочастотної вібрації та зменшення шуму для забезпечення точних показань, що дозволяє транспортним засобам досягати кращих загальних характеристик польоту.</p><p>Pixhawk® 6C Mini ідеально підходить для розробників у корпоративних дослідницьких лабораторіях, стартапах, академічних закладах (дослідники, професори, студенти) та комерційних застосувань.</p><p><strong>Key Design Points</strong></p><ul><li>High performance STM32H743 Processor with more computing power &amp; RAM</li><li>Новий економічний дизайн із низькопрофільним форм-фактором</li><li>Нова інтегрована система віброізоляції, призначена для фільтрації високочастотних вібрацій та зменшення шуму для забезпечення точних даних</li><li>IMUs are temperature-controlled by onboard heating resistors, allowing optimum working temperature of IMUs&amp;#x20</li></ul><h2 id="технічні-характеристики" tabindex="-1">Технічні характеристики <a class="header-anchor" href="#технічні-характеристики" aria-label="Permalink to &quot;Технічні характеристики&quot;">​</a></h2><h3 id="processors-sensors" tabindex="-1"><strong>Processors &amp; Sensors</strong> <a class="header-anchor" href="#processors-sensors" aria-label="Permalink to &quot;**Processors &amp; Sensors**&quot;">​</a></h3><ul><li>FMU Processor: STM32H743&amp;#x20 <ul><li>32 Bit Arm® Cortex®-M7, 480MHz, 2MB memory, 1MB SRAM&amp;#x20</li></ul></li><li>IO Processor: STM32F103 <ul><li> 32 Bit Arm® Cortex®-M3, 72MHz, 64KB SRAM&amp;#x20</li></ul></li><li>On-board sensors&amp;#x20 <ul><li> Accel/Gyro: ICM-42688-P&amp;#x20</li><li>Accel/Gyro: BMI055&amp;#x20</li><li>Mag: IST8310&amp;#x20</li><li>Барометр: MS5611</li></ul></li></ul><h3 id="electrical-data" tabindex="-1"><strong>Electrical data</strong> <a class="header-anchor" href="#electrical-data" aria-label="Permalink to &quot;**Electrical data**&quot;">​</a></h3><ul><li>Номінальна напруга: <ul><li>Максимальна вхідна напруга: 6 В</li><li>Вхід USB Power: 4.75~5.25V</li><li>Вхід Servo Rail: 0~36V</li></ul></li><li>Номінальний струм: <ul><li>`TELEM1`` обмежувач максимального вихідного струму: 1A</li><li>Комбінований обмежувач вихідного струму всіх інших портів: 1A</li></ul></li></ul><h3 id="mechanical-data" tabindex="-1"><strong>Mechanical data</strong> <a class="header-anchor" href="#mechanical-data" aria-label="Permalink to &quot;**Mechanical data**&quot;">​</a></h3><ul><li>Розміри: 53.3 x 39 x 16.2 mm</li><li>Вага: 39.2г</li></ul><h3 id="interfaces" tabindex="-1"><strong>Interfaces</strong> <a class="header-anchor" href="#interfaces" aria-label="Permalink to &quot;**Interfaces**&quot;">​</a></h3><ul><li><p>16- PWM серво виводів (8 з IO, 6 з FMU)</p></li><li><p>3 загальних послідовних портів</p><ul><li><code>TELEM1</code> - Full flow control, separate 1A current limit</li><li><code>TELEM2</code> - Full flow control</li></ul></li><li><p>2 порти GPS</p><ul><li>GPS1 - Повний порт GPS (GPS плюс захисний перемикач)</li><li>GPS2 - Basic GPS порт</li></ul></li><li><p>1 I2C порт</p><ul><li>Підтримує виділене I2C калібрування EEPROM, розташоване на модулі сенсорів</li></ul></li><li><p>2 CAN шини</p><ul><li>CAN шина має individual silent controls або ESC RX-MUX control</li></ul></li><li><p>1 порт відладки:</p><ul><li>FMU Debug Mini</li></ul></li><li><p>Виділений R/C вхід для Spektrum/DSM та S.BUS, CPPM, аналоговий / PWM RSSI</p></li><li><p>1 вхідний порт живлення (аналоговий)</p></li><li><p>Інші характеристики:</p><ul><li>Operating &amp; storage temperature: -40 ~ 85°c</li></ul></li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Order from <a href="https://holybro.com/products/pixhawk-6c-mini" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="зборка-інсталяція" tabindex="-1">Зборка/інсталяція <a class="header-anchor" href="#зборка-інсталяція" aria-label="Permalink to &quot;Зборка/інсталяція&quot;">​</a></h2><p>Порт Pixhawk 4 Mini дуже схожий з портом Pixhawk 6C Mini. Please refer to the <a href="./../assembly/quick_start_pixhawk4_mini.html">Pixhawk 4 Mini Wiring Quick Start</a> as it provides instructions on how to assemble required/important peripherals including GPS, Power Module etc.</p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6c-mini/pixhawk-6c-mini-ports" target="_blank" rel="noreferrer">Holybro Pixhawk 6C Mini Port Pinout</a></li></ul><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Опис параметра QGC</th><th>Мітка порту на FC</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS1</td><td>GPS1</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3</td><td>N/A</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>N/A</td><td>FMU Debug</td></tr><tr><td>UART5</td><td>/dev/ttyS3</td><td>TELEM2</td><td>TELEM2</td></tr><tr><td>USART6</td><td>/dev/ttyS4</td><td>PX4IO</td><td>I/O PWM Out</td></tr><tr><td>UART7</td><td>/dev/ttyS5</td><td>TELEM1</td><td>TELEM1</td></tr><tr><td>UART8</td><td>/dev/ttyS6</td><td>GPS2</td><td>GPS2</td></tr></tbody></table><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><img src="'+a+'" alt="Pixhawk6c Mini Dimensions"></p><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>Pixhawk 6C Mini</em> can be double-redundant on the power supply if two power sources are supplied. The two power rails are: <strong>POWER1</strong> and <strong>USB</strong>.</p><p><strong>Normal Operation Maximum Ratings</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWER1</strong> inputs (4.9V to 5.5V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>За таких умов система не буде витрачати жодної потужності (не буде працювати), але залишиться неушкодженою.</p><ol><li><strong>POWER1</strong> inputs (operational range 4.1V to 5.7V, 0V to 10V undamaged)</li><li><strong>USB</strong> input (operational range 4.1V to 5.7V, 0V to 6V undamaged)</li><li>Servo input: VDD_SERVO pin of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 42V undamaged)</li></ol><p><strong>Voltage monitoring</strong></p><p>Pixhawk 6C Mini використовує аналогові модулі живлення.</p><p>Holybro makes various analog <a href="./../power_module/">power modules</a> for different needs:</p><ul><li><a href="./../power_module/holybro_pm02.html">PM02 Power Module</a></li><li><a href="./../power_module/holybro_pm06_pixhawk4mini_power_module.html">PM06 Power Module</a></li><li><a href="./../power_module/holybro_pm07_pixhawk4_power_module.html">PM07 Power Module</a></li><li><a href="https://holybro.com/products/pm08-power-module-14s-200a" target="_blank" rel="noreferrer">PM08 Power Module</a></li></ul><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v6c_default</span></span></code></pre></div><p><a id="debug_port"></a></p><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port.</p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-mini">Pixhawk Debug Mini</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> interface (JST SH connector).</p><table tabindex="0"><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td><code>Vtref</code></td><td>+3.3V</td></tr><tr><td>2 (blk)</td><td>Console TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>Console RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td><code>SWDIO</code></td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td><code>SWCLK</code></td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><p>Інформацію про використання цього порту див:</p><ul><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li><li><a href="./../debug/system_console.html">PX4 System Console</a> (Note, the FMU console maps to USART3).</li></ul><h2 id="периферіині-пристроі" tabindex="-1">Периферійні пристрої <a class="header-anchor" href="#периферіині-пристроі" aria-label="Permalink to &quot;Периферійні пристрої&quot;">​</a></h2><ul><li><a href="https://holybro.com/products/digital-air-speed-sensor" target="_blank" rel="noreferrer">Digital Airspeed Sensor</a></li><li><a href="./../telemetry/">Telemetry Radio Modules</a>: <ul><li><a href="./../telemetry/holybro_sik_radio.html">Holybro Telemetry Radio</a></li><li><a href="./../telemetry/holybro_microhard_p900_radio.html">Holybro Microhard P900 Radio</a></li><li><a href="./../telemetry/holybro_xbp9x_radio.html">Holybro XBP9X Telemetry Radio</a></li></ul></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li></ul><h2 id="підтримувані-платформи-конструкціі" tabindex="-1">Підтримувані платформи / Конструкції <a class="header-anchor" href="#підтримувані-платформи-конструкціі" aria-label="Permalink to &quot;Підтримувані платформи / Конструкції&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа / човен, який може керуватися звичайними RC сервоприводами або сервоприводами Futaba S-Bus. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="дивіться-також" tabindex="-1">Дивіться також <a class="header-anchor" href="#дивіться-також" aria-label="Permalink to &quot;Дивіться також&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/" target="_blank" rel="noreferrer">Holybro Docs</a> (Holybro)</li><li><a href="./../assembly/quick_start_pixhawk4_mini.html">Pixhawk 4 Mini Wiring Quick Start</a> (and <a href="./../assembly/quick_start_pixhawk6c.html">Pixhawk 6C Wiring QuickStart</a>)</li><li><a href="./../power_module/holybro_pm02.html">PM02 Power Module</a></li><li><a href="./../power_module/holybro_pm06_pixhawk4mini_power_module.html">PM06 Power Module</a></li><li><a href="./../power_module/holybro_pm07_pixhawk4_power_module.html">PM07 Power Module</a></li><li><a href="https://holybro.com/products/pm08-power-module-14s-200a" target="_blank" rel="noreferrer">PM08 Power Module</a></li><li><a href="https://docs.google.com/spreadsheets/d/1FcmWRKd6zjdz3-cnjEDYEmANKZOFzNSc/edit?usp=sharing&amp;ouid=113251442407318461574&amp;rtpof=true&amp;sd=true" target="_blank" rel="noreferrer">FMUv6C reference design pinout</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a>.</li></ul>',62)]))}const g=r(n,[["render",d]]);export{f as __pageData,g as default};
