import{_ as d}from"./chunks/pixhawk6x_hero_upright.CbrL03Gs.js";import{_ as n}from"./chunks/pixhawk6x_exploded_diagram.BSBem3D0.js";import{_ as h,a as p,b as u}from"./chunks/pixhawk6x_icm_mini.Bc-bvhSp.js";import{_ as c}from"./chunks/pixhawk6x_wiring_diagram.BpS__0X1.js";import{_ as m,c as b,a8 as l,G as e,w as r,B as i,o as k,j as a}from"./chunks/framework.BDnHobkS.js";const A=JSON.parse('{"title":"Holybro Pixhawk 6X","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk6x.md","filePath":"uk/flight_controller/pixhawk6x.md"}'),x={name:"uk/flight_controller/pixhawk6x.md"};function f(g,t,P,w,S,_){const o=i("tab"),s=i("tabs");return k(),b("div",null,[t[3]||(t[3]=l('<h1 id="holybro-pixhawk-6x" tabindex="-1">Holybro Pixhawk 6X <a class="header-anchor" href="#holybro-pixhawk-6x" aria-label="Permalink to &quot;Holybro Pixhawk 6X&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><em>Pixhawk 6X</em><sup>®</sup> is the latest update to the successful family of Pixhawk® flight controllers designed and made in collaboration with Holybro<sup>®</sup> and the PX4 team.</p><p>It is based on the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk​​® Autopilot FMUv6X Standard</a>, <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Autopilot Bus Standard</a>, and <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Connector Standard</a>.</p><p>Оснащений високопродуктивним процесором H7, модульним дизайном, потрійним резервуванням, платою IMU з контролем температури, ізольованими доменами сенсорів, що забезпечує неймовірну продуктивність, надійність та гнучкість.</p><h3 id="pixhawk-6x-rev-3" tabindex="-1">Pixhawk 6X (Rev 3) <a class="header-anchor" href="#pixhawk-6x-rev-3" aria-label="Permalink to &quot;Pixhawk 6X (Rev 3)&quot;">​</a></h3><p><img src="'+d+'" width="230px" title="Pixhawk6X Upright Image"> <img src="'+n+'" width="400px" title="Pixhawk6X Exploded Image"></p><h3 id="pixhawk-6x-icm-45686" tabindex="-1">Pixhawk 6X (ICM-45686) <a class="header-anchor" href="#pixhawk-6x-icm-45686" aria-label="Permalink to &quot;Pixhawk 6X (ICM-45686)&quot;">​</a></h3>',8)),e(s,null,{default:r(()=>[e(o,{name:"Standard v2A"},{default:r(()=>t[0]||(t[0]=[a("p",null,[a("img",{src:h,alt:"Pixhawk 6X Standard v2A"})],-1)])),_:1}),e(o,{name:"Standard v2B"},{default:r(()=>t[1]||(t[1]=[a("p",null,[a("img",{src:p,alt:"Pixhawk 6X Standard v2B"})],-1)])),_:1}),e(o,{name:"Mini"},{default:r(()=>t[2]||(t[2]=[a("p",null,[a("img",{src:u,alt:"Pixhawk 6X Mini"})],-1)])),_:1})]),_:1}),t[4]||(t[4]=l('<div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This autopilot is <a href="./../flight_controller/autopilot_pixhawk_standard.html">supported</a> by the PX4 maintenance and test teams.</p></div><h2 id="введення" tabindex="-1">Введення <a class="header-anchor" href="#введення" aria-label="Permalink to &quot;Введення&quot;">​</a></h2><p>Всередині Pixhawk® 6X ви можете знайти STM32H753 на базі STMicroelectronics®, поєднаний з сенсорною технологією від Bosch®, InvenSense®, що надає вам гнучкість і надійність для керування будь-яким автономним апаратом, придатним як для академічних, так і для комерційних застосувань.</p><p>Мікроконтролер H7 Pixhawk® 6X містить ядро Arm® Cortex®-M7 до 480 MHz, має 2MB flash пам’яті та 1MB RAM. Автопілот PX4 використовує переваги збільшеної потужності та оперативної пам’яті. Завдяки оновленій потужності обробки розробники можуть бути більш продуктивними та ефективними у своїй роботі з розробкою, що дозволяє використовувати складні алгоритми та моделі.</p><p>Відкритий стандарт FMUv6X містить високопродуктивні IMU з низьким рівнем шуму, призначені для кращої стабілізації. Triple redundant IMU &amp; double redundant barometer on separate buses. Коли автопілот PX4 виявляє відмову датчика, система безперервно переключається на інший, щоб забезпечити надійність керування польотом.</p><p>Кожен незалежний LDO живить кожен набір сенсорів з незалежним керуванням живленням. Система віброізоляції для фільтрації високочастотної вібрації та зменшення шуму для забезпечення точних показань, що дозволяє транспортним засобам досягати кращих загальних характеристик польоту.</p><p>Зовнішня шина датчиків (SPI5) має дві лінії вибору чипів та сигнали готовності даних для додаткових датчиків та корисного навантаження з інтерфейсом SPI, а також з інтегрованим Microchip Ethernet PHY, високошвидкісний обмін даними з комп&#39;ютерами місії через ethernet тепер можливий.</p><p>Pixhawk®​ 6X ідеально підходить для розробників у корпоративних дослідницьких лабораторіях, стартапах, академічних потреб (досліджень, професорів, студентів) та комерційного застосування.</p><h2 id="ключові-пункти-дизаину" tabindex="-1">Ключові пункти дизайну <a class="header-anchor" href="#ключові-пункти-дизаину" aria-label="Permalink to &quot;Ключові пункти дизайну&quot;">​</a></h2><ul><li>Високопродуктивний процесор STM32H753</li><li>Modular flight controller: separated IMU, FMU, and Base system connected by a 100-pin &amp; a 50-pin Pixhawk®​ Autopilot Bus connector.</li><li>Redundancy: 3x IMU sensors &amp; 2x Barometer sensors on separate buses</li><li>Потрійне резервування доменів: повністю ізольовані сенсорні домени з окремими шинами та окремим керуванням живленням</li><li>Нова система ізоляції вібрацій для фільтрації високочастотних вібрацій та зменшення шуму для забезпечення точних вимірювань</li><li>Інтерфейс Ethernet для високошвидкісної інтеграції комп&#39;ютера місії</li><li>IMUs are temperature-controlled by onboard heating resistors, allowing optimum working temperature of IMUs&amp;#x20</li></ul><h3 id="processors-sensors" tabindex="-1">Processors &amp; Sensors <a class="header-anchor" href="#processors-sensors" aria-label="Permalink to &quot;Processors &amp; Sensors&quot;">​</a></h3><ul><li>Процесор FMU: STM32H753 <ul><li>32 Bit Arm® Cortex®-M7, 480MHz, 2MB flash memory, 1MB RAM</li></ul></li><li>IO Processor: STM32F100 <ul><li>32 Bit Arm®️ Cortex®️-M3, 24MHz, 8KB SRAM</li></ul></li><li>Сенсори на платі <ul><li>Accel/Gyro: ICM-20649 або BMI088</li><li>Accel/Gyro: ICM-42688-P</li><li>Accel/Gyro: ICM-42670-P</li><li>Mag: BMM150</li><li>Barometer: 2x BMP388</li></ul></li></ul><h3 id="електричні-дані" tabindex="-1">Електричні дані <a class="header-anchor" href="#електричні-дані" aria-label="Permalink to &quot;Електричні дані&quot;">​</a></h3><ul><li>Номінальна напруга: <ul><li>Максимальна вхідна напруга: 6 В</li><li>Вхід USB Power: 4.75~5.25V</li><li>Вхід Servo Rail: 0~36V</li></ul></li><li>Номінальний струм: <ul><li><code>TELEM1</code> output current limiter: 1.5A</li><li>Комбінований обмежувач вихідного струму всіх інших портів: 1.5A</li></ul></li></ul><h3 id="механічні-характеристики" tabindex="-1">Механічні характеристики <a class="header-anchor" href="#механічні-характеристики" aria-label="Permalink to &quot;Механічні характеристики&quot;">​</a></h3><ul><li>Розміри <ul><li>Модуль політного контролера: 38.8 x 31.8 x 14.6mm</li><li>Стандартна базова плата: 52.4 x 103.4 x 16.7mm</li><li>Міні базова плата: 43.4 x 72.8 x 14.2 mm</li></ul></li><li>Вага <ul><li>Модуль політного контролера: 23g</li><li>Стандартна базова плата: 51g</li><li>Міні базова плата: 26.5g</li></ul></li></ul><h3 id="інтерфеиси" tabindex="-1">Інтерфейси <a class="header-anchor" href="#інтерфеиси" aria-label="Permalink to &quot;Інтерфейси&quot;">​</a></h3><ul><li><p>16 PWM виводів сервоприводів</p></li><li><p>R/C вхід для Spektrum / DSM</p></li><li><p>Виділений R/C вхід для PPM та S.Bus входу</p></li><li><p>Спеціалізований аналоговий / PWM вхід RSSI та вивід S.Bus</p></li><li><p>4 загальних послідовних порти</p><ul><li>3 з повним контролем потоку</li><li>1 з окремим обмеженням струму 1.5A (Telem1)</li><li>1 з I2C та додатковою лінією GPIO для зовнішнього NFC зчитувача</li></ul></li><li><p>2 порти GPS</p><ul><li>1 повний GPS плюс порт запобіжного перемикача</li><li>1 базовий порт GPS</li></ul></li><li><p>1 I2C порт</p></li><li><p>1 порт Ethernet</p><ul><li>Transformerless Applications</li><li>100Mbps</li></ul></li><li><p>1 шина SPI</p><ul><li>2 лінії вибору чіпу</li><li>2 лінії готових даних</li><li>1 SPI SYNC лінія</li><li>1 лінія SPI reset</li></ul></li><li><p>2 CAN шини для CAN периферії</p><ul><li>CAN шина має individual silent controls або ESC RX-MUX control</li></ul></li><li><p>2 порти вводу живлення з SMBus</p><ul><li>1 AD &amp; IO port</li><li>2 додаткових аналогових входи</li><li>1 PWM/Capture вхід</li><li>2 виділені відладочні та GPIO лінії</li></ul></li><li><p>Інші характеристики:</p><ul><li>Operating &amp; storage temperature: -40 ~ 85°c</li></ul></li></ul><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Order from <a href="https://holybro.com/products/pixhawk-6x" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="зборка-інсталяція" tabindex="-1">Зборка/інсталяція <a class="header-anchor" href="#зборка-інсталяція" aria-label="Permalink to &quot;Зборка/інсталяція&quot;">​</a></h2><p>The <a href="./../assembly/quick_start_pixhawk6x.html">Pixhawk 6X Wiring Quick Start</a> provides instructions on how to assemble required/important peripherals including GPS, Power Module etc.</p><h2 id="з-єднання" tabindex="-1">З&#39;єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З&#39;єднання&quot;">​</a></h2><p>Sample Wiring Diagram <img src="'+c+'" alt="Pixhawk 6X Wiring Overview"></p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Baseboard Pinout</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-mini-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Mini-Baseboard Pinout</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-baseboards/pixhawk-jetson-baseboard" target="_blank" rel="noreferrer">Holybro Pixhawk Jetson Baseboard</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-baseboards/pixhawk-rpi-cm4-baseboard" target="_blank" rel="noreferrer">Holybro Pixhawk RPi CM4 Baseboard</a></li></ul><p>Примітки:</p><ul><li>The <a href="./../camera/fc_connected_camera.html#camera-capture-configuration">camera capture pin</a> (<code>PI0</code>) is pin 2 on the AD&amp;IO port, marked above as <code>FMU_CAP1</code>.</li></ul><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Debug Console</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>UART4 &amp; I2C</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>TELEM2</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>PX4IO/RC</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>GPS2</td></tr></tbody></table><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><a href="https://docs.holybro.com/autopilot/pixhawk-6x/dimensions" target="_blank" rel="noreferrer">Pixhawk 6X Dimensions</a></p><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>Pixhawk 6X</em> can be triple-redundant on the power supply if three power sources are supplied. The three power rails are: <strong>POWER1</strong>, <strong>POWER2</strong> and <strong>USB</strong>. The <strong>POWER1</strong> &amp; <strong>POWER2</strong> ports on the Pixhawk 6X uses the 6 circuit <a href="https://www.molex.com/molex/products/part-detail/pcb_receptacles/5024430670" target="_blank" rel="noreferrer">2.00mm Pitch CLIK-Mate Wire-to-Board PCB Receptacle</a>.</p><p><strong>Normal Operation Maximum Ratings</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (4.9V to 5.5V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>За таких умов система не буде витрачати жодної потужності (не буде працювати), але залишиться неушкодженою.</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (operational range 4.1V to 5.7V, 0V to 10V undamaged)</li><li><strong>USB</strong> input (operational range 4.1V to 5.7V, 0V to 6V undamaged)</li><li>Servo input: VDD_SERVO pin of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 42V undamaged)</li></ol><p><strong>Voltage monitoring</strong></p><p>Digital I2C battery monitoring is enabled by default (see <a href="./../assembly/quick_start_pixhawk6x.html#power">Quickstart &gt; Power</a>).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Analog battery monitoring via an ADC is not supported on this particular board, but may be supported in variations of this flight controller with a different baseboard.</p></div><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v6x_default</span></span></code></pre></div><p><a id="debug_port"></a></p><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port.</p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-full">Pixhawk Debug Full</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> interface (JST SM10B connector).</p><table tabindex="0"><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td><code>Vtref</code></td><td>+3.3V</td></tr><tr><td>2 (blk)</td><td>Console TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>Console RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td><code>SWDIO</code></td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td><code>SWCLK</code></td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td><code>SWO</code></td><td>+3.3V</td></tr><tr><td>7 (blk)</td><td>NFC GPIO</td><td>+3.3V</td></tr><tr><td>8 (чорний)</td><td>PH11</td><td>+3.3V</td></tr><tr><td>9 (чорний)</td><td>nRST</td><td>+3.3V</td></tr><tr><td>10 (чорний)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><p>Інформацію про використання цього порту див:</p><ul><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li><li><a href="./../debug/system_console.html">PX4 System Console</a> (Note, the FMU console maps to USART3).</li></ul><h2 id="периферіині-пристроі" tabindex="-1">Периферійні пристрої <a class="header-anchor" href="#периферіині-пристроі" aria-label="Permalink to &quot;Периферійні пристрої&quot;">​</a></h2><ul><li><a href="https://holybro.com/collections/telemetry-radios?orderby=date" target="_blank" rel="noreferrer">Telemetry Radio Modules</a></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li><li><a href="https://holybro.com/collections/sensors" target="_blank" rel="noreferrer">Holybro Sensors</a></li><li><a href="https://holybro.com/collections/gps-rtk-systems" target="_blank" rel="noreferrer">Holybro GPS &amp; RTK Systems</a></li><li><a href="https://holybro.com/collections/power-modules-pdbs" target="_blank" rel="noreferrer">Power Modules &amp; PDBs</a></li></ul><h2 id="підтримувані-платформи-конструкціі" tabindex="-1">Підтримувані платформи / Конструкції <a class="header-anchor" href="#підтримувані-платформи-конструкціі" aria-label="Permalink to &quot;Підтримувані платформи / Конструкції&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа / човен, який може керуватися звичайними RC сервоприводами або сервоприводами Futaba S-Bus. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/" target="_blank" rel="noreferrer">Holybro Docs</a> (Holybro)</li><li><a href="./../assembly/quick_start_pixhawk6x.html">Pixhawk 6X Wiring QuickStart</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot FMUv6X Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a>.</li></ul>',60))])}const I=m(x,[["render",f]]);export{A as __pageData,I as default};
