import{_ as d,a as n,b as h,c as p,d as u,e as c}from"./chunks/pixhawk6x_pro_wiring_diagram_vtol.DB0vIl2X.js";import{_ as b,c as m,a8 as i,G as e,w as r,B as l,o as f,j as a}from"./chunks/framework.BDnHobkS.js";const A=JSON.parse('{"title":"Holybro Pixhawk 6X Pro","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk6x_pro.md","filePath":"uk/flight_controller/pixhawk6x_pro.md"}'),g={name:"uk/flight_controller/pixhawk6x_pro.md"};function k(x,t,P,w,y,S){const o=l("tab"),s=l("tabs");return f(),m("div",null,[t[3]||(t[3]=i('<h1 id="holybro-pixhawk-6x-pro" tabindex="-1">Holybro Pixhawk 6X Pro <a class="header-anchor" href="#holybro-pixhawk-6x-pro" aria-label="Permalink to &quot;Holybro Pixhawk 6X Pro&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>PX4 не розробляє цей (або будь-який інший) автопілот. Contact the <a href="https://holybro.com/" target="_blank" rel="noreferrer">manufacturer</a> for hardware support or compliance issues.</p></div><p><img src="'+d+'" alt="Pixhawk6X Pro Internal Architecture"></p><h2 id="ключові-пункти-дизаину" tabindex="-1">Ключові пункти дизайну <a class="header-anchor" href="#ключові-пункти-дизаину" aria-label="Permalink to &quot;Ключові пункти дизайну&quot;">​</a></h2><ul><li>High-performance ADIS16470 Industrial IMU with high accelerometer dynamic range (±40 g), perfect for accurate motion sensing in demanding UAV applications</li><li>All new advanced durable vibration isolation material with resonance frequency in the higher spectrum, ideal for industrial and commercial drone applications</li><li>Високопродуктивний процесор STM32H753</li><li>Інтерфейс Ethernet для високошвидкісної інтеграції комп&#39;ютера місії</li></ul><h2 id="baseboards" tabindex="-1">Baseboards <a class="header-anchor" href="#baseboards" aria-label="Permalink to &quot;Baseboards&quot;">​</a></h2><p>The Pixhawk 6X Pro can be purchased with a number of baseboards (or no baseboard) to suit different use cases and vehicle types, including <a href="https://docs.holybro.com/autopilot/pixhawk-baseboards/pixhawk-baseboard-v2-ports" target="_blank" rel="noreferrer">Standard v2A</a>, <a href="https://docs.holybro.com/autopilot/pixhawk-baseboards/pixhawk-baseboard-v2-ports" target="_blank" rel="noreferrer">Standard v2B</a>, and <a href="https://docs.holybro.com/autopilot/pixhawk-baseboards/pixhawk-mini-baseboard-ports" target="_blank" rel="noreferrer">Mini</a>, which are shown below. It can also be used with any other Pixhawk Autopilot Bus (PAB) specification-compliant baseboard, such as the <a href="./../companion_computer/holybro_pixhawk_jetson_baseboard.html">Holybro Pixhawk Jetson Baseboard</a> and <a href="./../companion_computer/holybro_pixhawk_rpi_cm4_baseboard.html">Holybro Pixhawk RPi CM4 Baseboard</a>.</p>',7)),e(s,null,{default:r(()=>[e(o,{name:"Pixhawk 6X Pro Standard v2A"},{default:r(()=>t[0]||(t[0]=[a("p",null,[a("img",{src:n,alt:"Pixhawk 6X Pro Standard v2A"})],-1)])),_:1}),e(o,{name:"Pixhawk 6X Pro Standard v2B"},{default:r(()=>t[1]||(t[1]=[a("p",null,[a("img",{src:h,alt:"Pixhawk 6X Pro Standard v2B"})],-1)])),_:1}),e(o,{name:"Pixhawk 6X Pro Mini"},{default:r(()=>t[2]||(t[2]=[a("p",null,[a("img",{src:p,alt:"Pixhawk 6X Pro Mini"})],-1)])),_:1})]),_:1}),t[4]||(t[4]=i('<h2 id="функціі" tabindex="-1">Функції <a class="header-anchor" href="#функціі" aria-label="Permalink to &quot;Функції&quot;">​</a></h2><ul><li>Triple redundant IMU &amp; double redundant barometer on separate buses</li><li>Modular flight controller: separated IMU, FMU, and Base system</li><li>Safety-driven design incorporates sensors from different manufacturers and model lineups</li><li>Independent LDO powers every sensor set with independent power control.</li><li>Temperature-controlled IMU board, allowing optimum working temperature of IMUs</li></ul><details class="details custom-block"><summary>Details</summary><p>Processors &amp; Sensors</p><ul><li>Процесор FMU: STM32H753 <ul><li>32 Bit Arm® Cortex®-M7, 480MHz, 2MB flash memory, 1MB RAM</li></ul></li><li>IO Processor: STM32F103 <ul><li>32 Bit Arm® Cortex®-M3, 72MHz, 64KB SRAM</li></ul></li><li>Сенсори на платі <ul><li>Accel/Gyro: ADIS16470 (±40g, Vibration Isolated, Industrial IMU)</li><li>Accel/Gyro: IIM-42652 (±16g, Vibration Isolated, Industrial IMU)</li><li>Accel/Gyro: ICM-45686 with BalancedGyro™ Technology (±32g, Hard Mounted)</li><li>Barometer: ICP20100</li><li>Barometer: BMP388</li><li>Mag: BMM150</li></ul></li></ul></details><details class="details custom-block"><summary>Details</summary><p>Електричні дані</p><ul><li>Номінальна напруга: <ul><li>Максимальна вхідна напруга: 6 В</li><li>Вхід USB Power: 4.75~5.25V</li><li>Вхід сервоприводу: 0~36V</li></ul></li><li>Current Ratings: - Telem1 output current limiter: 1.5A - All other port combined output current limiter: 1.5A</li></ul></details><details class="details custom-block"><summary>Details</summary><p>Механічні характеристики</p><ul><li>Розміри <ul><li>Flight Controller Module: 38.8 x 31.8 x 30.1mm</li><li>Стандартна базова плата: 52.4 x 103.4 x 16.7mm</li><li>Міні базова плата: 43.4 x 72.8 x 14.2 mm</li></ul></li><li>Weight - Flight Controller Module: 50g - Standard Baseboard: 51g - Mini Baseboard: 26.5g</li></ul></details><details class="details custom-block"><summary>Details</summary><p>Інтерфейси</p><ul><li>16 PWM виводів сервоприводів</li><li>R/C вхід для Spektrum / DSM</li><li>Виділений R/C вхід для PPM та S.Bus входу</li><li>Спеціалізований аналоговий / PWM вхід RSSI та вивід S.Bus</li><li>4 загальних послідовних порти <ul><li>3 з повним контролем потоку</li><li>1 з окремим обмеженням струму 1.5A (Telem1)</li><li>1 з I2C та додатковою лінією GPIO для зовнішнього NFC зчитувача</li></ul></li><li>2 порти GPS <ul><li>1 повний GPS плюс порт запобіжного перемикача</li><li>1 базовий порт GPS</li></ul></li><li>1 I2C порт</li><li>1 порт Ethernet <ul><li>Transformerless Applications</li><li>100Mbps</li></ul></li><li>1 шина SPI <ul><li>2 лінії вибору чіпу</li><li>2 лінії готових даних</li><li>1 SPI SYNC лінія</li><li>1 лінія SPI reset</li></ul></li><li>2 CAN шини для CAN периферії <ul><li>CAN шина має individual silent controls або ESC RX-MUX control</li></ul></li><li>2 Power input ports with SMBus - 1 AD &amp; IO port - 2 additional analog input - 1 PWM/Capture input - 2 Dedicated debug and GPIO lines</li></ul></details><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Order from <a href="https://holybro.com/products/pixhawk-6x-pro" target="_blank" rel="noreferrer">Holybro</a>.</p><h2 id="зборка-інсталяція" tabindex="-1">Зборка/інсталяція <a class="header-anchor" href="#зборка-інсталяція" aria-label="Permalink to &quot;Зборка/інсталяція&quot;">​</a></h2><p>The <a href="./../assembly/quick_start_pixhawk6x.html">Pixhawk 6X Wiring Quick Start</a> provides instructions on how to assemble required/important peripherals including GPS, Power Module etc.</p><h2 id="з-єднання" tabindex="-1">З&#39;єднання <a class="header-anchor" href="#з-єднання" aria-label="Permalink to &quot;З&#39;єднання&quot;">​</a></h2><h3 id="multicopter-wiring-example" tabindex="-1">Multicopter Wiring Example <a class="header-anchor" href="#multicopter-wiring-example" aria-label="Permalink to &quot;Multicopter Wiring Example&quot;">​</a></h3><p><img src="'+u+'" alt="Pixhawk 6X Pro Wiring Overview"></p><h3 id="vtol-wiring-example" tabindex="-1">VTOL Wiring Example <a class="header-anchor" href="#vtol-wiring-example" aria-label="Permalink to &quot;VTOL Wiring Example&quot;">​</a></h3><p><img src="'+c+'" alt="Pixhawk 6X Pro VTOL Fixed wing Wiring Overview"></p><h2 id="схема-розташування-виводів" tabindex="-1">Схема розташування виводів <a class="header-anchor" href="#схема-розташування-виводів" aria-label="Permalink to &quot;Схема розташування виводів&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Baseboard Pinout</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x/pixhawk-mini-baseboard-pinout" target="_blank" rel="noreferrer">Holybro Pixhawk Mini-Baseboard Pinout</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-baseboards/pixhawk-jetson-baseboard" target="_blank" rel="noreferrer">Holybro Pixhawk Jetson Baseboard</a></li><li><a href="https://docs.holybro.com/autopilot/pixhawk-baseboards/pixhawk-rpi-cm4-baseboard" target="_blank" rel="noreferrer">Holybro Pixhawk RPi CM4 Baseboard</a></li></ul><p>Примітки:</p><ul><li>The <a href="./../camera/fc_connected_camera.html#camera-capture-configuration">camera capture pin</a> (<code>PI0</code>) is pin 2 on the AD&amp;IO port, marked above as <code>FMU_CAP1</code>.</li></ul><h2 id="налаштування-послідовного-порту" tabindex="-1">Налаштування послідовного порту <a class="header-anchor" href="#налаштування-послідовного-порту" aria-label="Permalink to &quot;Налаштування послідовного порту&quot;">​</a></h2><table tabindex="0"><thead><tr><th>UART</th><th>Пристрій</th><th>Порт</th></tr></thead><tbody><tr><td>USART1</td><td>/dev/ttyS0</td><td>GPS</td></tr><tr><td>USART2</td><td>/dev/ttyS1</td><td>TELEM3</td></tr><tr><td>USART3</td><td>/dev/ttyS2</td><td>Debug Console</td></tr><tr><td>UART4</td><td>/dev/ttyS3</td><td>UART4 &amp; I2C</td></tr><tr><td>UART5</td><td>/dev/ttyS4</td><td>TELEM2</td></tr><tr><td>USART6</td><td>/dev/ttyS5</td><td>PX4IO/RC</td></tr><tr><td>UART7</td><td>/dev/ttyS6</td><td>TELEM1</td></tr><tr><td>UART8</td><td>/dev/ttyS7</td><td>GPS2</td></tr></tbody></table><h2 id="розміри" tabindex="-1">Розміри <a class="header-anchor" href="#розміри" aria-label="Permalink to &quot;Розміри&quot;">​</a></h2><p><a href="https://docs.holybro.com/autopilot/pixhawk-6x-pro/dimensions" target="_blank" rel="noreferrer">Pixhawk 6X Pro Dimensions</a></p><h2 id="номінальна-напруга" tabindex="-1">Номінальна напруга <a class="header-anchor" href="#номінальна-напруга" aria-label="Permalink to &quot;Номінальна напруга&quot;">​</a></h2><p><em>Pixhawk 6X Pro</em> can be triple-redundant on the power supply if three power sources are supplied. The three power rails are: <strong>POWER1</strong>, <strong>POWER2</strong> and <strong>USB</strong>. The <strong>POWER1</strong> &amp; <strong>POWER2</strong> ports on the Pixhawk 6X uses the 6 circuit <a href="https://www.molex.com/molex/products/part-detail/pcb_receptacles/5024430670" target="_blank" rel="noreferrer">2.00mm Pitch CLIK-Mate Wire-to-Board PCB Receptacle</a>.</p><p><strong>Normal Operation Maximum Ratings</strong></p><p>За таких умов всі джерела живлення будуть використовуватися в цьому порядку для живлення системи:</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (4.9V to 5.5V)</li><li><strong>USB</strong> input (4.75V to 5.25V)</li></ol><p><strong>Absolute Maximum Ratings</strong></p><p>За таких умов система не буде витрачати жодної потужності (не буде працювати), але залишиться неушкодженою.</p><ol><li><strong>POWER1</strong> and <strong>POWER2</strong> inputs (operational range 4.1V to 5.7V, 0V to 10V undamaged)</li><li><strong>USB</strong> input (operational range 4.1V to 5.7V, 0V to 6V undamaged)</li><li>Servo input: VDD_SERVO pin of <strong>FMU PWM OUT</strong> and <strong>I/O PWM OUT</strong> (0V to 42V undamaged)</li></ol><p><strong>Voltage monitoring</strong></p><p>Digital I2C battery monitoring is enabled by default (see <a href="./../assembly/quick_start_pixhawk6x.html#power">Quickstart &gt; Power</a>).</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Analog battery monitoring via an ADC is not supported on this particular board, but may be supported in variations of this flight controller with a different baseboard.</p></div><h2 id="збірка-прошивки" tabindex="-1">Збірка прошивки <a class="header-anchor" href="#збірка-прошивки" aria-label="Permalink to &quot;Збірка прошивки&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Most users will not need to build this firmware! It is pre-built and automatically installed by <em>QGroundControl</em> when appropriate hardware is connected.</p></div><p>To <a href="./../dev_setup/building_px4.html">build PX4</a> for this target:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>make px4_fmu-v6x_default</span></span></code></pre></div><h2 id="відладочнии-порт" tabindex="-1">Відладочний порт <a class="header-anchor" href="#відладочнии-порт" aria-label="Permalink to &quot;Відладочний порт&quot;">​</a></h2><p>The <a href="./../debug/system_console.html">PX4 System Console</a> and <a href="./../debug/swd_debug.html">SWD interface</a> run on the <strong>FMU Debug</strong> port.</p><p>The pinouts and connector comply with the <a href="./../debug/swd_debug.html#pixhawk-debug-full">Pixhawk Debug Full</a> interface defined in the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a> interface (JST SM10B connector).</p><table tabindex="0"><thead><tr><th>Pin</th><th>Сигнал</th><th>Вольтаж</th></tr></thead><tbody><tr><td>1 (red)</td><td><code>Vtref</code></td><td>+3.3V</td></tr><tr><td>2 (blk)</td><td>Console TX (OUT)</td><td>+3.3V</td></tr><tr><td>3 (blk)</td><td>Console RX (IN)</td><td>+3.3V</td></tr><tr><td>4 (blk)</td><td><code>SWDIO</code></td><td>+3.3V</td></tr><tr><td>5 (blk)</td><td><code>SWCLK</code></td><td>+3.3V</td></tr><tr><td>6 (blk)</td><td><code>SWO</code></td><td>+3.3V</td></tr><tr><td>7 (blk)</td><td>NFC GPIO</td><td>+3.3V</td></tr><tr><td>8 (чорний)</td><td>PH11</td><td>+3.3V</td></tr><tr><td>9 (чорний)</td><td>nRST</td><td>+3.3V</td></tr><tr><td>10 (чорний)</td><td><code>GND</code></td><td>GND</td></tr></tbody></table><p>Інформацію про використання цього порту див:</p><ul><li><a href="./../debug/swd_debug.html">SWD Debug Port</a></li><li><a href="./../debug/system_console.html">PX4 System Console</a> (Note, the FMU console maps to USART3).</li></ul><h2 id="периферіині-пристроі" tabindex="-1">Периферійні пристрої <a class="header-anchor" href="#периферіині-пристроі" aria-label="Permalink to &quot;Периферійні пристрої&quot;">​</a></h2><ul><li><a href="https://holybro.com/collections/telemetry-radios?orderby=date" target="_blank" rel="noreferrer">Telemetry Radio Modules</a></li><li><a href="./../sensor/rangefinders.html">Rangefinders/Distance sensors</a></li><li><a href="https://holybro.com/collections/sensors" target="_blank" rel="noreferrer">Holybro Sensors</a></li><li><a href="https://holybro.com/collections/gps-rtk-systems" target="_blank" rel="noreferrer">Holybro GPS &amp; RTK Systems</a></li><li><a href="https://holybro.com/collections/power-modules-pdbs" target="_blank" rel="noreferrer">Power Modules &amp; PDBs</a></li></ul><h2 id="supported-platforms-airframes" tabindex="-1">Supported Platforms/Airframes <a class="header-anchor" href="#supported-platforms-airframes" aria-label="Permalink to &quot;Supported Platforms/Airframes&quot;">​</a></h2><p>Будь-який мультикоптер / літак / наземна платформа / човен, який може керуватися звичайними RC сервоприводами або сервоприводами Futaba S-Bus. The complete set of supported configurations can be seen in the <a href="./../airframes/airframe_reference.html">Airframes Reference</a>.</p><h2 id="cad-file" tabindex="-1">CAD File <a class="header-anchor" href="#cad-file" aria-label="Permalink to &quot;CAD File&quot;">​</a></h2><p>Download <a href="https://docs.holybro.com/autopilot/pixhawk-6x-pro/download" target="_blank" rel="noreferrer">here</a>.</p><h2 id="дивіться-також" tabindex="-1">Дивіться також <a class="header-anchor" href="#дивіться-також" aria-label="Permalink to &quot;Дивіться також&quot;">​</a></h2><ul><li><a href="https://docs.holybro.com/autopilot/pixhawk-6x-pro" target="_blank" rel="noreferrer">Holybro Docs</a> (Holybro)</li><li><a href="./../assembly/quick_start_pixhawk6x.html">Pixhawk 6X Wiring QuickStart</a></li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-012%20Pixhawk%20Autopilot%20v6X%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot FMUv6X Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus Standard</a>.</li><li><a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Connector Standard</a>.</li></ul>',52))])}const v=b(g,[["render",k]]);export{A as __pageData,v as default};
