import{_ as t,c as o,a8 as r,o as i}from"./chunks/framework.BDnHobkS.js";const d=JSON.parse('{"title":"Pixhawk Autopilot Bus & Carriers","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_controller/pixhawk_autopilot_bus.md","filePath":"uk/flight_controller/pixhawk_autopilot_bus.md"}'),e={name:"uk/flight_controller/pixhawk_autopilot_bus.md"};function l(h,a,s,n,p,c){return i(),o("div",null,a[0]||(a[0]=[r('<h1 id="pixhawk-autopilot-bus-carriers" tabindex="-1">Pixhawk Autopilot Bus &amp; Carriers <a class="header-anchor" href="#pixhawk-autopilot-bus-carriers" aria-label="Permalink to &quot;Pixhawk Autopilot Bus &amp; Carriers&quot;">​</a></h1><p>The <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-010%20Pixhawk%20Autopilot%20Bus%20Standard.pdf" target="_blank" rel="noreferrer">Pixhawk Autopilot Bus (PAB) Standard</a> provides a standard interface design that allows any compliant Pixhawk flight controller to be used &quot;plug-and-play&quot; with any compliant base board.</p><p>Ця модульність полегшує інтеграцію політних контролерів в різні конструкції system-on-module. Наприклад, PAB означає, що ви можете використовувати той самий апаратний політний контролер на більш компактній платі з меншою кількістю виводів або на платі, яка інтегрується з супутнім комп&#39;ютером, тощо.</p><p>Наступні носії та політні контролери відповідають вимогам PAB і, отже, можуть використовуватися взаємозамінно.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The &quot;Mechanical Design&quot; section of the standard provides specific recommendations for mechanical compatibility between vendors. Політні контролери та базові порти, перераховані тут, очікуються бути сумісними з усіма рекомендаціями.</p></div><h2 id="pab-сумісні-носіі" tabindex="-1">PAB сумісні носії <a class="header-anchor" href="#pab-сумісні-носіі" aria-label="Permalink to &quot;PAB сумісні носії&quot;">​</a></h2><ul><li><a href="./../flight_controller/ark_pab.html">ARK Electronics Pixhawk Autopilot Bus Carrier</a></li><li><a href="https://holybro.com/products/pixhawk-baseboards" target="_blank" rel="noreferrer">Holybro Pixhawk Standard Baseboard</a></li><li><a href="https://holybro.com/products/pixhawk-baseboards" target="_blank" rel="noreferrer">Holybro Pixhawk Mini Baseboard</a></li><li><a href="./../companion_computer/holybro_pixhawk_rpi_cm4_baseboard.html">Holybro Pixhawk RPi CM4 Baseboard</a> (Integrated Companion/Flight Controller Board)</li></ul><h2 id="pab-сумісні-політні-контролери" tabindex="-1">PAB сумісні політні контролери <a class="header-anchor" href="#pab-сумісні-політні-контролери" aria-label="Permalink to &quot;PAB сумісні політні контролери&quot;">​</a></h2><ul><li><a href="./../flight_controller/ark_v6x.html">ARK Electronics ARKV6X</a></li><li><a href="./../flight_controller/pixhawk5x.html">Holybro Pixhawk 5X (FMUv5X)</a></li><li><a href="./../flight_controller/pixhawk6x.html">Holybro Pixhawk 6X (FMUv6X)</a></li><li><a href="./../flight_controller/cuav_pixhawk_v6x.html">CUAV Pixhawk V6X (FMUv6X)</a></li></ul>',9)]))}const b=t(e,[["render",l]]);export{d as __pageData,b as default};
