import{_ as t}from"./chunks/difficulty_hard.N1nnc_xq.js";import{_ as e}from"./chunks/remote_control.ss1psCxn.js";import{_ as r}from"./chunks/acrobatic_fw.C-QcG-la.js";import{_ as o,c as d,a8 as _,o as c}from"./chunks/framework.BDnHobkS.js";const W=JSON.parse('{"title":"Режим Acro (фіксоване крило)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_fw/acro.md","filePath":"uk/flight_modes_fw/acro.md"}'),i={name:"uk/flight_modes_fw/acro.md"};function l(n,a,m,s,h,f){return c(),d("div",null,a[0]||(a[0]=[_('<h1 id="режим-acro-фіксоване-крило" tabindex="-1">Режим Acro (фіксоване крило) <a class="header-anchor" href="#режим-acro-фіксоване-крило" aria-label="Permalink to &quot;Режим Acro (фіксоване крило)&quot;">​</a></h1><p><img src="'+t+'" title="Hard to fly" width="30px"> <img src="'+e+'" title="Manual/Remote control required" width="30px"> </p><p><em>Acro mode</em> is the manual mode for performing acrobatic maneuvers e.g. rolls, flips, stalls and acrobatic figures.</p><p>Ручки крена, тангажу та риштування керують швидкістю кутової обертання навколо відповідних вісей, а керування тяги безпосередньо передається до розподілу керування. Коли стіки будуть відцентровані, апарат перестане обертатися, але залишиться у своїй поточній орієнтації (на боці, перевернутий, тощо) і рухатиметься відповідно до свого поточного імпульсу.</p><p><img src="'+r+'" alt="FW Manual Acrobatic Flight"></p><h2 id="технічнии-опис" tabindex="-1">Технічний опис <a class="header-anchor" href="#технічнии-опис" aria-label="Permalink to &quot;Технічний опис&quot;">​</a></h2><p>Ручний режим для виконання акробатичних маневрів, наприклад, перекидів, сальто, стійок і акробатичних фігур.</p><p>Вхідні сигнали стіків крену-тангажу перетворюються на команди кутової швидкості, які стабілізуються автопілотом. Передача дроселя здійснюється безпосередньо для керування розподілом.</p><h2 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="FW_ACRO_X_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_X_MAX">FW_ACRO_X_MAX</a></td><td>Максимальна швидкість обертання корпусу x (швидкість обертання корпусу x, яку контролер намагається досягти, якщо користувач вводить ролл-стік в режимі acro). За замовчуванням: 90 градусів.</td></tr><tr><td><a id="FW_ACRO_Y_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_Y_MAX">FW_ACRO_Y_MAX</a></td><td>Максимальна швидкість тіла y в режимі acro (максимальна швидкість тіла y, якої намагається досягти контролер, якщо користувач вводить пітч-стик у режимі acro). За замовчуванням: 90 градусів.</td></tr><tr><td><a id="FW_ACRO_Z_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_Z_MAX">FW_ACRO_Z_MAX</a></td><td>Максимальна швидкість обертання корпусу z в режимі acro (максимальна швидкість обертання корпусу, якої намагається досягти контролер, якщо користувач вводить стик рискання в режимі acro). За замовчуванням: 45 градусів.</td></tr><tr><td><a id="FW_ACRO_YAW_EN"></a><a href="./../advanced_config/parameter_reference.html#FW_ACRO_YAW_EN">FW_ACRO_YAW_EN</a></td><td>Enable yaw rate controller (if disabled the pilot commands directly the yaw actuator). <code>0</code>: Disabled (Default), <code>1</code>: Enabled.</td></tr></tbody></table>',10)]))}const g=o(i,[["render",l]]);export{W as __pageData,g as default};
