import{_ as a}from"./chunks/position_fixed.t2m3lWVy.js";import{_ as t,c as i,a8 as l,o as n}from"./chunks/framework.BDnHobkS.js";const u=JSON.parse('{"title":"Режим посадки (фіксоване крило)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_fw/land.md","filePath":"uk/flight_modes_fw/land.md"}'),d={name:"uk/flight_modes_fw/land.md"};function o(r,e,s,h,c,_){return n(),i("div",null,e[0]||(e[0]=[l('<h1 id="режим-посадки-фіксоване-крило" tabindex="-1">Режим посадки (фіксоване крило) <a class="header-anchor" href="#режим-посадки-фіксоване-крило" aria-label="Permalink to &quot;Режим посадки (фіксоване крило)&quot;">​</a></h1><img src="'+a+'" title="Position estimate required (e.g. GPS)" width="30px"><p>The <em>Land</em> flight mode causes the vehicle to descend at the position where the mode was engaged, following a circular path until touchdown. Після посадки транспортний засіб вимкнеться через короткий проміжок часу (за замовчуванням).</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Fixed-wing <em>land mode</em> should only be used in an <strong>emergency</strong>! Транспортний засіб опуститься навколо поточного місцезнаходження незалежно від придатності підлягаючої території, і сяде на землю, слідуючи круговому шляху польоту.</p><p>Where possible, instead use <a href="./../flight_modes_fw/return.html">Return mode</a> with a predefined <a href="./../flight_modes_fw/mission.html#mission-landing">Fixed-wing mission landing</a>.</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>Режим автоматичний - для керування апаратом не потрібно втручання користувача.</li><li>Режим потребує принаймні дійсної локальної оцінки позиції (не потребує глобальної позиції). <ul><li>Літаючі транспортні засоби не можуть переключатися на цей режим без глобального положення.</li><li>Літаючі транспортні засоби перейдуть в режим аварійної безпеки, якщо втратять оцінку положення.</li></ul></li><li>Режим перешкоджає зброюванню (транспортний засіб повинен бути зброєний при переході на цей режим).</li><li>Перемикачі керування RC можуть використовуватися для зміни режимів польоту на будь-якому транспортному засобі.</li><li>Рух стіків радіокерування ігнорується.</li><li>The mode can be triggered using the <a href="https://mavlink.io/en/messages/common.html#MAV_CMD_NAV_LAND" target="_blank" rel="noreferrer">MAV_CMD_NAV_LAND</a> MAVLink command, or by explicitly switching to Land mode.</li></ul></div><h2 id="технічнии-підсумок" tabindex="-1">Технічний підсумок <a class="header-anchor" href="#технічнии-підсумок" aria-label="Permalink to &quot;Технічний підсумок&quot;">​</a></h2><p>Режим посадки змушує літальний засіб прямувати по спускаючій круговій траєкторії (шнуру) до посадки.</p><p>When the mode is engaged, the vehicle starts to loiter around the current vehicle position with loiter radius <a href="#NAV_LOITER_RAD">NAV_LOITER_RAD</a> and begins to descend with a constant descent speed. The descent speed is calculated using <a href="#FW_LND_ANG">FW_LND_ANG</a> and the set landing airspeed <a href="#FW_LND_AIRSPD">FW_LND_AIRSPD</a>. The vehicle will flare if configured to do so (see <a href="./../flight_modes_fw/mission.html#flaring-roll-out">Flaring</a>), and otherwise proceed circling with the constant descent rate until landing is detected.</p><p><a href="./../flight_modes_fw/mission.html#automatic-abort">Manual nudging</a> and <a href="./../flight_modes_fw/mission.html#nudging">automatic land abort</a> are not available in land mode.</p><h3 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h3><p>Поведінку режиму приземлення можна налаштувати за допомогою наведених нижче параметрів.</p><table tabindex="0"><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="NAV_LOITER_RAD"></a><a href="./../advanced_config/parameter_reference.html#NAV_LOITER_RAD">NAV_LOITER_RAD</a></td><td>Радіус блукання, який контролер відстежує протягом усієї послідовності посадки.</td></tr><tr><td><a id="FW_LND_ANG"></a><a href="./../advanced_config/parameter_reference.html#FW_LND_ANG">FW_LND_ANG</a></td><td>Виставте кут шляху пункту налаштувань.</td></tr><tr><td><a id="FW_LND_AIRSPD"></a><a href="./../advanced_config/parameter_reference.html#FW_LND_AIRSPD">FW_LND_AIRSPD</a></td><td>Налаштування швидкості.</td></tr></tbody></table><h2 id="дивіться-також" tabindex="-1">Дивіться також <a class="header-anchor" href="#дивіться-також" aria-label="Permalink to &quot;Дивіться також&quot;">​</a></h2><ul><li><a href="./../flight_modes_mc/land.html">Режим посадки (MC)</a></li><li><a href="./../flight_modes_vtol/land.html">Land Mode (VTOL)</a></li></ul>',14)]))}const g=t(d,[["render",o]]);export{u as __pageData,g as default};
