import{_ as t}from"./chunks/difficulty_easy.76S4H_Nf.js";import{_ as a}from"./chunks/remote_control.ss1psCxn.js";import{_ as r}from"./chunks/position_fixed.t2m3lWVy.js";import{_ as i}from"./chunks/position_fw.BSO5zQGu.js";import{_,c as o,a8 as d,o as l}from"./chunks/framework.BDnHobkS.js";const R=JSON.parse('{"title":"Режим позиції (фіксоване крило)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_fw/position.md","filePath":"uk/flight_modes_fw/position.md"}'),n={name:"uk/flight_modes_fw/position.md"};function s(h,e,m,c,f,p){return l(),o("div",null,e[0]||(e[0]=[d('<h1 id="режим-позиціі-фіксоване-крило" tabindex="-1">Режим позиції (фіксоване крило) <a class="header-anchor" href="#режим-позиціі-фіксоване-крило" aria-label="Permalink to &quot;Режим позиції (фіксоване крило)&quot;">​</a></h1><p><img src="'+t+'" title="Easy to fly" width="30px"> <img src="'+a+'" title="Manual/Remote control required" width="30px"> <img src="'+r+'" title="Position fix required (e.g. GPS)" width="30px"></p><p><em>Position mode</em> is the easiest and safest manual mode. Підтримується на транспортних засобах, які мають прогноз позиціонування (наприклад, GPS). Це полегшує пілотам контроль висоти транспортного засобу, і зокрема досягати і підтримувати фіксовану висоту. Режим буде утримувати курс транспортного засобу від вітру. Швидкість активно контролюється, якщо встановлений датчик швидкості.</p><p>The vehicle performs a <a href="https://en.wikipedia.org/wiki/Coordinated_flight" target="_blank" rel="noreferrer">coordinated turn</a> if the roll sticks are non-zero, while the pitch stick controls the rate of ascent/descent. Засувка визначає швидкість повітря — при 50% засувки літак буде утримувати свою поточну висоту з заданою крейсерською швидкістю.</p><p>Коли всі стіки управління відпущені/центровані (без крену, тангажу, курсу та близько 50% керування газом), літак повернеться до прямого, рівного польоту та збереже свою поточну висоту та траєкторію польоту не залежно від вітру. This makes it easy to recover from any problems when flying. Крен та тангаж - кутово-керовані (тому неможливо перевалюватися або робити петлю транспортному засобу).</p><p>Стік повороту може бути використана для збільшення/зменшення кута приводу автомобіля на поворотах. Якщо контролер фіксований у центрі, то він самостійно здійснює координацію повороту, що означає, що він застосовує необхідну швидкість розвороту для поточного кута крену, щоб виконати плавний поворот. The diagram below shows the mode behaviour visually (for a <a href="./../getting_started/rc_transmitter_receiver.html#transmitter_modes">mode 2 transmitter</a>).</p><p><img src="'+i+'" alt="FW Position Mode"></p><h2 id="технічнии-опис" tabindex="-1">Технічний опис <a class="header-anchor" href="#технічнии-опис" aria-label="Permalink to &quot;Технічний опис&quot;">​</a></h2><p>Position mode is like <a href="./../flight_modes_fw/altitude.html">Stabilized mode</a> but with course stabilization. Швидкість також стабілізується, якщо встановлений датчик швидкості.</p><ul><li>Центровані вхідні показники крену/тангажу/рискання (в межах дедбенду): <ul><li>Автопілот рівномірно підтримує транспортний засіб і утримує висоту, швидкість і курс над землею.</li></ul></li><li>Зовнішній центр: <ul><li>Стік регулює висота польоту.</li><li>Резервний стік керує швидкістю літального апарату, якщо підключений датчик швидкості. Without an airspeed sensor the vehicle will fly level at trim throttle (<a href="./../advanced_config/parameter_reference.html#FW_THR_TRIM">FW_THR_TRIM</a>), increasing or decreasing throttle as needed to climb or descend.</li><li>Стік керування використовує кут крена. Autopilot will maintain <a href="https://en.wikipedia.org/wiki/Coordinated_flight" target="_blank" rel="noreferrer">coordinated flight</a>.</li><li>Стік крену додає додатковий значення швидкості рискання (додається до розрахованого автопілотом для підтримки координованого польоту). Може бути використаний для ручної зміни кута рискання безпілотного засобу.</li></ul></li><li>Потрібен ручний ввід управління (наприклад, за допомогою пульта дистанційного керування, джойстика).</li><li>Необхідне джерело вимірювання висоти (зазвичай барометр або GPS)</li></ul><h2 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h2><p>Режим впливає на наступні параметри:</p><table tabindex="0"><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="FW_AIRSPD_MIN"></a><a href="./../advanced_config/parameter_reference.html#FW_AIRSPD_MIN">FW_AIRSPD_MIN</a></td><td>Мінімальна швидкість. За замовчуванням: 10 м/с.</td></tr><tr><td><a id="FW_AIRSPD_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_AIRSPD_MAX">FW_AIRSPD_MAX</a></td><td>Максимальна швидкість. За замовчуванням: 20 м/с.</td></tr><tr><td><a id="FW_AIRSPD_TRIM"></a><a href="./../advanced_config/parameter_reference.html#FW_AIRSPD_TRIM">FW_AIRSPD_TRIM</a></td><td>Крейсерська швидкість. За замовчуванням: 15 м/с.</td></tr><tr><td><a id="FW_MAN_P_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_P_MAX">FW_MAN_P_MAX</a></td><td>Установлення максимального кроку в режимі стабілізації кута нахилу. За замовчуванням: 45 градусів.</td></tr><tr><td><a id="FW_MAN_R_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_R_MAX">FW_MAN_R_MAX</a></td><td>Максимальне значення крена в режимі стабілізації кута нахилу. За замовчуванням: 45 градусів.</td></tr><tr><td><a id="FW_T_CLMB_R_SP"></a><a href="./../advanced_config/parameter_reference.html#FW_T_CLMB_R_SP">FW_T_CLMB_R_SP</a></td><td>Максимальна задана швидкість підйому. За замовчуванням: 3 м/с.</td></tr><tr><td><a id="FW_T_SINK_R_SP"></a><a href="./../advanced_config/parameter_reference.html#FW_T_SINK_R_SP">FW_T_SINK_R_SP</a></td><td>Максимальне значення зниження швидкості. За замовчуванням: 2 м/с.</td></tr><tr><td><a id="FW_PN_R_SLEW_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_PN_R_SLEW_MAX">FW_PN_R_SLEW_MAX</a></td><td>Обмеження швидкості нахилу вихідного значення. За замовчуванням: 90 °/с.</td></tr></tbody></table>',13)]))}const A=_(n,[["render",s]]);export{R as __pageData,A as default};
