import{_ as t}from"./chunks/difficulty_medium.CPaRe1i7.js";import{_ as a}from"./chunks/remote_control.ss1psCxn.js";import{_ as i}from"./chunks/stabilized_fw.Cp3EcCt4.js";import{_ as r,c as d,a8 as l,o}from"./chunks/framework.BDnHobkS.js";const F=JSON.parse('{"title":"Стабілізований режим (фіксоване крило)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_fw/stabilized.md","filePath":"uk/flight_modes_fw/stabilized.md"}'),n={name:"uk/flight_modes_fw/stabilized.md"};function s(_,e,h,m,c,f){return o(),d("div",null,e[0]||(e[0]=[l('<h1 id="стабілізовании-режим-фіксоване-крило" tabindex="-1">Стабілізований режим (фіксоване крило) <a class="header-anchor" href="#стабілізовании-режим-фіксоване-крило" aria-label="Permalink to &quot;Стабілізований режим (фіксоване крило)&quot;">​</a></h1><p><img src="'+t+'" title="Medium difficulty to fly" width="30px"> <img src="'+a+'" title="Manual/Remote control required" width="30px"></p><p><em>Stabilized mode</em> is a manual mode were centering the sticks levels the vehicle attitude (roll and pitch) and maintains the horizontal posture.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><em>Stabilized mode</em> is similar to <a href="./../flight_modes_fw/altitude.html">Altitude mode</a> in that releasing the sticks levels the vehicle, but unlike altitude mode it does not maintain altitude or heading. It is much easier to fly than <a href="./../flight_modes_fw/manual.html">Manual mode</a> because you can&#39;t roll or flip it, and if needed it is easy to level the vehicle (by centering the control sticks).</p></div><p>The vehicle climbs/descends based on pitch and throttle input and performs a <a href="https://en.wikipedia.org/wiki/Coordinated_flight" target="_blank" rel="noreferrer">coordinated turn</a> if the roll stick is non-zero. Курс і крен контролюються кутом (ви не можете перекотитися чи зробити петлю).</p><p>Транспортний засіб буде летіти інертно, якщо рівень газу знижується до 0% (двигун зупиняється). Для виконання повороту команда повинна бути утримана протягом маневру, оскільки якщо випускати рульове управління, літак зупинить поворот і врівноважить себе (так само відносно тангажу).</p><p>Стік повороту може бути використана для збільшення/зменшення кута приводу автомобіля на поворотах. Якщо контролер фіксований у центрі, то він самостійно здійснює координацію повороту, що означає, що він застосовує необхідну швидкість розвороту для поточного кута крену, щоб виконати плавний поворот.</p><p>The diagram below shows the mode behaviour visually (for a <a href="./../getting_started/rc_transmitter_receiver.html#transmitter_modes">mode 2 transmitter</a>).</p><p><img src="'+i+'" alt="FW Manual Flight"></p><h2 id="технічнии-опис" tabindex="-1">Технічний опис <a class="header-anchor" href="#технічнии-опис" aria-label="Permalink to &quot;Технічний опис&quot;">​</a></h2><p>Ручний режим, де центровані стіки крену/тангажурівняють положення транспортного засобу. Курс і висота транспортного засобу не підтримуються, і він може дрейфувати через вітер.</p><ul><li>Центровані стіки крену/тангажу/рискання (в межах мертвої зони) встановлюють повітряний апарат на прямолінійний та горизонтальний польот. Курс і висота транспортного засобу не підтримуються, і він може дрейфувати через вітер.</li><li>Стік керування використовує кут крена. Автопілот буде підтримувати <a href="https://en.wikipedia.org/wiki/Coordinated_flight">координований польот</a>.</li><li>Pitch stick controls pitch angle around the defined offset <a href="./../advanced_config/parameter_reference.html#FW_PSP_OFF">FW_PSP_OFF</a></li><li>Ручка дроселя керує дроселем безпосередньо.</li><li>Стік крену додає додатковий значення швидкості рискання (додається до розрахованого автопілотом для підтримки координованого польоту). Може бути використаний для ручної зміни кута рискання безпілотного засобу.</li><li>Потрібен ручний ввід управління (наприклад, за допомогою пульта дистанційного керування, джойстика).</li></ul><h2 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h2><p>Режим впливає на наступні параметри:</p><table tabindex="0"><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="FW_MAN_P_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_P_MAX">FW_MAN_P_MAX</a></td><td>Максимальний крен для керування вручну в режимі стабілізації кута нахилу. За замовчуванням: 45 градусів.</td></tr><tr><td><a id="FW_MAN_R_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_R_MAX">FW_MAN_R_MAX</a></td><td>Максимальне значення крена для керування в ручному режимі в режимі стабілізації кута нахилу. За замовчуванням: 45 градусів.</td></tr><tr><td><a id="FW_MAN_YR_MAX"></a><a href="./../advanced_config/parameter_reference.html#FW_MAN_YR_MAX">FW_MAN_YR_MAX</a></td><td>Максимальна вручну додана швидкість крену. За замовчуванням: 30 градусів на секунду.</td></tr><tr><td><a id="FW_PSP_OFF"></a><a href="./../advanced_config/parameter_reference.html#FW_PSP_OFF">FW_PSP_OFF</a></td><td>Зсув вихідної установки крена (крен на рівному польоті). За замовчуванням: 0 градусів.</td></tr></tbody></table>',15)]))}const M=r(n,[["render",s]]);export{F as __pageData,M as default};
