import{_ as t}from"./chunks/position_fixed.t2m3lWVy.js";import{_ as a,c as o,a8 as i,o as l}from"./chunks/framework.BDnHobkS.js";const p=JSON.parse('{"title":"Режим Зависання або Hold (Мультикоптер)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_mc/hold.md","filePath":"uk/flight_modes_mc/hold.md"}'),d={name:"uk/flight_modes_mc/hold.md"};function r(h,e,s,_,n,m){return l(),o("div",null,e[0]||(e[0]=[i('<h1 id="режим-зависання-або-hold-мультикоптер" tabindex="-1">Режим Зависання або Hold (Мультикоптер) <a class="header-anchor" href="#режим-зависання-або-hold-мультикоптер" aria-label="Permalink to &quot;Режим Зависання або Hold (Мультикоптер)&quot;">​</a></h1><img src="'+t+'" title="Position fix required (e.g. GPS)" width="30px"><p>The <em>Hold</em> flight mode causes the vehicle to stop and hover at its current GPS position and altitude.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><em>Hold mode</em> can be used to pause a mission or to help you regain control of a vehicle in an emergency. Зазвичай він активується за допомогою наперед заданого перемикача.</p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>Режим автоматичний - для керування апаратом не потрібно втручання користувача.</li><li>Режим вимагає глобальної оцінки 3D-позиції (з GPS або виведеної з <a href="./../ros/external_position_estimation.html#enabling-auto-modes-with-a-local-position">локальної позиції</a>). <ul><li>Літаючі транспортні засоби не можуть переключатися на цей режим без глобального положення.</li><li>Літаючі транспортні засоби перейдуть в режим аварійної безпеки, якщо втратять оцінку положення.</li><li>Роззброєні транспортні засоби можуть переключатися в режим без дійсної оцінки позиції, але не можуть озброюватися.</li></ul></li><li>Режим вимагає, щоб швидкість вітру та час польоту були в межах допустимих значень (вказано через параметри).</li><li>Перемикачі керування RC можуть використовуватися для зміни режимів польоту на будь-якому транспортному засобі.</li><li>Рух палиць дистанційного керування буде <a href="#COM_RC_OVERRIDE">за замовчуванням</a> змінювати транспортний засіб на <a href="./../flight_modes_mc/position.html">режим позиції</a>, якщо не виникне критична аварія батареї.</li></ul></div><h2 id="технічнии-підсумок" tabindex="-1">Технічний підсумок <a class="header-anchor" href="#технічнии-підсумок" aria-label="Permalink to &quot;Технічний підсумок&quot;">​</a></h2><p>Транспортний засіб ширяє у поточному положенні на поточній висоті. The vehicle will first ascend to <a href="#NAV_MIN_LTR_ALT">NAV_MIN_LTR_ALT</a> if the mode is engaged below this altitude.</p><p>RC stick movement will change the vehicle to <a href="./../flight_modes_mc/position.html">Position mode</a> (by <a href="#COM_RC_OVERRIDE">default</a>).</p><h3 id="параметри" tabindex="-1">Параметри <a class="header-anchor" href="#параметри" aria-label="Permalink to &quot;Параметри&quot;">​</a></h3><p>Поведінку режиму утримання можна налаштувати за допомогою наведених нижче параметрів.</p><table tabindex="0"><thead><tr><th>Параметр</th><th>Опис</th></tr></thead><tbody><tr><td><a id="NAV_MIN_LTR_ALT"></a><a href="./../advanced_config/parameter_reference.html#NAV_MIN_LTR_ALT">NAV_MIN_LTR_ALT</a></td><td>Це мінімальна висота над домашньою позицією, яку система завжди буде дотримуватися в режимі утримання, якщо перемикається в цей режим без вказівки висоти (наприклад, за допомогою перемикача на дистанційному керуванні).</td></tr><tr><td><a id="COM_RC_OVERRIDE"></a><a href="./../advanced_config/parameter_reference.html#COM_RC_OVERRIDE">COM_RC_OVERRIDE</a></td><td>Controls whether stick movement on a multicopter (or VTOL in MC mode) causes a mode change to <a href="./../flight_modes_mc/position.html">Position mode</a>. Це можна окремо увімкнути для автоматичних режимів та для режиму поза бортом, і в автоматичних режимах воно включено за замовчуванням.</td></tr><tr><td><a id="COM_RC_STICK_OV"></a><a href="./../advanced_config/parameter_reference.html#COM_RC_STICK_OV">COM_RC_STICK_OV</a></td><td>Кількість рухів стиків, яка викликає перехід у <a href="./../flight_modes_mc/position.html">режим Положення</a> (якщо <a href="#COM_RC_OVERRIDE">COM_RC_OVERRIDE</a> увімкнено).</td></tr></tbody></table><h2 id="дивіться-також" tabindex="-1">Дивіться також <a class="header-anchor" href="#дивіться-також" aria-label="Permalink to &quot;Дивіться також&quot;">​</a></h2><p><a href="./../flight_modes_fw/hold.html">Hold Mode (FW)</a></p>',13)]))}const u=a(d,[["render",r]]);export{p as __pageData,u as default};
