import{_ as t,c as l,a8 as a,o as i}from"./chunks/framework.BDnHobkS.js";const _=JSON.parse('{"title":"Режими польоту (VTOL)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flight_modes_vtol/index.md","filePath":"uk/flight_modes_vtol/index.md"}'),o={name:"uk/flight_modes_vtol/index.md"};function r(d,e,h,s,n,f){return i(),l("div",null,e[0]||(e[0]=[a('<h1 id="режими-польоту-vtol" tabindex="-1">Режими польоту (VTOL) <a class="header-anchor" href="#режими-польоту-vtol" aria-label="Permalink to &quot;Режими польоту (VTOL)&quot;">​</a></h1><p>Режими польоту забезпечують автопілот підтримку для полегшення ручного польоту транспортного засобу, автоматизацію загальних завдань, таких як зльот та посадка, виконання автономних місій або делегування керування польотом зовнішній системі.</p><p>Вертикально-взлітно-посадкові (VTOL) транспортні засоби можуть літати як багтроторні апарати, так і літаки, і, як правило, мають точно таке ж саме поведінку, як і відповідний тип транспортного засобу:</p><p>Поведінка режимів польоту, специфічних для VTOL, розглядається в таких випадках:</p><ul><li><a href="./../flight_modes_vtol/land.html">Режим «Посадка»</a>: VTOL, що літає як літак, перейде в режим багтроторного літака перед посадкою.</li><li><a href="./../flight_modes_vtol/mission.html">Режим «Місія»</a>: Місії підтримують специфічні для ВВП команди місій для зльоту, посадки та переходу між типами транспортних засобів.</li><li><a href="./../flight_modes_vtol/return.html">Режим «Повернення»</a>: VTOL транспортні засоби діють трохи інакше при поверненні, якщо вони повертаються як багтроторний літак або літак, ніж відповідні транспортні засоби.</li></ul><p>Для інших режимів польоту дивіться специфічну для транспортного засобу поведінку:</p><ul><li><a href="./../flight_modes_mc/">Режими польоту (Мультикоптер)</a></li><li><a href="./../flight_modes_fw/">Режими польоту (Фіксовані крила)</a></li></ul><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="./../config/flight_mode.html">Базова конфігурація &gt; Режими польоту</a> - Як відповідати перемикачі керування RC конкретним режимам польоту</li></ul>',9)]))}const c=t(o,[["render",r]]);export{_ as __pageData,c as default};
