import{_ as t}from"./chunks/rally_points.CKYuS8cy.js";import{_ as a,c as o,a8 as n,o as r}from"./chunks/framework.BDnHobkS.js";const u=JSON.parse('{"title":"Точки безпеки (точки збору)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/flying/plan_safety_points.md","filePath":"uk/flying/plan_safety_points.md"}'),l={name:"uk/flying/plan_safety_points.md"};function i(s,e,h,p,d,c){return r(),o("div",null,e[0]||(e[0]=[n('<h1 id="точки-безпеки-точки-збору" tabindex="-1">Точки безпеки (точки збору) <a class="header-anchor" href="#точки-безпеки-точки-збору" aria-label="Permalink to &quot;Точки безпеки (точки збору)&quot;">​</a></h1><p>Safety points are alternative <a href="./../flight_modes/return.html">Return Mode</a> destinations/landing points. When enabled, the vehicle will choose the <em>closest return destination</em> of: home location, mission landing pattern or a <em>safety point</em>.</p><p><img src="'+t+'" alt="Safety Points"></p><h2 id="створення-визначення-точок-безпеки" tabindex="-1">Створення / визначення точок безпеки <a class="header-anchor" href="#створення-визначення-точок-безпеки" aria-label="Permalink to &quot;Створення / визначення точок безпеки&quot;">​</a></h2><p>Safety points are created in <em>QGroundControl</em> (which calls them &quot;Rally Points&quot;).</p><p>На загальному рівні:</p><ol><li>Open <strong>QGroundControl &gt; Plan View</strong></li><li>Select the <strong>Rally</strong> tab/button on the <em>Plan Editor</em> (right of screen).</li><li>Select the <strong>Rally Point</strong> button on the toolbar (left of screen).</li><li>Натисніть будь-де на карті, щоб додати точку збору/безпеки. <ul><li>The <em>Plan Editor</em> displays and lets you edit the current rally point (shown as a green <strong>R</strong> on the map).</li><li>You can select another rally point (shown as a more orange/yellow <strong>R</strong> on the map) to edit it instead.</li></ul></li><li>Select the <strong>Upload Required</strong> button to upload the rally points (along with any <a href="./../flying/missions.html">mission</a> and <a href="./../flying/geofence.html">geofence</a>) to the vehicle.</li></ol><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>More complete documentation can be found in the <em>QGroundControl User Guide</em>: <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/plan_view/plan_rally_points.html" target="_blank" rel="noreferrer">Plan View - Rally Points</a>.</p></div><h2 id="використання-точок-безпеки" tabindex="-1">Використання точок безпеки <a class="header-anchor" href="#використання-точок-безпеки" aria-label="Permalink to &quot;Використання точок безпеки&quot;">​</a></h2><p>Safety points are not enabled by default (there are a number of different <a href="./../flight_modes/return.html#return_types">Return Mode Types</a>).</p><p>Щоб активувати точки безпеки:</p><ol><li><a href="./../advanced_config/parameters.html">Use the QGroundControl Parameter Editor</a> to set parameter: <a href="./../advanced_config/parameter_reference.html#RTL_TYPE">RTL_TYPE=3</a>.</li></ol>',12)]))}const g=a(l,[["render",i]]);export{u as __pageData,g as default};
