import{_ as e,a,b as r,c as l}from"./chunks/settings_view_general_rtk_gps.Is75R7C8.js";import{_ as n,c as s,a8 as i,o}from"./chunks/framework.BDnHobkS.js";const _=JSON.parse('{"title":"RTK GNSS (GPS)","description":"","frontmatter":{},"headers":[],"relativePath":"uk/gps_compass/rtk_gps.md","filePath":"uk/gps_compass/rtk_gps.md"}'),d={name:"uk/gps_compass/rtk_gps.md"};function c(h,t,g,p,m,u){return o(),s("div",null,t[0]||(t[0]=[i('<h1 id="rtk-gnss-gps" tabindex="-1">RTK GNSS (GPS) <a class="header-anchor" href="#rtk-gnss-gps" aria-label="Permalink to &quot;RTK GNSS (GPS)&quot;">​</a></h1><p><a href="https://en.wikipedia.org/wiki/Real_Time_Kinematic" target="_blank" rel="noreferrer">Real Time Kinematic (RTK)</a> GNSS/GPS systems provide centimeter-level accuracy, allowing PX4 to be used in applications like precision surveying (where pinpoint accuracy is essential).</p><p>This feature requires <em>QGroundControl</em> running on a laptop/PC and a vehicle with a WiFi or Telemetry radio link to the ground station laptop.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Some RTK GNSS setups can provide yaw/heading information, as an alternative to the compass:</p><ul><li><a href="./../gps_compass/u-blox_f9p_heading.html">RTK GPS Heading with Dual u-blox F9P</a>.</li><li>GPS безпосередньо виводить курс (див. таблицю нижче).</li></ul></div><h2 id="пристроі-що-підтримуються" tabindex="-1">Пристрої, що підтримуються <a class="header-anchor" href="#пристроі-що-підтримуються" aria-label="Permalink to &quot;Пристрої, що підтримуються&quot;">​</a></h2><p>PX4 supports the <a href="https://www.u-blox.com/en/product/neo-m8p" target="_blank" rel="noreferrer">u-blox M8P</a>, <a href="https://www.u-blox.com/en/product/zed-f9p-module" target="_blank" rel="noreferrer">u-blox F9P</a> and the <a href="https://www.trimble.com/Precision-GNSS/MB-Two-Board.aspx" target="_blank" rel="noreferrer">Trimble MB-Two</a> GPS, and products that incorporate them.</p><p>Список сумісних пристроїв RTK нижче, які очікуються для роботи з PX4 (він виключає припинені пристрої). Таблиця вказує пристрої, які також виводять курсову відмітку, а також можуть надавати курсову відмітку, коли використовуються дві одиниці на транспортному засобі. Він також відзначає пристрої, які підключаються через CAN шину, та ті, які підтримують PPK (пост-процесуальну кінематику).</p><table tabindex="0"><thead><tr><th style="text-align:left;">Пристрій</th><th style="text-align:center;">GPS</th><th style="text-align:center;">Компас</th><th style="text-align:center;"><a href="./../dronecan/">DroneCAN</a></th><th style="text-align:center;"><a href="#configuring-gps-as-yaw-heading-source">GPS Yaw</a></th><th style="text-align:center;">PPK</th></tr></thead><tbody><tr><td style="text-align:left;"><a href="./../dronecan/ark_rtk_gps.html">ARK RTK GPS</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">BMM150</td><td style="text-align:center;">✓</td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../dronecan/ark_mosaic__rtk_gps.html">ARK MOSAIC-X5 RTK GPS</a></td><td style="text-align:center;">Mosaic-X5</td><td style="text-align:center;">IIS2MDC</td><td style="text-align:center;">✓</td><td style="text-align:center;"><a href="./../gps_compass/septentrio.html#gnss-based-heading">Septentrio Dual Antenna</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_cuav_c-rtk.html">CUAV C-RTK GPS</a></td><td style="text-align:center;">M8P/M8N</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_cuav_c-rtk2.html">CUAV C-RTK2</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_cuav_c-rtk-9ps.html">CUAV C-RTK 9Ps GPS</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">RM3100</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_cuav_c-rtk.html">CUAV C-RTK2 PPK/RTK GNSS</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">RM3100</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_hex_hereplus.html">CubePilot Here+ RTK GPS</a></td><td style="text-align:center;">M8P</td><td style="text-align:center;">HMC5983</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://www.cubepilot.org/#/here/here3" target="_blank" rel="noreferrer">CubePilot Here3 CAN GNSS GPS (M8N)</a></td><td style="text-align:center;">M8P</td><td style="text-align:center;">ICM20948</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://store-drotek.com/911-sirius-rtk-gnss-rover-f9p.html" target="_blank" rel="noreferrer">Drotek SIRIUS RTK GNSS ROVER (F9P)</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">RM3100</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_gem1305.html">DATAGNSS GEM1305 RTK Receiver</a></td><td style="text-align:center;">TAU951M</td><td style="text-align:center;">✘</td><td style="text-align:center;"></td><td style="text-align:center;">✘</td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_fem_mini2.html">Femtones MINI2 Receiver</a></td><td style="text-align:center;">FB672, FB6A0</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_freefly.html">Freefly RTK GPS</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">IST8310</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../dronecan/holybro_h_rtk_zed_f9p_gps.html">Holybro H-RTK ZED-F9P RTK Rover (DroneCAN variant)</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">RM3100</td><td style="text-align:center;">✓</td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://holybro.com/collections/h-rtk-gps/products/h-rtk-zed-f9p-rover" target="_blank" rel="noreferrer">Holybro H-RTK ZED-F9P RTK Rover</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">RM3100</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://holybro.com/products/h-rtk-f9p-ultralight" target="_blank" rel="noreferrer">Holybro H-RTK F9P Ultralight</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">IST8310</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_holybro_h-rtk-f9p.html">Holybro H-RTK F9P Helical or Base</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">IST8310</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://holybro.com/products/dronecan-h-rtk-f9p-helical" target="_blank" rel="noreferrer">Holybro DroneCAN H-RTK F9P Helical</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">BMM150</td><td style="text-align:center;">✓</td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_holybro_h-rtk-f9p.html">Holybro H-RTK F9P Rover Lite</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">IST8310</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://holybro.com/products/dronecan-h-rtk-f9p-rover" target="_blank" rel="noreferrer">Holybro DroneCAN H-RTK F9P Rover</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">BMM150</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_holybro_h-rtk-m8p.html">Holybro H-RTK M8P GNSS</a></td><td style="text-align:center;">M8P</td><td style="text-align:center;">IST8310</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_holybro_unicore_um982.html">Holybro H-RTK Unicore UM982 GPS</a></td><td style="text-align:center;">UM982</td><td style="text-align:center;">IST8310</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/rtk_gps_holybro_unicore_um982.html#enable-gps-heading-yaw">Unicore Dual Antenna</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_locosys_r1.html">LOCOSYS Hawk R1</a></td><td style="text-align:center;">MC-1612-V2b</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_locosys_r2.html">LOCOSYS Hawk R2</a></td><td style="text-align:center;">MC-1612-V2b</td><td style="text-align:center;">IST8310</td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://store.mrobotics.io/product-p/m10020d.htm" target="_blank" rel="noreferrer">mRo u-blox ZED-F9 RTK L1/L2 GPS</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://docs.raccoonlab.co/guide/gps_mag_baro/gps_l1_l2_zed_f9p.html" target="_blank" rel="noreferrer">RaccoonLab L1/L2 ZED-F9P</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">RM3100</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://docs.raccoonlab.co/guide/gps_mag_baro/gnss_external_antenna_f9p_v320.html" target="_blank" rel="noreferrer">RaccoonLab L1/L2 ZED-F9P with external antenna</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">RM3100</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/septentrio_asterx-rib.html">Septentrio AsteRx-m3 Pro</a></td><td style="text-align:center;">AsteRx</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/septentrio.html#gnss-based-heading">Septentrio Dual Antenna</a></td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/septentrio_mosaic-go.html">Septentrio mosaic-go</a></td><td style="text-align:center;">mosaic X5 / mosaic H</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/septentrio.html#gnss-based-heading">Septentrio Dual Antenna</a></td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:left;"><a href="https://store-drotek.com/911-sirius-rtk-gnss-rover-f9p.html" target="_blank" rel="noreferrer">SIRIUS RTK GNSS ROVER (F9P)</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="https://www.sparkfun.com/products/15136" target="_blank" rel="noreferrer">SparkFun GPS-RTK2 Board - ZED-F9P</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;"><a href="./../gps_compass/u-blox_f9p_heading.html">Dual F9P</a></td><td style="text-align:center;"></td></tr><tr><td style="text-align:left;"><a href="./../gps_compass/rtk_gps_trimble_mb_two.html">Trimble MB-Two</a></td><td style="text-align:center;">F9P</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr></tbody></table><p>Примітки:</p><ul><li>✓ or a specific part number indicate that a features is supported, while ✘ or empty show that the feature is not supported. &quot;?&quot; означає &quot;невідомо&quot;.</li><li>Where possible and relevant the part name is used (i.e. ✓ in the GPS column indicates that a GPS module is present but the part is not known).</li><li>Деякі RTK-модулі можна використовувати лише в певній ролі (база або ровер), тоді як інші можна використовувати як взаємозамінні.</li><li>У списку може бути відсутнє деяке зняте з виробництва обладнання, яке все ще підтримується. For example <a href="./../gps_compass/rtk_gps_hex_hereplus.html">CubePilot Here+ RTK GPS</a> is discontinued and may be removed from the list in a future release. Перевірте попередні версії, якщо тут не згадано модуль, який перестали випускати.</li></ul><h2 id="налаштування-конфігурація-розташування" tabindex="-1">Налаштування/Конфігурація розташування <a class="header-anchor" href="#налаштування-конфігурація-розташування" aria-label="Permalink to &quot;Налаштування/Конфігурація розташування&quot;">​</a></h2><p>RTK positioning requires a <em>pair</em> of <a href="#supported-devices">RTK GNSS devices</a>: a &quot;base&quot; for the ground station and a &quot;rover&quot; for the vehicle.</p><p>Крім того, вам знадобиться:</p><ul><li>A <em>laptop/PC</em> with QGroundControl (QGroundControl for Android/iOS do not support RTK)</li><li>Транспортний засіб із WiFi або телеметричним радіозв&#39;язком з ноутбуком.</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p><em>QGroundControl</em> with a base module can theoretically enable RTK GPS for multiple vehicles/rover modules. На момент написання цього випадку використання цей випадок не був протестований.</p></div><h3 id="налаштування-програмного-забезпечення" tabindex="-1">Налаштування програмного забезпечення <a class="header-anchor" href="#налаштування-програмного-забезпечення" aria-label="Permalink to &quot;Налаштування програмного забезпечення&quot;">​</a></h3><h4 id="модуль-rover-rtk-транспортнии" tabindex="-1">Модуль Rover RTK (Транспортний) <a class="header-anchor" href="#модуль-rover-rtk-транспортнии" aria-label="Permalink to &quot;Модуль Rover RTK (Транспортний)&quot;">​</a></h4><p>The connection method and cables/connectors required depends on the selected RTK module (and on the <a href="./../flight_controller/">flight controller</a>).</p><p>Більшість з&#39;єднані через порт GPS контролера польоту, так само, як будь-який інший модуль GPS. Some are connected to the <a href="./../can/">CAN</a> bus (i.e. using <a href="./../dronecan/">DroneCAN</a>).</p><p>See <a href="#supported-devices">documentation for the selected device</a>, general <a href="./../gps_compass/#hardware-setup">GNSS Hardware/Configuration Setup</a>, and <a href="./../dronecan/">DroneCAN</a> for more information on wiring and configuration.</p><h4 id="базовии-модуль-rtk-наземнии" tabindex="-1">Базовий модуль RTK (наземний) <a class="header-anchor" href="#базовии-модуль-rtk-наземнии" aria-label="Permalink to &quot;Базовий модуль RTK (наземний)&quot;">​</a></h4><p>Connect the base module to <em>QGroundControl</em> via USB. Модуль бази не повинен зміщуватися, коли його використовують.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Choose a position where the base module won&#39;t need to be moved, has a clear view of the sky, and is well separated from any buildings. Часто корисно підняти базовий GPS, використовуючи штатив або монтувавши його на дах.</p></div><h4 id="телеметріине-радіо-wifi" tabindex="-1">Телеметрійне радіо/WiFi <a class="header-anchor" href="#телеметріине-радіо-wifi" aria-label="Permalink to &quot;Телеметрійне радіо/WiFi&quot;">​</a></h4><p>The vehicle and ground control laptop must be connected via <a href="./../telemetry/">wifi or a radio telemetry link</a>.</p><p>The link <em>must</em> use the MAVLink 2 protocol as it makes more efficient use of the channel. This should be set by default, but if not, follow the <a href="#mavlink2">MAVLink2 configuration instructions</a> below.</p><h3 id="процес-підключення-rtk" tabindex="-1">Процес підключення RTK <a class="header-anchor" href="#процес-підключення-rtk" aria-label="Permalink to &quot;Процес підключення RTK&quot;">​</a></h3><p>Підключення RTK GPS насправді просте:</p><ol><li><p>Start <em>QGroundControl</em> and attach the base RTK GPS via USB to the ground station. Пристрій визнається автоматично.</p></li><li><p>Start the vehicle and make sure it is connected to <em>QGroundControl</em>.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p><em>QGroundControl</em> displays an RTK GPS status icon in the top icon bar while an RTK GPS device is connected (in addition to the normal GPS status icon). Іконка червона, поки налаштовується RTK, а потім змінюється на білу, коли RTK GPS активний. Ви можете натиснути на піктограму, щоб побачити поточний стан та точність RTK.</p></div></li></ol><p>:::</p><ol start="3"><li><p><em>QGroundControl</em> then starts the RTK setup process (known as &quot;Survey-In&quot;).</p><p>Survey-In - це процедура запуску для отримання точної оцінки положення базової станції. The process typically takes several minutes (it ends after reaching the minimum time and accuracy specified in the <a href="#rtk-gps-settings">RTK settings</a>).</p><p>Ви можете відстежити прогрес, натиснувши на піктограму стану RTK GPS.</p><p><img src="'+e+'" alt="survey-in"></p></li><li><p>Після завершення опитування:</p><ul><li><p>The RTK GPS icon changes to white and <em>QGroundControl</em> starts to stream position data to the vehicle:</p><p><img src="'+a+'" alt="RTK streaming"></p></li><li><p>Транспортний GPS переходить у режим RTK. The new mode is displayed in the <em>normal</em> GPS status icon (<code>3D RTK GPS Lock</code>):</p><p><img src="'+r+'" alt="RTK GPS Status"></p></li></ul></li></ol><h3 id="налаштування-gps-як-джерело-розділення-курсування" tabindex="-1">Налаштування GPS як Джерело розділення/Курсування <a class="header-anchor" href="#налаштування-gps-як-джерело-розділення-курсування" aria-label="Permalink to &quot;Налаштування GPS як Джерело розділення/Курсування&quot;">​</a></h3><p>GPS can be used as a source for yaw fusion when using a single device with two antenna where <em>yaw output is supported by the device</em>, or when using some <a href="./../gps_compass/u-blox_f9p_heading.html">RTK GPS Setups with Dual u-blox F9P</a>. Using GPS as a heading source has the benefit that yaw calculations are not impacted by magnetic interference.</p><p>Both approaches work comparing the time taken for a GNSS signal to reach two separated antennas. The minimum distance between antenna depends on the device but is of the order of 50 cm (check manufacturer documentation).</p><p>The devices that can be used are listed in this way are listed in the <strong>GPS Yaw</strong> column of the table above, such as <a href="./../gps_compass/septentrio_asterx-rib.html">Septentrio AsteRx-m3 Pro</a>, <a href="./../gps_compass/rtk_gps_holybro_unicore_um982.html">Holybro H-RTK Unicore UM982 GPS</a>, and <a href="./../gps_compass/rtk_gps_trimble_mb_two.html">Trimble MB-Two</a>. The links in the table take you to the device-specific PX4 configuration.</p><p>Generally when using a GNSS as a source of yaw information you will need to configure the following parameters:</p><table tabindex="0"><thead><tr><th>Параметр</th><th>Налаштування</th></tr></thead><tbody><tr><td><a href="./../advanced_config/parameter_reference.html#GPS_YAW_OFFSET">GPS_YAW_OFFSET</a></td><td>The angle made by the <em>baseline</em> (the line between the two GPS antennas) relative to the vehicle x-axis (front/back axis, as shown <a href="./../config/flight_controller_orientation.html#calculating-orientation">here</a>).</td></tr><tr><td><a href="./../advanced_config/parameter_reference.html#EKF2_GPS_CTRL">EKF2_GPS_CTRL</a></td><td>Set bit position 3 &quot;Dual antenna heading&quot; to <code>1</code> (i.e. add 8 to the parameter value).</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>If using this feature, all other configuration should be setup up as normal (e.g. <a href="./../gps_compass/rtk_gps.html#positioning-setup-configuration">RTK Positioning</a>).</p></div><h3 id="додаткова-конфігурація-px4" tabindex="-1">Додаткова конфігурація PX4 <a class="header-anchor" href="#додаткова-конфігурація-px4" aria-label="Permalink to &quot;Додаткова конфігурація PX4&quot;">​</a></h3><p>The following settings may need to be changed (using <em>QGroundControl</em>).</p><h4 id="налаштування-rtk-gps" tabindex="-1">Налаштування RTK GPS <a class="header-anchor" href="#налаштування-rtk-gps" aria-label="Permalink to &quot;Налаштування RTK GPS&quot;">​</a></h4><p>The RTK GPS settings are specified in the <em>QGroundControl</em> <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/settings_view/general.html#rtk_gps" target="_blank" rel="noreferrer">General Settings</a> (<strong>SettingsView &gt; General Settings &gt; RTK GPS</strong>).</p><p><img src="'+l+'" alt="RTK GPS Setup"></p><p>Ці параметри визначають мінімальну тривалість та мінімальну точність для завершення процесу налаштування RTK GPS (відомий як &quot;Survey-In).</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>You can save and reuse a base position in order to save time: perform Survey-In once, select <em>Use Specified Base Position</em> and press <strong>Save Current Base Position</strong> to copy in the values for the last survey. Значення будуть збережені після перезавантажень QGC до тих пір, поки їх не змінять.</p></div><h4 id="mavlink2" tabindex="-1">MAVLink2 <a class="header-anchor" href="#mavlink2" aria-label="Permalink to &quot;MAVLink2&quot;">​</a></h4><p>Протокол MAVLink2 повинен бути використаний, оскільки він ефективніше використовує канали з низькою пропускною здатністю. Це має бути увімкнено за замовчуванням на останніх збірках.</p><p>Для забезпечення використання MAVLink2:</p><ul><li>Update the telemetry module firmware to the latest version (see <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/firmware.html" target="_blank" rel="noreferrer">QGroundControl &gt; Setup &gt; Firmware</a>).</li><li>Set <a href="./../advanced_config/parameter_reference.html#MAV_PROTO_VER">MAV_PROTO_VER</a> to 2 (see <a href="https://docs.qgroundcontrol.com/master/en/qgc-user-guide/setup_view/parameters.html" target="_blank" rel="noreferrer">QGroundControl Setup &gt; Parameters</a>)</li></ul><h4 id="вдосконалення" tabindex="-1">Вдосконалення <a class="header-anchor" href="#вдосконалення" aria-label="Permalink to &quot;Вдосконалення&quot;">​</a></h4><p>Вам може додатково знадобитися налаштувати деякі параметри, оскільки параметри за замовчуванням налаштовані з припущенням точності GPS в порядку метрів, а не сантиметрів. For example, you can decrease <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_V_NOISE">EKF2_GPS_V_NOISE</a> and <a href="./../advanced_config/parameter_reference.html#EKF2_GPS_P_NOISE">EKF2_GPS_P_NOISE</a> to 0.2.</p><h4 id="подвіині-приимачі" tabindex="-1">Подвійні приймачі <a class="header-anchor" href="#подвіині-приимачі" aria-label="Permalink to &quot;Подвійні приймачі&quot;">​</a></h4><p>Другий приймач GPS може бути використаний як резервний (RTK або не RTK). See the <a href="./../advanced_config/tuning_the_ecl_ekf.html#gps">EKF2 GPS Configuration</a> section.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="./../advanced/rtk_gps.html">RTK-GPS (PX4-Integration)</a>: Developer information about integrating RTK-GPS support into PX4.</li><li><a href="https://en.wikipedia.org/wiki/Real_Time_Kinematic" target="_blank" rel="noreferrer">Real Time Kinematic</a> (Wikipedia)</li></ul>',55)]))}const x=n(d,[["render",c]]);export{_ as __pageData,x as default};
