import{_ as e}from"./chunks/vertiq_esc_lineup.AxwKPEhA.js";import{_ as r,c as o,a8 as a,o as n}from"./chunks/framework.BDnHobkS.js";const f=JSON.parse('{"title":"Модулі Vertiq All-In-One Motor/ESC","description":"","frontmatter":{},"headers":[],"relativePath":"uk/peripherals/vertiq.md","filePath":"uk/peripherals/vertiq.md"}'),i={name:"uk/peripherals/vertiq.md"};function l(h,t,s,d,c,p){return n(),o("div",null,t[0]||(t[0]=[a('<h1 id="модулі-vertiq-all-in-one-motor-esc" tabindex="-1">Модулі Vertiq All-In-One Motor/ESC <a class="header-anchor" href="#модулі-vertiq-all-in-one-motor-esc" aria-label="Permalink to &quot;Модулі Vertiq All-In-One Motor/ESC&quot;">​</a></h1><p>Vertiq виготовляє високоефективні пропульсивні системи для комерційних та оборонних БПЛА. Основний дизайн складається з легкого, тісно інтегрованого двигуна та ESC з вбудованим позиційним датчиком. З контролем швидкості з закритим зворотним зв&#39;язком для найшвидшого часу реакції, провідною ефективністю, відсутністю стартових та зворотних вібрацій, що дозволяє керування низькою швидкістю та плавну зворотність, а також вбудованим контролером &quot;стоу&quot; для плавного розміщення пропелерів у режимі очікування у вибраному напрямку, модулі Vertiq мають значні переваги перед іншими ESC.</p><p><img src="'+e+'" alt="Vertiq Module Lineup"></p><p>All Vertiq modules support traditional <a href="https://iqmotion.readthedocs.io/en/latest/communication_protocols/hobby_protocol.html" target="_blank" rel="noreferrer">PWM input, DShot, OneShot, and Multishot communication protocols</a>. Vertiq&#39;s larger modules also support <a href="https://iqmotion.readthedocs.io/en/latest/communication_protocols/dronecan_protocol.html" target="_blank" rel="noreferrer">DroneCAN control</a>.</p><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>Purchasing information can be found on the <a href="https://www.vertiq.co/" target="_blank" rel="noreferrer">Vertiq website</a>.</p><h2 id="налаштування-програмного-забезпечення" tabindex="-1">Налаштування програмного забезпечення <a class="header-anchor" href="#налаштування-програмного-забезпечення" aria-label="Permalink to &quot;Налаштування програмного забезпечення&quot;">​</a></h2><h3 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h3><p>Підключення вашого модуля Vertiq до виходу ШІМ з вашого керування польотом або шини DroneCAN буде відрізнятися в залежності від вашої моделі. Будь ласка, перегляньте аркуші даних продукту для інформації щодо підключення.</p><p>All Vertiq datasheets can be found at <a href="https://www.vertiq.co/" target="_blank" rel="noreferrer">vertiq.co</a>.</p><h2 id="налаштування-прошивки" tabindex="-1">Налаштування прошивки <a class="header-anchor" href="#налаштування-прошивки" aria-label="Permalink to &quot;Налаштування прошивки&quot;">​</a></h2><p>Найкращий інструмент для налаштування вашого модуля Vertiq - це додаток IQ Control Center від Vertiq. You can find instructions for installation in <a href="https://iqmotion.readthedocs.io/en/latest/control_center_docs/speed_module_getting_started.html" target="_blank" rel="noreferrer">Getting Started with Speed Modules Using IQ Control Center</a>.</p><p>To get started with traditional PWM input or DShot with your flight controller, please see <a href="https://iqmotion.readthedocs.io/en/latest/tutorials/pwm_control_flight_controller.html" target="_blank" rel="noreferrer">PWM and DSHOT Control with a Flight Controller</a>.</p><p>To get started with DroneCAN with your flight controller, please see <a href="https://iqmotion.readthedocs.io/en/latest/tutorials/dronecan_flight_controller.html" target="_blank" rel="noreferrer">DroneCAN Integration with a PX4 Flight Controller</a>.</p><h2 id="налаштування-польотного-контролера" tabindex="-1">Налаштування польотного контролера <a class="header-anchor" href="#налаштування-польотного-контролера" aria-label="Permalink to &quot;Налаштування польотного контролера&quot;">​</a></h2><h3 id="конфігурація-dronecan" tabindex="-1">Конфігурація DroneCAN <a class="header-anchor" href="#конфігурація-dronecan" aria-label="Permalink to &quot;Конфігурація DroneCAN&quot;">​</a></h3><p>Instructions for integrating the motor/ESC using with DroneCAN can be found in <a href="https://iqmotion.readthedocs.io/en/latest/tutorials/dronecan_flight_controller.html#dronecan-integration-with-a-px4-flight-controller" target="_blank" rel="noreferrer">Flight Controller Configuration</a> (in <em>DroneCAN Integration with a PX4 Flight Controller</em>).</p><p>Ці інструкції допоможуть вам налаштувати правильні параметри для активації драйверів DroneCAN контролера польоту, встановити правильні конфігураційні параметри для зв&#39;язку з модулями Vertiq на шині DroneCAN, налаштування ESC та перевірку того, що ваш контролер польоту може належним чином керувати вашими модулями по протоколу DroneCAN.</p><h3 id="конфігурація-dshot-pwm" tabindex="-1">Конфігурація DShot/PWM <a class="header-anchor" href="#конфігурація-dshot-pwm" aria-label="Permalink to &quot;Конфігурація DShot/PWM&quot;">​</a></h3><p>Instructions for integrating the motor/ESC using PWM and DShot can be found in <a href="https://iqmotion.readthedocs.io/en/latest/tutorials/pwm_control_flight_controller.html" target="_blank" rel="noreferrer">PWM and DShot Control with a Flight Controller</a>. DShot рекомендовано.</p><h2 id="подальша-інформація" tabindex="-1">Подальша інформація <a class="header-anchor" href="#подальша-інформація" aria-label="Permalink to &quot;Подальша інформація&quot;">​</a></h2><ul><li><a href="https://www.vertiq.co/" target="_blank" rel="noreferrer">https://www.vertiq.co/</a> — Learn more about Vertiq modules</li><li><a href="https://iqmotion.readthedocs.io/en/latest/index.html" target="_blank" rel="noreferrer">Vertiq Documentation</a> — Additional information about configuring your Vertiq module</li></ul>',22)]))}const q=r(i,[["render",l]]);export{f as __pageData,q as default};
