import{_ as t,c as r,a8 as l,o as a}from"./chunks/framework.BDnHobkS.js";const g=JSON.parse('{"title":"Реліз 1.12","description":"","frontmatter":{},"headers":[],"relativePath":"uk/releases/1.12.md","filePath":"uk/releases/1.12.md"}'),i={name:"uk/releases/1.12.md"};function o(n,e,s,u,p,h){return a(),r("div",null,e[0]||(e[0]=[l(`<h1 id="реліз-1-12" tabindex="-1">Реліз 1.12 <a class="header-anchor" href="#реліз-1-12" aria-label="Permalink to &quot;Реліз 1.12&quot;">​</a></h1><ul><li><a href="#release-1-12">Release 1.12</a><ul><li><a href="#pre-releases">Pre Releases</a></li><li><a href="#changes">Changes</a><ul><li><a href="#common">Common</a></li><li><a href="#sensors">Sensors</a></li><li><a href="#hardware">Hardware</a></li><li><a href="#mavlink">MAVLink</a></li><li><a href="#commander">Commander</a></li><li><a href="#multicopter">Multicopter</a></li><li><a href="#vtol">VTOL</a></li><li><a href="#control">Control</a></li><li><a href="#gps">GPS</a></li><li><a href="#nuttx">NuttX</a></li><li><a href="#uavcan">UAVCAN</a></li></ul></li></ul></li></ul><h2 id="попередніи-реліз" tabindex="-1">Попередній реліз <a class="header-anchor" href="#попередніи-реліз" aria-label="Permalink to &quot;Попередній реліз&quot;">​</a></h2><ul><li><a href="https://github.com/PX4/PX4-Autopilot/releases/tag/v1.12.0-beta4" target="_blank" rel="noreferrer">Beta 4</a></li><li><a href="https://github.com/PX4/PX4-Autopilot/releases/tag/v1.12.0-beta3" target="_blank" rel="noreferrer">Beta 3</a></li><li><a href="https://github.com/PX4/PX4-Autopilot/releases/tag/v1.12.0-beta2" target="_blank" rel="noreferrer">Beta 2</a></li><li><a href="https://github.com/PX4/PX4-Autopilot/releases/tag/v1.12.0-beta1" target="_blank" rel="noreferrer">Beta 1</a></li></ul><h2 id="зміни" tabindex="-1">Зміни <a class="header-anchor" href="#зміни" aria-label="Permalink to &quot;Зміни&quot;">​</a></h2><h3 id="загальні" tabindex="-1">Загальні <a class="header-anchor" href="#загальні" aria-label="Permalink to &quot;Загальні&quot;">​</a></h3><ul><li><strong>RTL Trigger based on remaining flight range (<a href="https://github.com/PX4/PX4-Autopilot/pull/16399" target="_blank" rel="noreferrer">PR#16399</a>)</strong><ul><li>Обчислює час до дому, у режимі RTL, враховуючи швидкість руху автомобіля, швидкість вітру та відстань/напрямок до пункту призначення</li></ul></li><li><strong>Pre-emptive geofence breach (<a href="https://github.com/PX4/PX4-Autopilot/pull/16400" target="_blank" rel="noreferrer">PR#16400</a>)</strong><ul><li>Triggers a breach if the <em>predicted</em> current trajectory will result in a breach, allowing the vehicle to be re-routed to a safe hold position.</li></ul></li><li><strong>Airframe Scripts</strong><ul><li>Синтаксис для встановлення значень за замовчуванням було змінено, і власні сценарії потребують оновлення</li><li>See <a href="https://github.com/PX4/PX4-Autopilot/pull/16796/files#diff-dcf2f5536f47f260e5e0ff3b3fd22eaef6b6c510126463d70affa0eb7bd4d3ddL20" target="_blank" rel="noreferrer">PR#16796</a> for an example.</li></ul></li><li>Безпека (перемикач) за замовчуванням вимкнена (двигуни розброньовані, але сервоприводи / закрилки можуть рухатися)</li><li>Безпековий перемикач є защіпним: коли він вимкнутий, він залишиться вимкнутим</li><li>Засіб виявлення землі: Розширити виявлення землі для використання відстані до ґрунту, якщо датчик відстані присутній</li><li>Додана підтримка для IRC Ghost включаючи телеметрію</li></ul><h3 id="датчики" tabindex="-1">Датчики <a class="header-anchor" href="#датчики" aria-label="Permalink to &quot;Датчики&quot;">​</a></h3><ul><li>Калібрування магнітомера швидше і надійніше <ul><li>нові коефіцієнти калібрування для м&#39;якого заліза</li><li>автоматично визначати обертання зовнішніх датчиків</li></ul></li><li>Оптимізований датчик керування швидкістю потоку даних (мінімальна затримка від кінця до кінця внутрішньої петлі)</li></ul><h3 id="апаратне-забезпечення-hardware" tabindex="-1">Апаратне забезпечення(Hardware) <a class="header-anchor" href="#апаратне-забезпечення-hardware" aria-label="Permalink to &quot;Апаратне забезпечення(Hardware)&quot;">​</a></h3><p>Випуск включає новий апаратний засіб для наступних плат, периферійних пристроїв та аксесуарів:</p><ul><li>Pixhawk FMUv6U (Read more about this spec on the <a href="https://github.com/pixhawk/Pixhawk-Standards" target="_blank" rel="noreferrer">Pixhawk GitHub Repository</a>)</li><li>Pixhawk FMUv6X (Read more about this spec on the <a href="https://github.com/pixhawk/Pixhawk-Standards" target="_blank" rel="noreferrer">Pixhawk GitHub Repository</a>)</li><li>CUAV X7 / X7Pro (<a href="http://www.cuav.net/en/x7en/" target="_blank" rel="noreferrer">Read more about this product on the manufacturers site</a>]</li><li>CUAV Nora (<a href="http://www.cuav.net/en/nora/" target="_blank" rel="noreferrer">Read more about this product on the manufacturers site</a>)</li><li>CUAV CAN GPS (<a href="http://www.cuav.net/en/neo-3-2/" target="_blank" rel="noreferrer">Read more about this product on the manufacturers site</a>)</li><li>SP Racing H7 Extreme (<a href="http://seriouslypro.com/spracingh7extreme" target="_blank" rel="noreferrer">Read more about this product on the manufacturers site</a>)</li><li>Bitcraze Crazyflie v2.1 (<a href="https://www.bitcraze.io/products/crazyflie-2-1/" target="_blank" rel="noreferrer">Read more about this product on the manufacturers site</a>)</li><li>ARK CAN Flow (<a href="https://arkelectron.com/product/ark-flow/" target="_blank" rel="noreferrer">Read more about this product on the manufacturers site</a>)</li><li>mRo Ctrl Zero H7 (Experimental) (<a href="https://store.mrobotics.io/mRo-Control-Zero-F7-p/mro-ctrl-zero-f7.htm" target="_blank" rel="noreferrer">Read more about this product on the manufacturers site</a>)</li></ul><p>Вони видалені:</p><ul><li>Вилучення з припиненням виробництва Intel AeroFC</li></ul><h3 id="mavlink" tabindex="-1">MAVLink <a class="header-anchor" href="#mavlink" aria-label="Permalink to &quot;MAVLink&quot;">​</a></h3><ul><li><strong>MAVLink Ethernet configuration (<a href="https://github.com/PX4/PX4-Autopilot/pull/14460" target="_blank" rel="noreferrer">PR#14460</a>)</strong><ul><li>Налаштування каналу Ethernet MAVLink, такі як UDP-порт, віддалений порт та режим мовлення тепер можуть бути змінені динамічно за допомогою параметрів.</li></ul></li><li><strong>Support for querying <code>COMPONENT_INFORMATION</code> (<a href="https://github.com/PX4/PX4-Autopilot/pull/16039" target="_blank" rel="noreferrer">PR#16039</a>)</strong><ul><li>Метадані параметра тепер автоматично синхронізуються в поєднанні з щоденним QGC.</li><li>Це нове повідомлення дозволяє будь-якій системі MAVLink запитувати різноманітну ієрархічну інформацію від автопілота, тобто розуміти, які команди підтримуються в місіях або отримувати метадані параметрів. This message was introduced primarily to help GCS better understand autopilots (RFC: <a href="https://github.com/mavlink/mavlink/issues/1339" target="_blank" rel="noreferrer">mavlink#1339</a>)</li></ul></li></ul><h3 id="командер" tabindex="-1">Командер <a class="header-anchor" href="#командер" aria-label="Permalink to &quot;Командер&quot;">​</a></h3><ul><li><strong>Commander: use control mode flags and cleanup arm/disarm (<a href="https://github.com/PX4/PX4-Autopilot/pull/16266" target="_blank" rel="noreferrer">PR#16266</a>)</strong><ul><li>Consolidate scattered arming requirements in arm_disarm(), and, keeps the <code>vehicle_control_mode</code> last state in commander</li></ul></li><li><strong>Commander: Separate out manual control setpoint processing (<a href="https://github.com/PX4/PX4-Autopilot/pull/16878" target="_blank" rel="noreferrer">PR#16878</a>)</strong><ul><li>Adds a new class <code>ManualControl</code> for handling <code>manual_control_setpoint</code> and handles RC loss, RC override, and RC arming/disarming</li></ul></li></ul><h3 id="мультикоптер" tabindex="-1">Мультикоптер <a class="header-anchor" href="#мультикоптер" aria-label="Permalink to &quot;Мультикоптер&quot;">​</a></h3><ul><li><p><strong>More intuitive stick feel in Position mode</strong></p><ul><li>Горизонтальний вхід палиці відображений на точках прискорення замість встановлених точок швидкості</li><li>Видаляє неочікувані зміни нахилу при досягненні швидкості руху</li><li>Інтуїтивне переміщення, наприклад, при посадці</li><li>Opt out possible using <a href="./../advanced_config/parameter_reference.html#MPC_POS_MODE">MPC_POS_MODE</a></li><li>Development: <a href="https://github.com/PX4/PX4-Autopilot/pull/12072" target="_blank" rel="noreferrer">First attempt</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/16052" target="_blank" rel="noreferrer">Introduction</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/16320" target="_blank" rel="noreferrer">Improvements</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/16786" target="_blank" rel="noreferrer">Bugfix zero oscillation</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/16791" target="_blank" rel="noreferrer">Bugfix position unlock</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/17078" target="_blank" rel="noreferrer">Bugfix invalid setpoint</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/17437" target="_blank" rel="noreferrer">Bugfix high velocity pre takeoff</a></li></ul></li><li><p><strong>Hover thrust independent velocity control gains</strong></p><ul><li>Parameters <code>MPC_{XY/Z}_VEL_{P/I/D}</code> were replaced with <code>MPC_{XY/Z}_VEL_{P/I/D}_ACC</code>, see: <a href="./../advanced_config/parameter_reference.html#MPC_XY_VEL_P_ACC">MPC_XY_VEL_P_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_XY_VEL_I_ACC">MPC_XY_VEL_I_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_XY_VEL_D_ACC">MPC_XY_VEL_D_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_P_ACC">MPC_Z_VEL_P_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_I_ACC">MPC_Z_VEL_I_ACC</a>, <a href="./../advanced_config/parameter_reference.html#MPC_Z_VEL_D_ACC">MPC_Z_VEL_D_ACC</a></li></ul></li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Прибутки отримали нове значення</p><pre><code>- Шкала відхилення від швидкості у $м/с$ до виходу прискорення у $м/с^2$
- Існуючі виграші потрібно приблизно перескалювати множником: $гравітаційна \\_ стала / тяга \\_ струму$
- Automatic parameter transition assumes 50% hover thrust: \`~10m/s^2 / 50% = 20 m/s^2\`.
  See [question](https://github.com/PX4/PX4-Autopilot/pull/14823#issuecomment-791357646)
</code></pre></div><ul><li><p>Development: <a href="https://github.com/PX4/PX4-Autopilot/pull/14749" target="_blank" rel="noreferrer">Logic introduction</a>, <a href="https://github.com/PX4/PX4-Autopilot/pull/14823" target="_blank" rel="noreferrer">Parameter replacement</a></p></li><li><p><strong>Improve Rounded Turns (<a href="https://github.com/PX4/PX4-Autopilot/pull/16376" target="_blank" rel="noreferrer">PR#16376</a>)</strong></p><ul><li>Створює більш округлений поворот при точках маршруту в місіях мультиротора (використовуючи логіку навігації у стилі L1 на кутах)</li><li>See <a href="./../flight_modes_fw/mission.html#rounded-turns-inter-waypoint-trajectory">Mission Mode &gt; Inter-waypoint Trajectory</a> and <a href="./../flying/missions.html#setting-acceptance-turning-radius">Mission &gt; Setting Acceptance/Turning Radius</a></li></ul></li><li><p><strong>Removal of Rattitude flight mode (<a href="https://github.com/PX4/PX4-Autopilot/pull/17019" target="_blank" rel="noreferrer">PR#17019</a>)</strong></p><ul><li>Сповістіть нас, якщо ви хочете повернути його.</li></ul></li></ul><h3 id="vtol" tabindex="-1">VTOL <a class="header-anchor" href="#vtol" aria-label="Permalink to &quot;VTOL&quot;">​</a></h3><ul><li><strong>RTL improvements (<a href="https://github.com/PX4/PX4-Autopilot/pull/16377" target="_blank" rel="noreferrer">PR#16377</a>)</strong><ul><li>Підвищує надійність RTL безпекових заходів, враховуючи багато крайніх випадків при спробі посадки, залежно від поточного режиму транспортного засобу (Багатокоптерний проти Фіксований крило)</li></ul></li><li>Значні вдосконалення ТЕКС фіксованого крила / VTOL</li></ul><h3 id="управління" tabindex="-1">Управління <a class="header-anchor" href="#управління" aria-label="Permalink to &quot;Управління&quot;">​</a></h3><ul><li><strong>Dynamic Notch Filter updated with Gyro FFT (<a href="https://github.com/PX4/PX4-Autopilot/pull/16385" target="_blank" rel="noreferrer">PR#16385</a>)</strong><ul><li>Додає динамічне фільтрування виѐмок до даних керування гіроскопом, що призводить до набагато плавнішого керування</li></ul></li><li><strong>Multi-EKF enabled by default</strong> on stm32f7 and stm32h7 boards</li></ul><h3 id="gps" tabindex="-1">GPS <a class="header-anchor" href="#gps" aria-label="Permalink to &quot;GPS&quot;">​</a></h3><ul><li>The GPS protocol now defaults to u-blox for faster startup, and <a href="./../advanced_config/parameter_reference.html#GPS_1_PROTOCOL">GPS_x_PROTOCOL</a> needs to be changed if another GPS is used.</li></ul><h3 id="nuttx" tabindex="-1">NuttX <a class="header-anchor" href="#nuttx" aria-label="Permalink to &quot;NuttX&quot;">​</a></h3><p>Nuttx was upgraded from <a href="https://github.com/apache/incubator-nuttx/compare/nuttx-8.2..nuttx-10.0.1" target="_blank" rel="noreferrer">8.2+ to NuttX 10.10.0+</a> (@ <a href="https://github.com/apache/incubator-nuttx/commit/904a602c74dc08a100b5c2bd490807de19e73e10" target="_blank" rel="noreferrer">904a602c74dc08a100b5c2bd490807de19e73e10</a>)</p><ul><li><p><strong>SDCARD performance:</strong> Results in better performance on H7 Targets</p><ul><li>[<strong>BACKPORT</strong>] stm32:SDIO:Use 250 Ms Data path timeout, regardless of Card Clock frequency</li><li>[<strong>BACKPORT</strong>] stm32h7:SDMMC:Use 250 Ms Data path timeout, regardless of Card Clock frequency</li><li>[<strong>BACKPORT</strong>] stm32f7:SDMMC:Use 250 Ms Data path timeout, regardless of Card Clock frequency</li><li>[<strong>BACKPORT</strong>] Fixes race condition in event wait logic of SDMMC driver.</li><li>[<strong>BACKPORT</strong>] mmcsd:Stuck in 1-bit mode, Removed CONFIG_ARCH_HAVE_SDIO_DELAYED_INVLDT</li></ul></li><li><p><strong>Ethernet stability:</strong></p><ul><li>[<strong>BACKPORT</strong>] stm32x7:Ethernet Fixed hardfaults, from too big frames</li><li>[<strong>BACKPORT</strong>] stm32:Ethernet Fix too big frames</li></ul></li><li><p><strong>Boot up stability</strong> V5-V6X ensuring the LSE (RTC) oscillator is started</p><ul><li><p>[<strong>BACKPORT</strong>] stm32h7:lse fix Kconfig help text</p></li><li><p>[<strong>BACKPORT</strong>] stm32f7:lse Use Kconfig values directly</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:Add DBGMCU</p></li><li><p>[<strong>BACKPORT</strong>] stm32f7:Add option to auto select LSE CAPABILITY</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:Add option to auto select LSE CAPABILITY</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This Knob will cycle through the correct* values from low to high. To avoid damaging the crystal. We want to use the lowest setting that gets the OSC running. See app note AN2867 *It will take into account the rev of the silicon and use the correct code points to achieve the drive strength. See Eratta ES0392 Rev 7 2.2.14 LSE oscillator driving capability selection bits are swapped.</p></div></li></ul></li></ul><p>:::</p><ul><li><p><strong>Driver changes</strong></p><ul><li><p>[<strong>BACKPORT</strong>] drivers/serial: fix Rx interrupt enable for cdcacm</p></li><li><p>[<strong>BACKPORT</strong>] binnfmt:Fix return before close ELF fd</p></li><li><p>[<strong>BACKPORT</strong>] stm32f7:Allow for reuse of the OTG_ID GPIO</p></li><li><p>[<strong>BACKPORT</strong>] stm32f7:SDMMC fix reset of do_gpio</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7: serial: use dma tx semaphore as resource holder</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:SDMMC fix reset of do_gpio</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:Serial Add RX and TX DMA</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:Allow for reuse of the OTG_ID GPIO</p></li><li><p>[<strong>BACKPORT</strong>] Kinetis:kinetis:Replace DMA</p></li><li><p>[<strong>BACKPORT</strong>] kinetis:Serial use eDMA</p></li><li><p>[<strong>BACKPORT</strong>] kinetis:SPI use eDMA</p></li><li><p>[<strong>BACKPORT</strong>] Kinetis:Serail No DMA Poll needed</p></li><li><p>[<strong>BACKPORT</strong>] libc/stdio: Preallocate the stdin, stdout and stderr For targets without consoles.</p></li></ul></li><li><p><strong>FlexCan fixes</strong></p><ul><li>[<strong>BACKPORT</strong>][flexcan] Correct reset state for CTRL1 register</li><li>[<strong>BACKPORT</strong>][flexcan] Fix TX drop #2792 and correctly set CAN timings to non-zeroed registers</li><li>[<strong>BACKPORT</strong>] FlexCAN Fix TX abort process</li></ul></li><li><p><strong>Support for CAN bootloader</strong></p><ul><li>[<strong>BACKPORT</strong>] s32k1xx:Support ramfunc</li></ul></li><li><p><strong>STM32F412 cleanup</strong></p></li><li><p>[<strong>BACKPORT</strong>] stm32f412:Corrected Pin count</p></li><li><p>[<strong>BACKPORT</strong>] stm32f412:Replaced Kludged pinmap with one for SoC</p></li><li><p>[<strong>BACKPORT</strong>] stm32412: Fixes pinmap CAN1</p></li><li><p><strong>Security patches</strong></p></li><li><p>[<strong>BACKPORT</strong>] tcp: Remove incomplete support for TCP reassembly</p></li><li><p>[<strong>BACKPORT</strong>] net/tcp/tcp_input.c: Correct bad check of urgent data length</p></li><li><p>[<strong>BACKPORT</strong>] libc: Add additional checks to malloc realloc and memalign</p></li><li><p><strong>IMXRT fixes</strong></p></li><li><p>Додавання Single wire та правильних налаштувань парності до IMXRT для підтримки sbus та іншого.</p></li><li><p>[<strong>BACKPORT</strong>] imxrt:serial support single-wire mode</p></li><li><p>[<strong>BACKPORT</strong>] imxrt:imxrt_lowputc Fixed parity settings.</p></li><li><p><strong>STM32H7 improvements</strong></p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:SPI Fix 16 bit SPI mode</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:DMA BDMA does not auto disabled on completion</p></li><li><p>[<strong>BACKPORT</strong>] Fix HEAP clobbering static data in SRAM4</p></li><li><p>[<strong>BACKPORT</strong>] stm32h7:SDMMC fix reset of do_gpio</p></li></ul><h3 id="uavcan" tabindex="-1">UAVCAN <a class="header-anchor" href="#uavcan" aria-label="Permalink to &quot;UAVCAN&quot;">​</a></h3><ul><li>UAVCANv0: Хоча базові функції, такі як оновлення прошивки та синхронізація параметрів вузлів CAN, були впроваджені більше 5 років тому, ми оновили підтримку, тепер, нарешті, пристрої є на ринку. Типові модулі CAN GPS, швидкості повітря та потужності підтримуються</li><li>UAVCANv0 Вузол: PX4 підтримував будівництво вузлів протягом багатьох років - тепер ми підтримуємо будівництво конкретних цілей, таких як одиниці GPS CUAV</li><li>UAVCANv1: Початковий альфа-варіант повної реалізації від кінця до кінця</li></ul>`,35)]))}const d=t(i,[["render",o]]);export{g as __pageData,d as default};
