import{_ as a,a as t}from"./chunks/tfslot_integration.CFeV_up5.js";import{_ as r,c as s,a8 as i,o as n}from"./chunks/framework.BDnHobkS.js";const g=JSON.parse('{"title":"TFSLOT датчик швидкості польоту ефекту Вентурі","description":"","frontmatter":{},"headers":[],"relativePath":"uk/sensor/airspeed_tfslot.md","filePath":"uk/sensor/airspeed_tfslot.md"}'),o={name:"uk/sensor/airspeed_tfslot.md"};function l(h,e,p,d,c,f){return n(),s("div",null,e[0]||(e[0]=[i('<h1 id="tfslot-датчик-швидкості-польоту-ефекту-вентурі" tabindex="-1">TFSLOT датчик швидкості польоту ефекту Вентурі <a class="header-anchor" href="#tfslot-датчик-швидкості-польоту-ефекту-вентурі" aria-label="Permalink to &quot;TFSLOT датчик швидкості польоту ефекту Вентурі&quot;">​</a></h1><p><a href="https://github.com/ThunderFly-aerospace/TFSLOT01" target="_blank" rel="noreferrer">TFSLOT</a> is an open-source airspeed sensor based on the <a href="https://en.wikipedia.org/wiki/Venturi_effect" target="_blank" rel="noreferrer">Venturi effect</a> that also has an IMU.</p><p><img src="'+a+'" alt="TFSLOT and TFSLOT WITH TFASPDIMU02 board"></p><p><a href="https://github.com/ThunderFly-aerospace/TFSLOT01" target="_blank" rel="noreferrer">TFSLOT</a> is an airspeed sensor based on venturi effects. In the basic configuration, the TFSLOT is equipped with the <a href="https://github.com/ThunderFly-aerospace/TFASPDIMU02" target="_blank" rel="noreferrer">TFASPDIMU02</a> sensor board, which contains a differential pressure sensor (<a href="https://sensirion.com/products/catalog/?filter_series=d1816d53-f5c8-47e3-ab47-818c3fd54259" target="_blank" rel="noreferrer">Sensirion SDP3x series</a>) and a 9-axis motion tracking sensor (<a href="https://invensense.tdk.com/products/motion-tracking/9-axis/icm-20948/" target="_blank" rel="noreferrer">ICM-20948</a>). Одиницю IMU можна використовувати як зовнішній компас.</p><ul><li>Цей дизайн має кілька переваг при використанні на невеликих і повільних безпілотних літальних апаратах.</li><li>Краща роздільна здатність при низьких швидкостях повітря (нижче 10 м/с).</li><li>Налаштована чутливість, яка надається зміною профілю.</li><li>Менша схильність до засмічення (наприклад, глиною після посадки)</li><li>Стійкий до погодних умов (дощ, сніг тощо)</li><li>Пряме інтегрування датчика різниці тиску без будь-яких додаткових трубок. Менше шансів на відмову датчика.</li><li>Можливість прямої інтеграції в конструкцію безпілотника. Дизайн є повністю відкритим джерелом.</li><li>Інтегрований зовнішній блок ІНС.</li></ul><p>Завдяки надрукованому трубці дуже легко змінити профіль вимірювання і таким чином змінити чутливість в певних діапазонах швидкості. У базовій формі воно оптимізовано так, що виміряний диференційний тиск відповідає тиску від труби Піто.</p><p><img src="'+t+`" alt="TFSLOT integrated in TF-G2"><em>First integration of TFSLOT into <a href="https://github.com/ThunderFly-aerospace/TF-G2/" target="_blank" rel="noreferrer">TF-G2</a> autogyro</em></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Full documentation and source files can be found on <a href="https://github.com/ThunderFly-aerospace/TFSLOT01" target="_blank" rel="noreferrer">GitHub</a>.</p></div><h2 id="де-купити" tabindex="-1">Де купити <a class="header-anchor" href="#де-купити" aria-label="Permalink to &quot;Де купити&quot;">​</a></h2><p>TFSLOT is possible to buy at <a href="https://www.tindie.com/products/thunderfly/tfslot01a-inovative-drone-airspeed-sensor/" target="_blank" rel="noreferrer">Tindie store</a> or by sending us an inquiry via email at <a href="mailto:info@thunderfly.cz" target="_blank" rel="noreferrer">info@thunderfly.cz</a></p><h2 id="підключення" tabindex="-1">Підключення <a class="header-anchor" href="#підключення" aria-label="Permalink to &quot;Підключення&quot;">​</a></h2><p><a href="https://github.com/ThunderFly-aerospace/TFASPDIMU02" target="_blank" rel="noreferrer">TFASPDIMU02</a> is equipped with I2C JST-GH connector, which conforms to the pinout of the <a href="https://github.com/pixhawk/Pixhawk-Standards/blob/master/DS-009%20Pixhawk%20Connector%20Standard.pdf" target="_blank" rel="noreferrer">dronecode standard</a>. Отже, датчик може бути підключений безпосередньо до порту I2C автопілота за допомогою кабелю I2C 4pin JST-GH.</p><h2 id="налаштування" tabindex="-1">Налаштування <a class="header-anchor" href="#налаштування" aria-label="Permalink to &quot;Налаштування&quot;">​</a></h2><p>Because there is an <a href="https://invensense.tdk.com/products/motion-tracking/9-axis/icm-20948/" target="_blank" rel="noreferrer">IMU IC</a> connected in front of the sensor, the IMU IC needs to be set into a bridge mode. Після цього можна запустити драйвер датчика швидкості повітря. Це може бути зроблено за допомогою наступних послідовностей команд. Команди передбачають підключення до порту I2C2.</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">icm20948_i2c_passthrough</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> start</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -X</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -b</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 2</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -a</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 0x68</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sdp3x_airspeed</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> start</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -X</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -b</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 2</span></span></code></pre></div><p>This sequence can be stored on the SD card in the <code>/etc/config.txt file</code>. You can read more about the configuration on the SD card on a <a href="./../concept/system_startup.html#replacing-the-system-startup">separate page</a>.</p><p>Оскільки перетворення різниці тиску на швидкість повітря відрізняється від трубки Піто, цей профіль потребує змін. This is done by setting parameter <a href="./../advanced_config/parameter_reference.html#CAL_AIR_CMODEL"><code>CAL_AIR_CMODEL</code></a> to 3 (Venturi effect based airspeed sensor)</p><h2 id="калібрування" tabindex="-1">Калібрування <a class="header-anchor" href="#калібрування" aria-label="Permalink to &quot;Калібрування&quot;">​</a></h2><p>Калібрування дещо складне через те, що поточна версія прошивки не підтримує калібрування з від&#39;ємними значеннями. Оскільки використовуваний датчик вимірює обидва напрямки повітряного потоку симетрично і має нульове зміщення, немає потреби калібрувати його знову перед кожним зльотом. Але вам потрібно забезпечити, щоб під час калібрування не було потоку повітря.</p><p>Найлегший спосіб калібрування - взяти клейку стрічку і приклеїти її між тиском на входах до датчика. Потім розпочніть процес калібрування і дмухайте ззаду, коли буде вимога. Якщо буде створено тиск принаймні 50 Па, калібрування буде успішним.</p>`,20)]))}const b=r(o,[["render",l]]);export{g as __pageData,b as default};
